        (oldAllowance) = StakeModuleLib._approveOperator($v_DataStoreModuleLib_IsolatedStorage[DATASTORE],poolId,operatorId,allowance);
        emit return$_approveOperator(oldAllowance);
    }

    function $delegate(uint256 DATASTORE,uint256 poolId,uint256[] calldata operatorIds,uint256[] calldata allowances,uint256 fallbackOperator) external payable {
        StakeModuleLib.delegate($v_DataStoreModuleLib_IsolatedStorage[DATASTORE],poolId,operatorIds,allowances,fallbackOperator);
