        (ret0) = StakeModuleLib._isGeodePackageIsolated(_packageAddress);
    }

    function $_getLiquidityPool(uint256 DATASTORE,uint256 _poolId) external view returns (ILiquidityPool ret0) {
        (ret0) = StakeModuleLib._getLiquidityPool($v_DataStoreModuleLib_IsolatedStorage[DATASTORE],_poolId);
    }

    function $isPriceValid(uint256 self,uint256 poolId) external view returns (bool isValid) {
        (isValid) = StakeModuleLib.isPriceValid($v_StakeModuleLib_PooledStaking[self],poolId);
    }

    function $isMintingAllowed(uint256 self,uint256 DATASTORE,uint256 poolId) external view returns (bool ret0) {
        (ret0) = StakeModuleLib.isMintingAllowed($v_StakeModuleLib_PooledStaking[self],$v_DataStoreModuleLib_IsolatedStorage[DATASTORE],poolId);
    }

    function $_mintgETH(uint256 self,uint256 DATASTORE,uint256 _poolId,uint256 _ethAmount) external payable returns (uint256 mintedgETH) {
        (mintedgETH) = StakeModuleLib._mintgETH($v_StakeModuleLib_PooledStaking[self],$v_DataStoreModuleLib_IsolatedStorage[DATASTORE],_poolId,_ethAmount);
        emit return$_mintgETH(mintedgETH);
    }

    function $_buyback(uint256 DATASTORE,uint256 _poolId,uint256 _maxEthToSell,uint256 _deadline) external payable returns (uint256 remETH, uint256 boughtgETH) {
        (remETH, boughtgETH) = StakeModuleLib._buyback($v_DataStoreModuleLib_IsolatedStorage[DATASTORE],_poolId,_maxEthToSell,_deadline);
        emit return$_buyback(remETH, boughtgETH);
    }

    function $deposit(uint256 self,uint256 DATASTORE,uint256 poolId,uint256 mingETH,uint256 deadline,address receiver) external payable returns (uint256 boughtgETH, uint256 mintedgETH) {
        (boughtgETH, mintedgETH) = StakeModuleLib.deposit($v_StakeModuleLib_PooledStaking[self],$v_DataStoreModuleLib_IsolatedStorage[DATASTORE],poolId,mingETH,deadline,receiver);
        emit return$deposit(boughtgETH, mintedgETH);
    }

    function $_canStake(uint256 self,bytes calldata _pubkey,uint256 _verificationIndex) external view returns (bool ret0) {
        (ret0) = StakeModuleLib._canStake($v_StakeModuleLib_PooledStaking[self],_pubkey,_verificationIndex);
    }

    function $canStake(uint256 self,bytes calldata pubkey) external view returns (bool ret0) {
        (ret0) = StakeModuleLib.canStake($v_StakeModuleLib_PooledStaking[self],pubkey);
    }

    function $proposeStake(uint256 self,uint256 DATASTORE,uint256 poolId,uint256 operatorId,bytes[] calldata pubkeys,bytes[] calldata signatures1,bytes[] calldata signatures31) external payable {
        StakeModuleLib.proposeStake($v_StakeModuleLib_PooledStaking[self],$v_DataStoreModuleLib_IsolatedStorage[DATASTORE],poolId,operatorId,pubkeys,signatures1,signatures31);
    }

    function $stake(uint256 self,uint256 DATASTORE,uint256 operatorId,bytes[] calldata pubkeys) external payable {
        StakeModuleLib.stake($v_StakeModuleLib_PooledStaking[self],$v_DataStoreModuleLib_IsolatedStorage[DATASTORE],operatorId,pubkeys);
    }

    receive() external payable {}
}

