contract ERC1155PausableBurnableSupply is
  IERC1155PausableBurnableSupply,
  ERC1155,
  AccessControl,
  Pausable,
  ERC1155Burnable,
  ERC1155Supply
{
  bytes32 public constant URI_SETTER_ROLE = keccak256("URI_SETTER_ROLE");
  bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

  /**
   * @dev DEFAULT_ADMIN_ROLE is not set, no more role management here.
   */
  constructor(string memory uri_) ERC1155(uri_) {
    _grantRole(URI_SETTER_ROLE, msg.sender);
    _grantRole(PAUSER_ROLE, msg.sender);
    _grantRole(MINTER_ROLE, msg.sender);
  }

  function setURI(string memory newuri) public override onlyRole(URI_SETTER_ROLE) {
    _setURI(newuri);
  }

  function pause() public override onlyRole(PAUSER_ROLE) {
    _pause();
  }

  function unpause() public override onlyRole(PAUSER_ROLE) {
    _unpause();
  }

  function mint(
    address account,
    uint256 id,
    uint256 amount,
    bytes memory data
  ) public override onlyRole(MINTER_ROLE) {
    _mint(account, id, amount, data);
  }

  function mintBatch(
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory data
  ) public override onlyRole(MINTER_ROLE) {
    _mintBatch(to, ids, amounts, data);
  }

  function _beforeTokenTransfer(
    address operator,
    address from,
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory data
