// SPDX-License-Identifier: MIT
pragma solidity =0.8.7;

// interfaces
import {IDataStoreModule} from "../../interfaces/modules/IDataStoreModule.sol";
// libraries
import {DataStoreModuleLib as DSML} from "./libs/DataStoreModuleLib.sol";
// external
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

/**
 * @title DSM: DataStore Module
 *
 * @notice A storage management tool designed to create a safe and scalable storage layout
 * for upgradable contracts with various types of data classes (users,packages,definitions).
 *
 * @dev review: this module delegates its functionality to DSML (DataStoreModuleLib).
 * DSM or DSML has NO access control.
 *
 * @dev There are no additional functionalities implemented seperately from the library.
 *
 * @dev NO function needs to be overriden when inherited.
 *
 * @dev __DataStoreModule_init (or _unchained) call is NOT NECESSARY when inherited.
 *
 * @dev No storage-altering external/public functions are exposed here, only view/pure external functions.
 *
 * @author Ice Bear & Crash Bandicoot
 */
abstract contract DataStoreModule is IDataStoreModule, Initializable {
  using DSML for DSML.IsolatedStorage;

  /**
   * @custom:section                           ** VARIABLES **
   *
   * @dev Do not add any other variables here. Modules do NOT have a gap.
   * Library's main struct has a gap, providing up to 16 storage slots for this module.
   */
  DSML.IsolatedStorage internal DATASTORE;

  /**
   * @custom:section                           ** INITIALIZING **
   */

  function __DataStoreModule_init() internal onlyInitializing {}

  function __DataStoreModule_init_unchained() internal onlyInitializing {}

  /**
   * @custom:section                           ** HELPER FUNCTIONS **
   *
   * @custom:visibility -> pure-external
   */

  /**
   * @notice useful function for string inputs - returns same with the DSML.generateId
   * @dev id is generated by keccak(name, type)
   */
  function generateId(
    string calldata _name,
    uint256 _type
  ) external pure virtual override returns (uint256 id) {
    id = uint256(keccak256(abi.encodePacked(_name, _type)));
  }

  /**
   * @notice useful view function for string inputs - returns same with the DSML.generateId
   */
  function getKey(
    uint256 _id,
    bytes32 _param
  ) external pure virtual override returns (bytes32 key) {
    return DSML.getKey(_id, _param);
  }

  /**
   * @custom:section                           ** DATA GETTER FUNCTIONS **
   *
   * @custom:visibility -> view-external
   */

  /**
   * @dev useful for outside reach, shouldn't be used within contracts as a referance
   * @return allIdsByType is an array of IDs of the given TYPE from Datastore,
   * returns a specific index
   */
  function allIdsByType(
    uint256 _type,
    uint256 _index
  ) external view virtual override returns (uint256) {
    return DATASTORE.allIdsByType[_type][_index];
  }

  function allIdsByTypeLength(uint256 _type) external view virtual override returns (uint256) {
    return DATASTORE.allIdsByType[_type].length;
  }

  function readUint(uint256 id, bytes32 key) external view virtual override returns (uint256 data) {
    data = DATASTORE.readUint(id, key);
  }

  function readAddress(
    uint256 id,
    bytes32 key
  ) external view virtual override returns (address data) {
    data = DATASTORE.readAddress(id, key);
  }

  function readBytes(
    uint256 id,
    bytes32 key
  ) external view virtual override returns (bytes memory data) {
    data = DATASTORE.readBytes(id, key);
  }

  /**
   * @custom:section                           ** ARRAY GETTER FUNCTIONS **
   *
   * @custom:visibility -> view-external
   */

  function readUintArray(
    uint256 id,
    bytes32 key,
    uint256 index
  ) external view virtual override returns (uint256 data) {
    data = DATASTORE.readUintArray(id, key, index);
  }

  function readBytesArray(
    uint256 id,
    bytes32 key,
    uint256 index
  ) external view virtual override returns (bytes memory data) {
    data = DATASTORE.readBytesArray(id, key, index);
  }

  function readAddressArray(
    uint256 id,
    bytes32 key,
    uint256 index
  ) external view virtual override returns (address data) {
    data = DATASTORE.readAddressArray(id, key, index);
  }
}

// SPDX-License-Identifier: MIT

pragma solidity =0.8.7;

/**
 * @title DSML: DataStore Module Library
 *
 * @notice A Storage Management Library created for the contracts and modules that inherits DataStoreModule (DSM).
 * Enables Dynamic Structs with unlimited key space.
 * Provides an Isolated Storage Layout with IDs and KEYs.
 * Focusing on upgradable contracts with various data types to create a
 * * sustainable development environment.
 * In summary, extra gas cost that would be saved with Storage packing are
 * * ignored to create dynamic structs.
 *
 * @dev Distinct id and key pairs SHOULD return different storage slots. No collisions!
 * @dev IDs are the representation of an entity with any given key as properties.
 * @dev review: Reserved TYPEs are defined within globals/id_type.sol
 * @dev review: For a safer development process, NEVER use the IsolatedStorage with strings. Refer to globals/reserved_key_space.sol
 *
 * @dev While it is a good practice for keeping a record;
 * * TYPE for ID is NOT mandatory, an ID might not have an explicit type.
 * * e.g., When a relational data is added with getKey, like allowance, it has a unique ID but no TYPE.
 * * Thus there are no checks for types or keys.
 *
 * @dev readUint(id, arrayName) returns the lenght of array.
 *
 * @dev Contracts relying on this library must use DataStoreModuleLib.IsolatedStorage
 * @dev This is an internal library, requires NO deployment.
 *
 * @author Ice Bear & Crash Bandicoot
 */

library DataStoreModuleLib {
  /**
   * @notice Main Struct for reading/writing operations for given (id, key) pairs.
   *
   * @param allIdsByType type => id[], optional categorization for IDs, can be directly accessed.
   * @param uintData keccak(id, key) =>  returns uint256
   * @param bytesData keccak(id, key) => returns bytes
   * @param addressData keccak(id, key) =>  returns address
   * @param __gap keep the struct size at 16
   *
   * @dev any other storage type can be expressed as uint or bytes. E.g., bools are 0/1 as uints.
   */
  struct IsolatedStorage {
    mapping(uint256 => uint256[]) allIdsByType;
    mapping(bytes32 => uint256) uintData;
    mapping(bytes32 => bytes) bytesData;
    mapping(bytes32 => address) addressData;
    uint256[12] __gap;
  }

  /**
   * @custom:section                           ** HELPERS **
   *
   * @custom:visibility -> pure-internal
   */

  /**
   * @notice generalized method of generating an ID
   *
   * @dev Some TYPEs may require permissionless creation, allowing anyone to claim any ID;
   * meaning malicious actors can claim names to mislead people. To prevent this
   * TYPEs will be considered during ID generation.
   */
  function generateId(bytes memory _name, uint256 _type) internal pure returns (uint256 id) {
    id = uint256(keccak256(abi.encodePacked(_name, _type)));
  }

  /**
   * @notice hash of given ID and a KEY defines the key for the IsolatedStorage
   * @return key bytes32, hash.
   **/
  function getKey(uint256 id, bytes32 param) internal pure returns (bytes32 key) {
    key = keccak256(abi.encodePacked(id, param));
  }

  /**
   * @custom:section                           ** DATA GETTERS **
   *
   * @custom:visibility -> view-internal
   */

  function readUint(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key
  ) internal view returns (uint256 data) {
    data = self.uintData[getKey(_id, _key)];
  }

  function readBytes(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key
  ) internal view returns (bytes memory data) {
    data = self.bytesData[getKey(_id, _key)];
  }

  function readAddress(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key
  ) internal view returns (address data) {
    data = self.addressData[getKey(_id, _key)];
  }

  /**
   * @custom:section                           ** ARRAY GETTERS **
   *
   * @custom:visibility -> view-internal
   */

  function readUintArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _index
  ) internal view returns (uint256 data) {
    data = self.uintData[getKey(_index, getKey(_id, _key))];
  }

  function readBytesArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _index
  ) internal view returns (bytes memory data) {
    data = self.bytesData[getKey(_index, getKey(_id, _key))];
  }

  function readAddressArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _index
  ) internal view returns (address data) {
    data = self.addressData[getKey(_index, getKey(_id, _key))];
  }

  /**
   * @custom:section                           ** STATE MODIFYING FUNCTIONS **
   *
   * @custom:visibility -> internal
   */

  /**
   * @custom:subsection                        ** DATA SETTERS **
   */

  function writeUint(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _data
  ) internal {
    self.uintData[getKey(_id, _key)] = _data;
  }

  function addUint(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _addend
  ) internal {
    self.uintData[getKey(_id, _key)] += _addend;
  }

  function subUint(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _minuend
  ) internal {
    self.uintData[getKey(_id, _key)] -= _minuend;
  }

  function writeBytes(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    bytes memory _data
  ) internal {
    self.bytesData[getKey(_id, _key)] = _data;
  }

  function writeAddress(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    address _data
  ) internal {
    self.addressData[getKey(_id, _key)] = _data;
  }

  /**
   * @custom:subsection                        ** ARRAY SETTERS **
   */

  function appendUintArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    self.uintData[getKey(self.uintData[arrayKey]++, arrayKey)] = _data;
  }

  function appendBytesArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    bytes memory _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    self.bytesData[getKey(self.uintData[arrayKey]++, arrayKey)] = _data;
  }

  function appendAddressArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    address _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    self.addressData[getKey(self.uintData[arrayKey]++, arrayKey)] = _data;
  }

  /**
   * @custom:subsection                        ** BATCH ARRAY SETTERS **
   */

  function appendUintArrayBatch(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256[] memory _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    uint256 arrayLen = self.uintData[arrayKey];

    for (uint256 i = 0; i < _data.length; ) {
      self.uintData[getKey(arrayLen++, arrayKey)] = _data[i];
      unchecked {
        i += 1;
      }
    }

    self.uintData[arrayKey] = arrayLen;
  }

  function appendBytesArrayBatch(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    bytes[] memory _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    uint256 arrayLen = self.uintData[arrayKey];

    for (uint256 i = 0; i < _data.length; ) {
      self.bytesData[getKey(arrayLen++, arrayKey)] = _data[i];
      unchecked {
        i += 1;
      }
    }

    self.uintData[arrayKey] = arrayLen;
  }

  function appendAddressArrayBatch(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    address[] memory _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    uint256 arrayLen = self.uintData[arrayKey];

    for (uint256 i = 0; i < _data.length; ) {
      self.addressData[getKey(arrayLen++, arrayKey)] = _data[i];
      unchecked {
        i += 1;
      }
    }

    self.uintData[arrayKey] = arrayLen;
  }
}

contracts/Portal/modules/GeodeModule/Geo.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.7;

// globals
import {ID_TYPE} from "../../globals/id_type.sol";
// interfaces
import {IGeodeModule} from "../../interfaces/modules/IGeodeModule.sol";
// libraries
import {GeodeModuleLib as GML} from "./libs/GeodeModuleLib.sol";
import {DataStoreModuleLib as DSML} from "../DataStoreModule/libs/DataStoreModuleLib.sol";
// contracts
import {DataStoreModule} from "../DataStoreModule/DataStoreModule.sol";
// external
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

/**
 * @title GM: Geode Module
 *
 * @notice Base logic for Upgradable Packages:
 * * Dual Governance with Senate+Governance: Governance proposes, Senate approves.
 * * Limited Upgradability built on top of UUPS via Dual Governance.
 *
 * @dev review: this module delegates its functionality to GML (GeodeModuleLib):
 * GML has onlyGovernance, onlySenate, onlyController modifiers for access control.
 *
 * @dev There are 1 additional functionalities implemented seperately from the library:
 * Mutating UUPS pattern to fit Limited Upgradability:
 * 1. New implementation contract is proposed with CONTRACT_UPGRADE (TYPE 2), refer to globals/id_type.sol.
 * 2. Proposal is approved by the contract owner, Senate.
 * 3. approveProposal calls _handleUpgrade which mimics UUPS.upgradeTo:
 * 3.1. Checks the implementation address with _authorizeUpgrade, also preventing any UUPS upgrades.
 * 3.2. Upgrades the contract with no function to call afterwards.
 * 3.3. Sets contract version. Note that, mentioned version is different from version defined within UUPS
 * * & does not increase linearly like one might expect.
 *
 * @dev 1 function needs to be overriden when inherited: isolationMode. (also refer to approveProposal)
 *
 * @dev __GeodeModule_init (or _unchained) call is NECESSARY when inherited.
 * However, deployer MUST call initializer after upgradeTo call,
 * SHOULD NOT call initializer on upgradeToAndCall or new ERC1967Proxy calls.
 *
 * @dev This module inherits DataStoreModule.
 *
 * @author Ice Bear & Crash Bandicoot
 */
abstract contract GeodeModule is IGeodeModule, DataStoreModule, UUPSUpgradeable {
  using GML for GML.DualGovernance;

  /**
   * @custom:section                           ** VARIABLES **
   *
   * @dev Do not add any other variables here. Modules do NOT have a gap.
   * Library's main struct has a gap, providing up to 16 storage slots for this module.
   */
  GML.DualGovernance internal GEODE;

  /**
   * @custom:section                           ** EVENTS **
   */
  event ContractVersionSet(uint256 version);

  event ControllerChanged(uint256 indexed ID, address CONTROLLER);
  event Proposed(uint256 indexed TYPE, uint256 ID, address CONTROLLER, uint256 deadline);
  event Approved(uint256 ID);
  event NewSenate(address senate, uint256 expiry);

  /**
   * @custom:section                           ** ABSTRACT FUNCTIONS **
   */
  function isolationMode() external view virtual override returns (bool);

  /**
   * @custom:section                           ** INITIALIZING **
   */

  function __GeodeModule_init(
    address governance,
    address senate,
    uint256 senateExpiry,
    uint256 packageType,
    bytes calldata initVersionName
  ) internal onlyInitializing {
    __UUPSUpgradeable_init();
    __DataStoreModule_init();
    __GeodeModule_init_unchained(governance, senate, senateExpiry, packageType, initVersionName);
  }

  /**
   * @dev This function uses _getImplementation(), clearly deployer SHOULD NOT call initializer on
   * upgradeToAndCall or new ERC1967Proxy calls. _getImplementation() returns 0 then.
   * @dev GOVERNANCE and SENATE set to msg.sender at beginning, can not propose+approve otherwise.
   * @dev native approveProposal(public) is not used here. Because it has an _handleUpgrade,
   * however initialization does not require UUPS.upgradeTo.
   */
  function __GeodeModule_init_unchained(
    address governance,
    address senate,
    uint256 senateExpiry,
    uint256 packageType,
    bytes calldata initVersionName
  ) internal onlyInitializing {
    require(governance != address(0), "GM:governance can not be zero");
    require(senate != address(0), "GM:senate can not be zero");
    require(senateExpiry > block.timestamp, "GM:low senateExpiry");
    require(packageType != 0, "GM:packageType can not be zero");
    require(initVersionName.length != 0, "GM:initVersionName can not be empty");

    GEODE.GOVERNANCE = msg.sender;
    GEODE.SENATE = msg.sender;

    GEODE.SENATE_EXPIRY = senateExpiry;
    GEODE.PACKAGE_TYPE = packageType;

    uint256 initVersion = GEODE.propose(
      DATASTORE,
      _getImplementation(),
      ID_TYPE.CONTRACT_UPGRADE,
      initVersionName,
      1 days
    );

    GEODE.approveProposal(DATASTORE, initVersion);

    _setContractVersion(initVersionName);

    GEODE.GOVERNANCE = governance;
    GEODE.SENATE = senate;
  }

  /**
   * @custom:section                           ** LIMITED UUPS VERSION CONTROL **
   *
   * @custom:visibility -> internal
   */

  /**
   * @dev required by the OZ UUPS module, improved by the Geode Module.
   */
  function _authorizeUpgrade(address proposed_implementation) internal virtual override {
    require(
      GEODE.isUpgradeAllowed(proposed_implementation, _getImplementation()),
      "GM:not allowed to upgrade"
    );
  }

  function _setContractVersion(bytes memory versionName) internal virtual {
    uint256 newVersion = DSML.generateId(versionName, GEODE.PACKAGE_TYPE);
    GEODE.CONTRACT_VERSION = newVersion;

    emit ContractVersionSet(newVersion);
  }

  /**
   * @dev Would use the public upgradeTo() call, which does _authorizeUpgrade and _upgradeToAndCallUUPS,
   * but it is external, OZ have not made it public yet.
   */
  function _handleUpgrade(
    address proposed_implementation,
    bytes memory versionName
  ) internal virtual {
    _authorizeUpgrade(proposed_implementation);
    _upgradeToAndCallUUPS(proposed_implementation, new bytes(0), false);
    _setContractVersion(versionName);
  }

  /**
   * @custom:section                           ** GETTER FUNCTIONS **
   *
   * @custom:visibility -> view-external
   */

  // TODO: maybe seperate this? why not.
  function GeodeParams()
    external
    view
    virtual
    override
    returns (
      address governance,
      address senate,
      address approvedUpgrade,
      uint256 senateExpiry,
      uint256 packageType
    )
  {
    governance = GEODE.GOVERNANCE;
    senate = GEODE.SENATE;
    approvedUpgrade = GEODE.APPROVED_UPGRADE;
    senateExpiry = GEODE.SENATE_EXPIRY;
    packageType = GEODE.PACKAGE_TYPE;
  }

  function getContractVersion() public view virtual override returns (uint256) {
    return GEODE.CONTRACT_VERSION;
  }

  function getProposal(
    uint256 id
  ) external view virtual override returns (GML.Proposal memory proposal) {
    proposal = GEODE.getProposal(id);
  }

  /**
   * @custom:section                           ** SETTER FUNCTIONS **
   *
   * @custom:visibility -> public/external
   */

  /**
   * @custom:subsection                        ** ONLY GOVERNANCE **
   *
   */

  function propose(
    address _CONTROLLER,
    uint256 _TYPE,
    bytes calldata _NAME,
    uint256 duration
  ) public virtual override returns (uint256 id) {
    id = GEODE.propose(DATASTORE, _CONTROLLER, _TYPE, _NAME, duration);
  }

  function rescueSenate(address _newSenate) external virtual override {
    GEODE.rescueSenate(_newSenate);
  }

  /**
   * @custom:subsection                        ** ONLY SENATE **
   */

  /**
   * @dev handles TYPE 2 proposals by upgrading the contract immediately.
   */
  function approveProposal(
    uint256 id
  ) public virtual override returns (address _controller, uint256 _type, bytes memory _name) {
    (_controller, _type, _name) = GEODE.approveProposal(DATASTORE, id);

    if (_type == ID_TYPE.CONTRACT_UPGRADE) {
      _handleUpgrade(_controller, _name);
    }
  }

  function changeSenate(address _newSenate) external virtual override {
    GEODE.changeSenate(_newSenate);
  }

  /**
   * @custom:subsection                        ** ONLY CONTROLLER **
   */

  function changeIdCONTROLLER(uint256 id, address newCONTROLLER) external virtual override {
    GML.changeIdCONTROLLER(DATASTORE, id, newCONTROLLER);
  }
}

contracts/Portal/modules/GeodeModule/lib.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.7;

// globals
import {PERCENTAGE_DENOMINATOR} from "../../../globals/macros.sol";
import {ID_TYPE} from "../../../globals/id_type.sol";
import {RESERVED_KEY_SPACE as rks} from "../../../globals/reserved_key_space.sol";
// libraries
import {DataStoreModuleLib as DSML} from "../../DataStoreModule/libs/DataStoreModuleLib.sol";

/**
 * @title GML: Geode Module Library
 *
 * @notice Dual Governance & Limited Upgradability:
 * Administration of the Isolated Storage with a Dual Governance consisting a Governance and a Senate.
 * Administration of a UUPS contract with Limited Upgradability for Packages like Portal, LiquidityPool.
 *
 * @dev review: DataStoreModule for the IsolatedStorage logic.
 * @dev review: Reserved TYPEs are defined within globals/id_type.sol
 *
 * @dev SENATE_EXPIRY is not mandatory to utilize. Simply set it to MAX_UINT256 if rescueSenate is not needed.
 *
 * @dev There are 3 ways to set a new Senate:
 * 1. With a proposal TYPE 1. Proposal's controller becomes the new Senate, refreshes the expiry.
 * 2. Current Senate can call changeSenate, which doesn't change the expiry
 * 3. As a circuit breaker: If senate is expired, then rescue senate can be called by governance.
 * @dev Currently, there are no way to set a new Governance.
 *
 *
 * @dev Contracts relying on this library must use GeodeModuleLib.DualGovernance
 * @dev This is an external library, requires deployment.
 *
 * @author Ice Bear & Crash Bandicoot
 */
library GeodeModuleLib {
  using DSML for DSML.IsolatedStorage;

  /**
   * @custom:section                           ** STRUCTS **
   */

  /**
   * @notice Giving the control of a specific ID to proposed CONTROLLER.
   *
   * @param TYPE: refer to globals/id_type.sol
   * @param CONTROLLER: the address that refers to the change that is proposed by given proposal.
   * * This slot can refer to the controller of an id, a new implementation contract, a new Senate etc.
   * @param NAME: DataStore generates ID by keccak(name, type)
   * @param deadline: refers to last timestamp until a proposal expires, limited by MAX_PROPOSAL_DURATION
   * * Expired proposals can not be approved by Senate
   * * Expired proposals can not be overriden by new proposals
   **/
  struct Proposal {
    address CONTROLLER;
    uint256 TYPE;
    bytes NAME;
    uint256 deadline;
  }

  /**
   * @notice Dual Governance allows 2 parties to manage a package with proposals and approvals.
   * @param GOVERNANCE a community that works to improve the core product and ensures its adoption in the DeFi ecosystem
   * Suggests updates, such as new operators, contract upgrades, a new Senate (without any permission to force them)
   * @param SENATE An address that protects the users by controlling the state of governance, contract updates and other crucial changes
   * @param APPROVED_UPGRADE only 1 implementation contract SHOULD be "approved" at any given time.
   * @param SENATE_EXPIRY refers to the last timestamp that SENATE can continue operating. Might not be utilized. Limited by MAX_SENATE_PERIOD
   * @param PACKAGE_TYPE every package has a specific TYPE. Defined in globals/id_type.sol
   * @param CONTRACT_VERSION always refers to the upgrade proposal ID. Does NOT increase uniformly like one might expect.
   * @param proposals till approved, proposals are kept separated from the Isolated Storage
   * @param __gap keep the struct size at 16
   **/
  struct DualGovernance {
    address GOVERNANCE;
    address SENATE;
    address APPROVED_UPGRADE;
    uint256 SENATE_EXPIRY;
    uint256 PACKAGE_TYPE;
    uint256 CONTRACT_VERSION;
    mapping(uint256 => Proposal) proposals;
    uint256[9] __gap;
  }

  /**
   * @custom:section                           ** CONSTANTS **
   */

  /// @notice a proposal can have a duration between 1 days to 4 weeks (inclusive)
  uint32 public constant MIN_PROPOSAL_DURATION = 1 days;
  uint32 public constant MAX_PROPOSAL_DURATION = 4 weeks;

  /// @notice if expiry is utilized, a senate can be active for a year.
  /// @dev "MAX" underlines a new senate can be set without expecting an expiry
  uint32 public constant MAX_SENATE_PERIOD = 365 days;

  /**
   * @custom:section                           ** EVENTS **
   */
  event Proposed(uint256 indexed TYPE, uint256 ID, address CONTROLLER, uint256 deadline);
  event Approved(uint256 ID);
  event NewSenate(address senate, uint256 expiry);
  event ControllerChanged(uint256 indexed ID, address CONTROLLER);

  /**
   * @custom:section                           ** MODIFIERS **
   */
  modifier onlyGovernance(DualGovernance storage self) {
    require(msg.sender == self.GOVERNANCE, "GML:GOVERNANCE role needed");
    _;
  }

  modifier onlySenate(DualGovernance storage self) {
    require(msg.sender == self.SENATE, "GML:SENATE role needed");
    require(block.timestamp < self.SENATE_EXPIRY, "GML:SENATE expired");
    _;
  }

  modifier onlyController(DSML.IsolatedStorage storage DATASTORE, uint256 id) {
    require(msg.sender == DATASTORE.readAddress(id, rks.CONTROLLER), "GML:CONTROLLER role needed");
    _;
  }

  /**
   * @custom:section                           ** LIMITED UUPS VERSION CONTROL **
   *
   * @custom:visibility -> view-external
   */

  /**
   * @notice Check if it is allowed to change the package version to given proposedImplementation.
   * @dev provided for _authorizeUpgrade
   * @dev currentImplementation should always be UUPS._getImplementation()
   * @dev currentImplementation or zero as proposedImplementation will return false
   **/
  function isUpgradeAllowed(
    DualGovernance storage self,
    address proposedImplementation,
    address currentImplementation
  ) external view returns (bool) {
    return
      (self.APPROVED_UPGRADE != address(0)) &&
      (proposedImplementation != currentImplementation) &&
      (self.APPROVED_UPGRADE == proposedImplementation);
  }

  /**
   * @custom:section                           ** GETTER FUNCTIONS **
   *
   * @custom:visibility -> view-external
   */

  function getProposal(
    DualGovernance storage self,
    uint256 id
  ) external view returns (Proposal memory) {
    return self.proposals[id];
  }

  /**
   * @custom:section                           ** SETTER FUNCTIONS **
   */

  /**
   * @custom:subsection                        ** INTERNAL **
   *
   * @custom:visibility -> internal
   */
  function _setSenate(DualGovernance storage self, address _newSenate, uint256 _expiry) internal {
    self.SENATE = _newSenate;
    self.SENATE_EXPIRY = _expiry;

    emit NewSenate(self.SENATE, self.SENATE_EXPIRY);
  }

  /**
   * @custom:subsection                        ** ONLY GOVERNANCE **
   *
   * @custom:visibility -> external
   */

  /**
   * @notice generates a new ID with given TYPE and NAME, proposes it to be owned by a CONTROLLER.
   * @dev DATASTORE[id] will not be updated until the proposal is approved
   * @dev Proposals can NEVER be overriden
   */
  function propose(
    DualGovernance storage self,
    DSML.IsolatedStorage storage DATASTORE,
    address _CONTROLLER,
    uint256 _TYPE,
    bytes calldata _NAME,
    uint256 duration
  ) external onlyGovernance(self) returns (uint256 id) {
    id = DSML.generateId(_NAME, _TYPE);

    require(self.proposals[id].deadline == 0, "GML:already proposed");
    require((DATASTORE.readBytes(id, rks.NAME)).length == 0, "GML:ID already exist");
    require(_CONTROLLER != address(0), "GML:CONTROLLER can NOT be ZERO");
    require(
      (_TYPE != ID_TYPE.NONE) && (_TYPE != ID_TYPE.__GAP__) && (_TYPE != ID_TYPE.POOL),
      "GML:TYPE is NONE, GAP or POOL"
    );
    require(
      (duration >= MIN_PROPOSAL_DURATION) && (duration <= MAX_PROPOSAL_DURATION),
      "GML:invalid proposal duration"
    );

    uint256 _deadline = block.timestamp + duration;

    self.proposals[id] = Proposal({
      CONTROLLER: _CONTROLLER,
      TYPE: _TYPE,
      NAME: _NAME,
      deadline: _deadline
    });

    emit Proposed(_TYPE, id, _CONTROLLER, _deadline);
  }

  /**
   * @notice changes Senate in a scenerio where the current Senate acts maliciously!
   * * We are sure this will not be the case, but creating a method for possible recovery is a must.
   * @notice Normally, Governance creates Senate Proposals frequently to signal it does not have
   * * any intent of malicious overtake.
   * note: If Governance does not send a Senate Proposal "a while" before the SENATE_EXPIRY,
   * * we recommend users to take their money out.
   * @dev Obviously, Governance needs to wait for SENATE_EXPIRY.
   * @dev Refreshes the expiry
   */
  function rescueSenate(
    DualGovernance storage self,
    address _newSenate
  ) external onlyGovernance(self) {
    require(block.timestamp > self.SENATE_EXPIRY, "GML:cannot rescue yet");

    _setSenate(self, _newSenate, block.timestamp + MAX_SENATE_PERIOD);
  }

  /**
   * @custom:subsection                        ** ONLY SENATE **
   *
   * @custom:visibility -> external
   */

  /**
   * @notice approves a proposal and records given data to DataStore
   * @notice specific changes for the reserved types (1,2,3) are implemented here,
   * any other addition should take place in Portal, as not related. Note that GM has additional logic for TYPE 2 approvals.
   * @param id given ID proposal that has will be approved by Senate
   * @dev Senate is not able to approve approved proposals
   * @dev Senate is not able to approve expired proposals
   */
  function approveProposal(
    DualGovernance storage self,
    DSML.IsolatedStorage storage DATASTORE,
    uint256 id
  ) external onlySenate(self) returns (address _controller, uint256 _type, bytes memory _name) {
    require(self.proposals[id].deadline > block.timestamp, "GML:NOT an active proposal");

    _controller = self.proposals[id].CONTROLLER;
    _type = self.proposals[id].TYPE;
    _name = self.proposals[id].NAME;

    DATASTORE.writeUint(id, rks.TYPE, _type);
    DATASTORE.writeAddress(id, rks.CONTROLLER, _controller);
    DATASTORE.writeBytes(id, rks.NAME, _name);
    DATASTORE.allIdsByType[_type].push(id);

    if (_type == ID_TYPE.SENATE) {
      _setSenate(self, _controller, block.timestamp + MAX_SENATE_PERIOD);
    } else if (_type == ID_TYPE.CONTRACT_UPGRADE) {
      self.APPROVED_UPGRADE = _controller;
    }

    // important
    self.proposals[id].deadline = block.timestamp;

    emit Approved(id);
  }

  /**
   * @notice It is useful to be able to change the Senate's address without changing the expiry.
   * @dev Does not change the expiry
   */
  function changeSenate(DualGovernance storage self, address _newSenate) external onlySenate(self) {
    _setSenate(self, _newSenate, self.SENATE_EXPIRY);
  }

  /**
   * @custom:section                           ** ONLY CONTROLLER **
   *
   * @custom:visibility -> external
   */

  /**
   * @notice change the CONTROLLER of an ID
   * @dev this operation can not be reverted by the old CONTROLLER!!!
   * @dev can not provide address(0), try 0x000000000000000000000000000000000000dEaD
   */
  function changeIdCONTROLLER(
    DSML.IsolatedStorage storage DATASTORE,
    uint256 id,
    address newCONTROLLER
  ) external onlyController(DATASTORE, id) {
    require(newCONTROLLER != address(0), "GML:CONTROLLER can not be zero");

    DATASTORE.writeAddress(id, rks.CONTROLLER, newCONTROLLER);

    emit ControllerChanged(id, newCONTROLLER);
  }
}

contracts/Portal/modules/LiquidityModule.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.7;

// libraries
import {LiquidityModuleLib as LML} from "./LiquidityModuleLib.sol";

/**
 * @title AL: Amplification Library
 *
 * @notice A helper library for Liquidity Module Library (LML) to calculate and ramp the A parameter of a given `LiquidityModuleLib.Swap` struct.
 *
 * @dev review: Liquidity Module for the StableSwap logic.
 * @dev This library assumes the LML.Swap struct is fully validated.
 *
 * @dev This is an internal library, requires NO deployment.
 *
 * @author Ice Bear & Crash Bandicoot
 */
library AmplificationLib {
  /**
   * @custom:section                           ** CONSTANTS **
   */
  uint256 public constant A_PRECISION = 100;
  uint256 public constant MAX_A = 10 ** 6;
  uint256 public constant MAX_A_CHANGE = 2;
  uint256 public constant MIN_RAMP_TIME = 14 days;

  /**
   * @custom:section                           ** EVENTS **
   */
  event RampA(uint256 oldA, uint256 newA, uint256 initialTime, uint256 futureTime);
  event StopRampA(uint256 currentA, uint256 time);

  /**
   * @custom:section                           ** GETTER FUNCTIONS **
   *
   * @custom:visibility -> view-internal
   */

  /**
   * @notice Return A, the amplification coefficient * n * (n - 1)
   * @dev See the StableSwap paper for details
   * @param self Swap struct to read from
   * @return A parameter
   */
  function getA(LML.Swap storage self) internal view returns (uint256) {
    return _getAPrecise(self) / (A_PRECISION);
  }

  /**
   * @notice Return A in its raw precision
   * @dev See the StableSwap paper for details
   * @param self Swap struct to read from
   * @return A parameter in its raw precision form
   */
  function getAPrecise(LML.Swap storage self) internal view returns (uint256) {
    return _getAPrecise(self);
  }

  /**
   * @notice Return A in its raw precision
   * @dev See the StableSwap paper for details
   * @param self Swap struct to read from
   * @return A parameter in its raw precision form
   */
  function _getAPrecise(LML.Swap storage self) internal view returns (uint256) {
    uint256 t1 = self.futureATime; // time when ramp is finished
    uint256 a1 = self.futureA; // final A value when ramp is finished

    if (block.timestamp < t1) {
      uint256 t0 = self.initialATime; // time when ramp is started
      uint256 a0 = self.initialA; // initial A value when ramp is started
      if (a1 > a0) {
        // a0 + (a1 - a0) * (block.timestamp - t0) / (t1 - t0)
        return a0 + ((a1 - a0) * (block.timestamp - t0)) / (t1 - t0);
      } else {
        // a0 - (a0 - a1) * (block.timestamp - t0) / (t1 - t0)
        return a0 - ((a0 - a1) * (block.timestamp - t0)) / (t1 - t0);
      }
    } else {
      return a1;
    }
  }

  /**
   * @custom:section                           ** SETTER FUNCTIONS **
   *
   * @custom:visibility -> internal
   */

  /**
   * @notice Start ramping up or down A parameter towards given futureA_ and futureTime_
   * Checks if the change is too rapid, and commits the new A value only when it falls under
   * the limit range.
   * @param self Swap struct to update
   * @param futureA_ the new A to ramp towards
   * @param futureTime_ timestamp when the new A should be reached
   */
  function rampA(LML.Swap storage self, uint256 futureA_, uint256 futureTime_) internal {
    require(block.timestamp >= self.initialATime + 1 days, "AL:Wait 1 day before starting ramp");
    require(futureTime_ >= block.timestamp + MIN_RAMP_TIME, "AL:Insufficient ramp time");
    require(futureA_ > 0 && futureA_ < MAX_A, "AL:futureA_ must be > 0 and < MAX_A");

    uint256 initialAPrecise = _getAPrecise(self);
    uint256 futureAPrecise = futureA_ * A_PRECISION;

    if (futureAPrecise < initialAPrecise) {
      require(futureAPrecise * MAX_A_CHANGE >= initialAPrecise, "AL:futureA_ is too small");
    } else {
      require(futureAPrecise <= initialAPrecise * MAX_A_CHANGE, "AL:futureA_ is too large");
    }

    self.initialA = initialAPrecise;
    self.futureA = futureAPrecise;
    self.initialATime = block.timestamp;
    self.futureATime = futureTime_;

    emit RampA(initialAPrecise, futureAPrecise, block.timestamp, futureTime_);
  }

  /**
   * @notice Stops ramping A immediately. Once this function is called, rampA()
   * cannot be called for another 24 hours
   * @param self Swap struct to update
   */
  function stopRampA(LML.Swap storage self) internal {
    require(self.futureATime > block.timestamp, "AL:Ramp is already stopped");

    uint256 currentA = _getAPrecise(self);
    self.initialA = currentA;
    self.futureA = currentA;
    self.initialATime = block.timestamp;
    self.futureATime = block.timestamp;

    emit StopRampA(currentA, block.timestamp);
  }
}

// SPDX-License-Identifier: MIT
pragma solidity =0.8.7;

// globals
import {PERCENTAGE_DENOMINATOR} from "../../../globals/macros.sol";
// interfaces
import {IgETH} from "../../../interfaces/IgETH.sol";
// libraries
import {AmplificationLib as AL} from "./AmplificationLib.sol";
// contracts
import {ILPToken} from "../../../interfaces/helpers/ILPToken.sol";

/**
 * @title LiquidityModule Library - LML
 *
 * @notice A library to be used within LiquidityModule
 * * Contains functions responsible for custody and AMM functionalities with some changes.
 * * The main functionality of Liquidity Pools is allowing the depositors to have instant access to liquidity
 * * relying on the Oracle Price, with the help of Liquidity Providers.
 *
 * @dev focus point (1-1) of the pricing algorithm is manipulated with PriceIn and PriceOut functions.
 * Because the underlying price of the staked assets are expected to raise in time.
 * One can see this similar to accomplishing a "rebasing" logic, with the help of a trusted price source.
 * Whenever "Effective Balance" is mentioned it refers to the balance projected with the underlying price.
 *
 * @dev Contracts relying on this library must initialize LiquidityModuleLib.Swap struct
 * * Note that this library contains both functions called by users and admins.
 * * Admin functions should be protected within contracts using this library.
 *
 * @author Ice Bear & Crash Bandicoot
 */
library LiquidityModuleLib {
  /**
   * @custom:section                           ** STRUCTS **
   */

  /**
   * @notice Storage struct for the liquidity pool logic, should be correctly initialized.
   *
   * @param gETH ERC1155 contract
   * @param lpToken address of the LP Token
   * @param pooledTokenId gETH ID of the pooled staking derivative
   * @param initialA the amplification coefficient * n * (n - 1)
   * @param futureA the amplification coef that will be effective after futureATime
   * @param initialATime variable around the ramp management of A
   * @param futureATime variable around the ramp management of A
   * @param swapFee fee as a percentage/PERCENTAGE_DENOMINATOR, will be deducted from resulting tokens of a swap
   * @param adminFee fee as a percentage/PERCENTAGE_DENOMINATOR, will be deducted from swapFee
   * @param balances the pool balance as [ETH, gETH]; the contract's actual token balance might differ
   * @param __gap keep the contract size at 16
   */
  struct Swap {
    IgETH gETH;
    ILPToken lpToken;
    uint256 pooledTokenId;
    uint256 initialA;
    uint256 futureA;
    uint256 initialATime;
    uint256 futureATime;
    uint256 swapFee;
    uint256 adminFee;
    uint256[2] balances;
    uint256[5] __gap;
  }

  /**
   * @notice Struct storing variables used in calculations in the
   * calculateWithdrawOneTokenDY function to avoid stack too deep errors
   */
  struct CalculateWithdrawOneTokenDYInfo {
    uint256 d0;
    uint256 d1;
    uint256 newY;
    uint256 feePerToken;
    uint256 preciseA;
  }

  /**
   * @notice  Struct storing variables used in calculations in the
   * {add,remove} Liquidity functions to avoid stack too deep errors
   */
  struct ManageLiquidityInfo {
    ILPToken lpToken;
    uint256 d0;
    uint256 d1;
    uint256 d2;
    uint256 preciseA;
    uint256 totalSupply;
    uint256[2] balances;
  }

  /**
   * @custom:section                           ** CONSTANTS **
   */

  /// @notice Max swap fee is 1% or 100bps of each swap
  uint256 public constant MAX_SWAP_FEE = PERCENTAGE_DENOMINATOR / 100;

  /// @notice Max adminFee is 50% of the swapFee
  /// adminFee does not add additional fee on top of swapFee
  /// instead it takes a certain percentage of the swapFee.
  /// Therefore it has no impact on users but only on the earnings of LPs
  uint256 public constant MAX_ADMIN_FEE = (50 * PERCENTAGE_DENOMINATOR) / 100;

  /// @notice Constant value used as max loop limit
  uint256 private constant MAX_LOOP_LIMIT = 256;

  /**
   * @custom:section                           ** EVENTS **
   */

  event TokenSwap(
    address indexed buyer,
    uint256 tokensSold,
    uint256 tokensBought,
    uint128 soldId,
    uint128 boughtId
  );
  event AddLiquidity(
    address indexed provider,
    uint256[2] tokenAmounts,
    uint256[2] fees,
    uint256 invariant,
    uint256 lpTokenSupply
  );
  event RemoveLiquidity(address indexed provider, uint256[2] tokenAmounts, uint256 lpTokenSupply);
  event RemoveLiquidityOne(
    address indexed provider,
    uint256 lpTokenAmount,
    uint256 lpTokenSupply,
    uint256 boughtId,
    uint256 tokensBought
  );
  event RemoveLiquidityImbalance(
    address indexed provider,
    uint256[2] tokenAmounts,
    uint256[2] fees,
    uint256 invariant,
    uint256 lpTokenSupply
  );
  event NewAdminFee(uint256 newAdminFee);
  event NewSwapFee(uint256 newSwapFee);

  /**
   * @custom:section                           ** HELPERS **
   *
   * @custom:visibility -> pure-internal
   */

  /**
   * @custom:subsection Math helpers
   */

  /**
   * @notice Compares a and b and returns true if the difference between a and b is 1 or 0.
   * @param a uint256 to compare with
   * @param b uint256 to compare with
   * @return True if the difference between a and b is less than 1 or equal.
   */
  function within1(uint256 a, uint256 b) internal pure returns (bool) {
    return (difference(a, b) <= 1);
  }

  /**
   * @notice Calculates absolute difference between a and b
   * @param a uint256 to compare with
   * @param b uint256 to compare with
   * @return Difference between a and b
   */
  function difference(uint256 a, uint256 b) internal pure returns (uint256) {
    if (a > b) {
      return a - b;
    }
    return b - a;
  }

  /**
   * @custom:subsection StableSwap invariants: D,Y,YD
   */

  /**
   * @notice Calculate the price of a token in the pool with given
   *  balances and a particular D.
   *
   * @dev This is accomplished via solving the invariant iteratively.
   * See the StableSwap paper and Curve.fi implementation for further details.
   *
   * x_1**2 + x1 * (sum' - (A*n**n - 1) * D / (A * n**n)) = D ** (n + 1) / (n ** (2 * n) * prod' * A)
   * x_1**2 + b*x_1 = c
   * x_1 = (x_1**2 + c) / (2*x_1 + b)
   *
   * @param a the amplification coefficient * n * (n - 1). See the StableSwap paper for details.
   * @param tokenIndex Index of token we are calculating for.
   * @param xp a  set of pool balances. Array should be
   * the same cardinality as the pool.
   * @param d the stableswap invariant
   * @return the price of the token, in the same precision as in xp
   */
  function getYD(
    uint256 a,
    uint8 tokenIndex,
    uint256[2] memory xp,
    uint256 d
  ) internal pure returns (uint256) {
    uint256 numTokens = 2;
    require(tokenIndex < numTokens, "LML:Token not found");

    uint256 c = d;
    uint256 s;
    uint256 nA = a * numTokens;

    for (uint256 i = 0; i < numTokens; ++i) {
      if (i != tokenIndex) {
        s = s + xp[i];
        c = (c * d) / (xp[i] * (numTokens));
        // If we were to protect the division loss we would have to keep the denominator separate
        // and divide at the end. However this leads to overflow with large numTokens or/and D.
        // c = c * D * D * D * ... overflow!
      }
    }
    c = (c * d * AL.A_PRECISION) / (nA * numTokens);

    uint256 b = s + ((d * AL.A_PRECISION) / nA);
    uint256 yPrev;
    uint256 y = d;

    for (uint256 i = 0; i < MAX_LOOP_LIMIT; ++i) {
      yPrev = y;
      y = ((y * y) + c) / (2 * y + b - d);
      if (within1(y, yPrev)) {
        return y;
      }
    }
    revert("Approximation did not converge");
  }

  /**
   * @notice Get D, the StableSwap invariant, based on a set of balances and a particular A.
   * @param xp a  set of pool balances. Array should be the same cardinality
   * as the pool.
   * @param a the amplification coefficient * n * (n - 1) in A_PRECISION.
   * See the StableSwap paper for details
   * @return the invariant, at the precision of the pool
   */
  function getD(uint256[2] memory xp, uint256 a) internal pure returns (uint256) {
    uint256 numTokens = 2;
    uint256 s = xp[0] + xp[1];
    if (s == 0) {
      return 0;
    }

    uint256 prevD;
    uint256 d = s;
    uint256 nA = a * numTokens;

    for (uint256 i = 0; i < MAX_LOOP_LIMIT; ++i) {
      uint256 dP = (d ** (numTokens + 1)) / (numTokens ** numTokens * xp[0] * xp[1]);
      prevD = d;
      d =
        ((((nA * s) / AL.A_PRECISION) + dP * numTokens) * (d)) /
        (((nA - AL.A_PRECISION) * (d)) / (AL.A_PRECISION) + ((numTokens + 1) * dP));

      if (within1(d, prevD)) {
        return d;
      }
    }

    // Convergence should occur in 4 loops or less. If this is reached, there may be something wrong
    // with the pool. If this were to occur repeatedly, LPs should withdraw via `removeLiquidity()`
    // function which does not rely on D.
    revert("D does not converge");
  }

  /**
   * @notice Calculate the new balances of the tokens given the indexes of the token
   * that is swapped from (FROM) and the token that is swapped to (TO).
   * This function is used as a helper function to calculate how much TO token
   * the user should receive on swap.
   *
   * @param preciseA precise form of amplification coefficient
   * @param tokenIndexFrom index of FROM token
   * @param tokenIndexTo index of TO token
   * @param x the new total amount of FROM token
   * @param xp balances of the tokens in the pool
   * @return the amount of TO token that should remain in the pool
   */
  function getY(
    uint256 preciseA,
    uint8 tokenIndexFrom,
    uint8 tokenIndexTo,
    uint256 x,
    uint256[2] memory xp
  ) internal pure returns (uint256) {
    uint256 numTokens = 2;
    require(tokenIndexFrom != tokenIndexTo, "LML:Cannot compare token to itself");
    require(tokenIndexFrom < numTokens && tokenIndexTo < numTokens, "LML:Tokens must be in pool");

    uint256 d = getD(xp, preciseA);
    uint256 c = d;
    uint256 s = x;
    uint256 nA = numTokens * (preciseA);

    c = (c * d) / (x * numTokens);
    c = (c * d * (AL.A_PRECISION)) / (nA * numTokens);
    uint256 b = s + ((d * AL.A_PRECISION) / nA);

    uint256 yPrev;
    uint256 y = d;

    for (uint256 i = 0; i < MAX_LOOP_LIMIT; ++i) {
      yPrev = y;
      y = ((y * y) + c) / (2 * y + b - d);
      if (within1(y, yPrev)) {
        return y;
      }
    }
    revert("Approximation did not converge");
  }

  /**
   * @custom:subsection                           ** REBASING FUNCTIONS **
   *
   * @custom:visibility -> view-internal
   */

  /**
   * @notice This function MULTIPLIES the Staking Derivative (gETH) balance with underlying relative price (pricePerShare),
   * to keep pricing around 1-OraclePrice instead of 1-1 like stableSwap pool.
   * @dev this function assumes prices are sent with the indexes that [ETH, gETH]
   * @param balance balance that will be taken into calculation
   * @param i if i is 0 it means we are dealing with ETH, if i is 1 it is gETH
   */
  function _pricedIn(
    Swap storage self,
    uint256 balance,
    uint256 i
  ) internal view returns (uint256) {
    return
      i == 1
        ? (balance * self.gETH.pricePerShare(self.pooledTokenId)) / self.gETH.denominator()
        : balance;
  }

  /**
   * @notice This function DIVIDES the Staking Derivative (gETH) balance with underlying relative price (pricePerShare),
   * to keep pricing around 1-OraclePrice instead of 1-1 like stableSwap pool.
   * @dev this function assumes prices are sent with the indexes that [ETH, gETH]
   * @param balance balance that will be taken into calculation
   * @param i if i is 0 it means we are dealing with ETH, if i is 1 it is gETH
   */
  function _pricedOut(
    Swap storage self,
    uint256 balance,
    uint256 i
  ) internal view returns (uint256) {
    return
      i == 1
        ? (balance * self.gETH.denominator()) / self.gETH.pricePerShare(self.pooledTokenId)
        : balance;
  }

  /**
   * @notice This function MULTIPLIES the Staking Derivative (gETH) balance with underlying relative price (pricePerShare),
   * to keep pricing around 1-OraclePrice instead of 1-1 like stableSwap pool.
   * @dev this function assumes prices are sent with the indexes that [ETH, gETH]
   * @param balances ARRAY of balances that will be taken into calculation
   */
  function _pricedInBatch(
    Swap storage self,
    uint256[2] memory balances
  ) internal view returns (uint256[2] memory _p) {
    _p[0] = balances[0];
    _p[1] = (balances[1] * self.gETH.pricePerShare(self.pooledTokenId)) / self.gETH.denominator();
    return _p;
  }

  /**
   * @notice This function DIVIDES the Staking Derivative (gETH) balance with underlying relative price (pricePerShare),
   * to keep pricing around 1-OraclePrice instead of 1-1 like stableSwap pool.
   * @dev this function assumes prices are sent with the indexes that [ETH, gETH]
   * @param balances ARRAY of balances that will be taken into calculation
   */
  function _pricedOutBatch(
    Swap storage self,
    uint256[2] memory balances
  ) internal view returns (uint256[2] memory _p) {
    _p[0] = balances[0];
    _p[1] = (balances[1] * self.gETH.denominator()) / self.gETH.pricePerShare(self.pooledTokenId);
    return _p;
  }

  /**
   * @custom:subsection                           ** DEBT FUNCTIONS **
   *
   * @custom:visibility -> view
   *
   * @dev debt refers to the amount of ETH needed to stabilize the pool
   */

  /**
   * @custom:visibility -> internal
   */
  /**
   * @notice Get Debt, The amount of buyback for stable pricing.
   * @param xp a  set of pool balances. Array should be the same cardinality
   * as the pool.
   * @param a the amplification coefficient * n * (n - 1) in A_PRECISION.
   * See the StableSwap paper for details
   * @return debt the half of the D StableSwap invariant when debt is needed to be payed.
   */
  function _getDebt(
    Swap storage self,
    uint256[2] memory xp,
    uint256 a
  ) internal view returns (uint256 debt) {
    uint256 halfD = getD(xp, a) / 2;
    if (xp[0] >= halfD) {
      debt = 0;
    } else {
      uint256 dy = xp[1] - halfD;
      uint256 feeHalf = (dy * self.swapFee) / PERCENTAGE_DENOMINATOR / 2;
      debt = halfD - xp[0] + feeHalf;
    }
  }

  /**
   * @custom:visibility -> external
   */
  /**
   * @return debt the half of the D StableSwap invariant when debt is needed to be payed.
   * @dev might change when price is in.
   */
  function getDebt(Swap storage self) external view returns (uint256) {
    return _getDebt(self, _pricedInBatch(self, self.balances), AL._getAPrecise(self));
  }

  /**
   * @custom:section                           ** SWAP HELPER FUNCTIONS **
   */

  /**
   * @custom:visibility -> pure-internal
   */
  /**
   * @notice A simple method to calculate amount of each underlying
   * tokens that is returned upon burning given amount of
   * LP tokens
   *
   * @param amount the amount of LP tokens that would to be burned on
   * withdrawal
   * @return amounts of tokens user will receive as an array [ETH, gETH]
   */
  function _calculateRemoveLiquidity(
    uint256[2] memory balances,
    uint256 amount,
    uint256 totalSupply
  ) internal pure returns (uint256[2] memory amounts) {
    require(amount <= totalSupply, "LML:Cannot exceed total supply");

    amounts[0] = (balances[0] * amount) / totalSupply;
    amounts[1] = (balances[1] * amount) / totalSupply;

    return amounts;
  }

  /**
   * @custom:visibility -> view-internal
   */

  function _calculateWithdrawOneToken(
    Swap storage self,
    uint256 tokenAmount,
    uint8 tokenIndex,
    uint256 totalSupply
  ) internal view returns (uint256, uint256) {
    uint256 dy;
    uint256 newY;
    uint256 currentY;

    (dy, newY, currentY) = calculateWithdrawOneTokenDY(self, tokenIndex, tokenAmount, totalSupply);

    uint256 dySwapFee = currentY - newY - dy;

    return (dy, dySwapFee);
  }

  /**
   * @notice Internally calculates a swap between two tokens.
   *
   * @dev The caller is expected to transfer the actual amounts (dx and dy)
   * using the token contracts.
   *
   * @param self Swap struct to read from
   * @param tokenIndexFrom the token to sell
   * @param tokenIndexTo the token to buy
   * @param dx the number of tokens to sell. If the token charges a fee on transfers,
   * use the amount that gets transferred after the fee.
   * @return dy the number of tokens the user will get
   * @return dyFee the associated fee
   */
  function _calculateSwap(
    Swap storage self,
    uint8 tokenIndexFrom,
    uint8 tokenIndexTo,
    uint256 dx,
    uint256[2] memory balances
  ) internal view returns (uint256 dy, uint256 dyFee) {
    require(tokenIndexFrom < 2 && tokenIndexTo < 2, "LML:Token index out of range");

    uint256 x = _pricedIn(self, dx + balances[tokenIndexFrom], tokenIndexFrom);
    uint256[2] memory pricedBalances = _pricedInBatch(self, balances);
    uint256 y = _pricedOut(
      self,
      getY(AL._getAPrecise(self), tokenIndexFrom, tokenIndexTo, x, pricedBalances),
      tokenIndexTo // => not id, index !!!
    );
    dy = balances[tokenIndexTo] - y - 1;
    dyFee = (dy * self.swapFee) / (PERCENTAGE_DENOMINATOR);
    dy = dy - dyFee;
  }

  /**
   * @custom:visibility -> view-external
   */

  /**
   * @notice Calculate the dy, the amount of selected token that user receives and
   * the fee of withdrawing in one token
   * @param tokenAmount the amount to withdraw in the pool's precision
   * @param tokenIndex which token will be withdrawn
   * @param self Swap struct to read from
   * @return the amount of token user will receive
   */
  function calculateWithdrawOneToken(
    Swap storage self,
    uint256 tokenAmount,
    uint8 tokenIndex
  ) external view returns (uint256) {
    (uint256 availableTokenAmount, ) = _calculateWithdrawOneToken(
      self,
      tokenAmount,
      tokenIndex,
      self.lpToken.totalSupply()
    );
    return availableTokenAmount;
  }

  /**
   * @notice Calculate the dy of withdrawing in one token
   * @param self Swap struct to read from
   * @param tokenIndex which token will be withdrawn
   * @param tokenAmount the amount to withdraw in the pools precision
   * @return the d and the new y after withdrawing one token
   */
  function calculateWithdrawOneTokenDY(
    Swap storage self,
    uint8 tokenIndex,
    uint256 tokenAmount,
    uint256 totalSupply
  ) internal view returns (uint256, uint256, uint256) {
    // Get the current D, then solve the stableswap invariant
    // y_i for D - tokenAmount

    require(tokenIndex < 2, "LML:Token index out of range");

    CalculateWithdrawOneTokenDYInfo memory v = CalculateWithdrawOneTokenDYInfo(0, 0, 0, 0, 0);
    v.preciseA = AL._getAPrecise(self);
    v.d0 = getD(_pricedInBatch(self, self.balances), v.preciseA);
    v.d1 = v.d0 - ((tokenAmount * v.d0) / totalSupply);

    require(tokenAmount <= self.balances[tokenIndex], "LML:Withdraw exceeds available");

    v.newY = _pricedOut(
      self,
      getYD(v.preciseA, tokenIndex, _pricedInBatch(self, self.balances), v.d1),
      tokenIndex
    );

    uint256[2] memory xpReduced;

    v.feePerToken = self.swapFee / 2;
    for (uint256 i = 0; i < 2; ++i) {
      uint256 xpi = self.balances[i];
      xpReduced[i] =
        xpi -
        ((((i == tokenIndex) ? (xpi * v.d1) / v.d0 - v.newY : xpi - ((xpi * v.d1) / (v.d0))) *
          (v.feePerToken)) / (PERCENTAGE_DENOMINATOR));
    }

    uint256 dy = xpReduced[tokenIndex] -
      _pricedOut(
        self,
        (getYD(v.preciseA, tokenIndex, _pricedInBatch(self, xpReduced), v.d1)),
        tokenIndex
      );
    dy = dy - 1;

    return (dy, v.newY, self.balances[tokenIndex]);
  }

  /**
   * @notice Get the virtual price, to help calculate profit
   * @param self Swap struct to read from
   * @return the virtual price
   */
  function getVirtualPrice(Swap storage self) external view returns (uint256) {
    uint256 d = getD(_pricedInBatch(self, self.balances), AL._getAPrecise(self));
    ILPToken lpToken = self.lpToken;
    uint256 supply = lpToken.totalSupply();
    if (supply > 0) {
      return (d * 10 ** 18) / supply;
    }
    return 0;
  }

  /**
   * @notice Externally calculates a swap between two tokens.
   * @param self Swap struct to read from
   * @param tokenIndexFrom the token to sell
   * @param tokenIndexTo the token to buy
   * @param dx the number of tokens to sell. If the token charges a fee on transfers,
   * use the amount that gets transferred after the fee.
   * @return dy the number of tokens the user will get
   */
  function calculateSwap(
    Swap storage self,
    uint8 tokenIndexFrom,
    uint8 tokenIndexTo,
    uint256 dx
  ) external view returns (uint256 dy) {
    (dy, ) = _calculateSwap(self, tokenIndexFrom, tokenIndexTo, dx, self.balances);
  }

  /**
   * @notice Uses _calculateRemoveLiquidity with Effective Balances,
   * then projects the prices to the token amounts
   * to get Real Balances, before removing them from pool.
   */
  function calculateRemoveLiquidity(
    Swap storage self,
    uint256 amount
  ) external view returns (uint256[2] memory) {
    return
      _pricedOutBatch(
        self,
        _calculateRemoveLiquidity(
          _pricedInBatch(self, self.balances),
          amount,
          self.lpToken.totalSupply()
        )
      );
  }

  /**
   * @notice A simple method to calculate prices from deposits or
   * withdrawals, excluding fees but including slippage. This is
   * helpful as an input into the various "min" parameters on calls
   * to fight front-running
   *
   * @dev This shouldn't be used outside frontends for user estimates.
   *
   * @param self Swap struct to read from
   * @param amounts an array of token amounts to deposit or withdrawal,
   * corresponding to pooledTokens. The amount should be in each
   * pooled token's native precision. If a token charges a fee on transfers,
   * use the amount that gets transferred after the fee.
   * @param deposit whether this is a deposit or a withdrawal
   * @return if deposit was true, total amount of lp token that will be minted and if
   * deposit was false, total amount of lp token that will be burned
   */
  function calculateTokenAmount(
    Swap storage self,
    uint256[2] calldata amounts,
    bool deposit
  ) external view returns (uint256) {
    uint256 a = AL._getAPrecise(self);
    uint256[2] memory balances = self.balances;

    uint256 d0 = getD(_pricedInBatch(self, balances), a);
    for (uint256 i = 0; i < 2; ++i) {
      if (deposit) {
        balances[i] = balances[i] + amounts[i];
      } else {
        require(amounts[i] <= balances[i], "LML:Cannot withdraw > available");
        balances[i] = balances[i] - amounts[i];
      }
    }
    uint256 d1 = getD(_pricedInBatch(self, balances), a);
    uint256 totalSupply = self.lpToken.totalSupply();

    if (deposit) {
      return ((d1 - d0) * totalSupply) / d0;
    } else {
      return ((d0 - d1) * totalSupply) / d0;
    }
  }

  /**
   * @custom:section                           ** ADMIN HELPER FUNCTIONS **
   *
   * @custom:visibility -> view-external
   */

  /**
   * @notice return accumulated amount of admin fees of the token with given index
   * @param self Swap struct to read from
   * @param index Index of the pooled token
   * @return admin balance in the token's precision
   */
  function getAdminBalance(Swap storage self, uint256 index) external view returns (uint256) {
    require(index < 2, "LML:Token index out of range");
    if (index == 0) {
      return address(this).balance - (self.balances[index]);
    } else if (index == 1) {
      return self.gETH.balanceOf(address(this), self.pooledTokenId) - (self.balances[index]);
    } else {
      revert();
    }
  }

  /**
   * @custom:section                           ** STATE MODIFYING FUNCTIONS **
   *
   * @custom:visibility -> external
   */

  /**
   * @notice swap two tokens in the pool
   * @param self Swap struct to read from and write to
   * @param tokenIndexFrom the token the user wants to sell
   * @param tokenIndexTo the token the user wants to buy
   * @param dx the amount of tokens the user wants to sell
   * @param minDy the min amount the user would like to receive, or revert.
   * @return amount of token user received on swap
   */
  function swap(
    Swap storage self,
    uint8 tokenIndexFrom,
    uint8 tokenIndexTo,
    uint256 dx,
    uint256 minDy
  ) external returns (uint256) {
    IgETH gETHRef = self.gETH;
    if (tokenIndexFrom == 0) {
      // Means user is selling some ETH to the pool to get some gETH.
      // In which case, we need to send exactly that amount of ETH.
      require(dx == msg.value, "LML:Cannot swap != eth sent");
    }
    if (tokenIndexFrom == 1) {
      // Means user is selling some gETH to the pool to get some ETH.

      require(dx <= gETHRef.balanceOf(msg.sender, self.pooledTokenId), "LML:Cannot swap > you own");

      // Transfer tokens first
      uint256 beforeBalance = gETHRef.balanceOf(address(this), self.pooledTokenId);
      gETHRef.safeTransferFrom(msg.sender, address(this), self.pooledTokenId, dx, "");

      // Use the actual transferred amount for AMM math
      dx = gETHRef.balanceOf(address(this), self.pooledTokenId) - beforeBalance;
    }

    uint256 dy;
    uint256 dyFee;
    // Meaning the real balances *without* any effect of underlying price
    // However, when we call _calculateSwap, it uses pricedIn function before calculation,
    // and pricedOut function after the calculation. So, we don't need to use priceOut here.
    uint256[2] memory balances = self.balances;
    (dy, dyFee) = _calculateSwap(self, tokenIndexFrom, tokenIndexTo, dx, balances);

    require(dy >= minDy, "LML:Swap didnot result in min tokens");
    uint256 dyAdminFee = (dyFee * self.adminFee) / PERCENTAGE_DENOMINATOR;

    // To prevent any Reentrancy, balances are updated before transfering the tokens.
    self.balances[tokenIndexFrom] = balances[tokenIndexFrom] + dx;
    self.balances[tokenIndexTo] = balances[tokenIndexTo] - dy - dyAdminFee;

    if (tokenIndexTo == 0) {
      // Means contract is going to send Idle Ether (ETH)
      (bool sent, ) = payable(msg.sender).call{value: dy}("");
      require(sent, "LML:Failed to send Ether");
    }
    if (tokenIndexTo == 1) {
      // Means contract is going to send staked ETH (gETH)
      gETHRef.safeTransferFrom(address(this), msg.sender, self.pooledTokenId, dy, "");
    }

    emit TokenSwap(msg.sender, dx, dy, tokenIndexFrom, tokenIndexTo);

    return dy;
  }

  /**
   * @notice Add liquidity to the pool
   * @param self Swap struct to read from and write to
   * @param amounts the amounts of each token to add, in their native precision
   * @param minToMint the minimum LP tokens adding this amount of liquidity
   * should mint, otherwise revert. Handy for front-running mitigation
   * allowed addresses. If the pool is not in the guarded launch phase, this parameter will be ignored.
   * @return amount of LP token user received
   */
  function addLiquidity(
    Swap storage self,
    uint256[2] memory amounts,
    uint256 minToMint
  ) external returns (uint256) {
    require(amounts[0] == msg.value, "LML:received less or more ETH than expected");
    IgETH gETHRef = self.gETH;
    // current state
    ManageLiquidityInfo memory v = ManageLiquidityInfo(
      self.lpToken,
      0,
      0,
      0,
      AL._getAPrecise(self),
      0,
      self.balances
    );
    v.totalSupply = v.lpToken.totalSupply();
    if (v.totalSupply != 0) {
      v.d0 = getD(_pricedInBatch(self, v.balances), v.preciseA);
    }

    uint256[2] memory newBalances;
    newBalances[0] = v.balances[0] + msg.value;

    for (uint256 i = 0; i < 2; ++i) {
      require(v.totalSupply != 0 || amounts[i] > 0, "LML:Must supply all tokens in pool");
    }

    {
      // Transfer tokens first
      uint256 beforeBalance = gETHRef.balanceOf(address(this), self.pooledTokenId);
      gETHRef.safeTransferFrom(msg.sender, address(this), self.pooledTokenId, amounts[1], "");

      // Update the amounts[] with actual transfer amount
      amounts[1] = gETHRef.balanceOf(address(this), self.pooledTokenId) - beforeBalance;

      newBalances[1] = v.balances[1] + amounts[1];
    }

    // invariant after change
    v.d1 = getD(_pricedInBatch(self, newBalances), v.preciseA);
    require(v.d1 > v.d0, "LML:D should increase");

    // updated to reflect fees and calculate the user's LP tokens
    v.d2 = v.d1;
    uint256[2] memory fees;

    if (v.totalSupply != 0) {
      uint256 feePerToken = self.swapFee / 2;
      for (uint256 i = 0; i < 2; ++i) {
        uint256 idealBalance = (v.d1 * v.balances[i]) / v.d0;
        fees[i] =
          (feePerToken * (difference(idealBalance, newBalances[i]))) /
          (PERCENTAGE_DENOMINATOR);
        self.balances[i] =
          newBalances[i] -
          ((fees[i] * (self.adminFee)) / (PERCENTAGE_DENOMINATOR));
        newBalances[i] = newBalances[i] - (fees[i]);
      }
      v.d2 = getD(_pricedInBatch(self, newBalances), v.preciseA);
    } else {
      // the initial depositor doesn't pay fees
      self.balances = newBalances;
    }

    uint256 toMint;
    if (v.totalSupply == 0) {
      toMint = v.d1;
    } else {
      toMint = ((v.d2 - v.d0) * v.totalSupply) / v.d0;
    }

    require(toMint >= minToMint, "LML:Could not mint min requested");
    // mint the user's LP tokens
    v.lpToken.mint(msg.sender, toMint);

    emit AddLiquidity(msg.sender, amounts, fees, v.d1, v.totalSupply + toMint);
    return toMint;
  }

  /**
   * @notice Burn LP tokens to remove liquidity from the pool.
   * @dev Liquidity can always be removed, even when the pool is paused.
   * @param self Swap struct to read from and write to
   * @param amount the amount of LP tokens to burn
   * @param minAmounts the minimum amounts of each token in the pool
   * acceptable for this burn. Useful as a front-running mitigation
   * @return amounts of tokens the user received
   */
  function removeLiquidity(
    Swap storage self,
    uint256 amount,
    uint256[2] calldata minAmounts
  ) external returns (uint256[2] memory) {
    ILPToken lpToken = self.lpToken;
    IgETH gETHRef = self.gETH;
    require(amount <= lpToken.balanceOf(msg.sender), "LML:>LP.balanceOf");

    uint256[2] memory balances = self.balances;
    uint256 totalSupply = lpToken.totalSupply();

    uint256[2] memory amounts = _pricedOutBatch(
      self,
      _calculateRemoveLiquidity(_pricedInBatch(self, balances), amount, totalSupply)
    );

    for (uint256 i = 0; i < amounts.length; ++i) {
      require(amounts[i] >= minAmounts[i], "LML:amounts[i] < minAmounts[i]");
      self.balances[i] = balances[i] - amounts[i];
    }

    // To prevent any Reentrancy, LP tokens are burned before transfering the tokens.
    lpToken.burnFrom(msg.sender, amount);

    (bool sent, ) = payable(msg.sender).call{value: amounts[0]}("");
    require(sent, "LML:Failed to send Ether");

    gETHRef.safeTransferFrom(address(this), msg.sender, self.pooledTokenId, amounts[1], "");

    emit RemoveLiquidity(msg.sender, amounts, totalSupply - amount);
    return amounts;
  }

  /**
   * @notice Remove liquidity from the pool all in one token.
   * @param self Swap struct to read from and write to
   * @param tokenAmount the amount of the lp tokens to burn
   * @param tokenIndex the index of the token you want to receive
   * @param minAmount the minimum amount to withdraw, otherwise revert
   * @return amount chosen token that user received
   */
  function removeLiquidityOneToken(
    Swap storage self,
    uint256 tokenAmount,
    uint8 tokenIndex,
    uint256 minAmount
  ) external returns (uint256) {
    ILPToken lpToken = self.lpToken;
    IgETH gETHRef = self.gETH;

    require(tokenAmount <= lpToken.balanceOf(msg.sender), "LML:>LP.balanceOf");
    require(tokenIndex < 2, "LML:Token not found");

    uint256 totalSupply = lpToken.totalSupply();

    (uint256 dy, uint256 dyFee) = _calculateWithdrawOneToken(
      self,
      tokenAmount,
      tokenIndex,
      totalSupply
    );

    require(dy >= minAmount, "LML:dy < minAmount");

    // To prevent any Reentrancy, LP tokens are burned before transfering the tokens.
    self.balances[tokenIndex] =
      self.balances[tokenIndex] -
      (dy + ((dyFee * (self.adminFee)) / (PERCENTAGE_DENOMINATOR)));
    lpToken.burnFrom(msg.sender, tokenAmount);

    if (tokenIndex == 0) {
      (bool sent, ) = payable(msg.sender).call{value: dy}("");
      require(sent, "LML:Failed to send Ether");
    }
    if (tokenIndex == 1) {
      gETHRef.safeTransferFrom(address(this), msg.sender, self.pooledTokenId, dy, "");
    }

    emit RemoveLiquidityOne(msg.sender, tokenAmount, totalSupply, tokenIndex, dy);

    return dy;
  }

  /**
   * @notice Remove liquidity from the pool, weighted differently than the
   * pool's current balances.
   *
   * @param self Swap struct to read from and write to
   * @param amounts how much of each token to withdraw
   * @param maxBurnAmount the max LP token provider is willing to pay to
   * remove liquidity. Useful as a front-running mitigation.
   * @return actual amount of LP tokens burned in the withdrawal
   */
  function removeLiquidityImbalance(
    Swap storage self,
    uint256[2] memory amounts,
    uint256 maxBurnAmount
  ) public returns (uint256) {
    IgETH gETHRef = self.gETH;

    ManageLiquidityInfo memory v = ManageLiquidityInfo(
      self.lpToken,
      0,
      0,
      0,
      AL._getAPrecise(self),
      0,
      self.balances
    );
    v.totalSupply = v.lpToken.totalSupply();

    require(
      maxBurnAmount <= v.lpToken.balanceOf(msg.sender) && maxBurnAmount != 0,
      "LML:>LP.balanceOf"
    );

    uint256 feePerToken = self.swapFee / 2;
    uint256[2] memory fees;

    {
      uint256[2] memory balances1;

      v.d0 = getD(_pricedInBatch(self, v.balances), v.preciseA);
      for (uint256 i = 0; i < 2; ++i) {
        require(amounts[i] <= v.balances[i], "LML:Cannot withdraw > available");
        balances1[i] = v.balances[i] - amounts[i];
      }
      v.d1 = getD(_pricedInBatch(self, balances1), v.preciseA);

      for (uint256 i = 0; i < 2; ++i) {
        uint256 idealBalance = (v.d1 * v.balances[i]) / v.d0;
        uint256 _diff = difference(idealBalance, balances1[i]);
        fees[i] = (feePerToken * _diff) / PERCENTAGE_DENOMINATOR;
        uint256 adminFee = self.adminFee;
        {
          self.balances[i] = balances1[i] - ((fees[i] * adminFee) / PERCENTAGE_DENOMINATOR);
        }
        balances1[i] = balances1[i] - fees[i];
      }

      v.d2 = getD(_pricedInBatch(self, balances1), v.preciseA);
    }

    uint256 tokenAmount = ((v.d0 - v.d2) * (v.totalSupply)) / v.d0;
    require(tokenAmount != 0, "LML:Burnt amount cannot be zero");
    tokenAmount = tokenAmount + 1;

    require(tokenAmount <= maxBurnAmount, "LML:tokenAmount > maxBurnAmount");

    // To prevent any Reentrancy, LP tokens are burned before transfering the tokens.
    v.lpToken.burnFrom(msg.sender, tokenAmount);

    (bool sent, ) = payable(msg.sender).call{value: amounts[0]}("");
    require(sent, "LML:Failed to send Ether");

    gETHRef.safeTransferFrom(address(this), msg.sender, self.pooledTokenId, amounts[1], "");

    emit RemoveLiquidityImbalance(msg.sender, amounts, fees, v.d1, v.totalSupply - tokenAmount);

    return tokenAmount;
  }

  /**
   * @custom:subsection                           ** ADMIN FUNCTIONS **
   */

  /**
   * @notice withdraw all admin fees to a given address
   * @param self Swap struct to withdraw fees from
   * @param receiver Address to send the fees to
   */
  function withdrawAdminFees(Swap storage self, address receiver) external {
    IgETH gETHRef = self.gETH;
    uint256 tokenBalance = gETHRef.balanceOf(address(this), self.pooledTokenId) - self.balances[1];
    if (tokenBalance != 0) {
      gETHRef.safeTransferFrom(address(this), receiver, self.pooledTokenId, tokenBalance, "");
    }

    uint256 etherBalance = address(this).balance - self.balances[0];
    if (etherBalance != 0) {
      (bool sent, ) = payable(receiver).call{value: etherBalance}("");
      require(sent, "LML:Failed to send Ether");
    }
  }

  /**
   * @notice Sets the admin fee
   * @dev adminFee cannot be higher than 100% of the swap fee
   * @param self Swap struct to update
   * @param newAdminFee new admin fee to be applied on future transactions
   */
  function setAdminFee(Swap storage self, uint256 newAdminFee) external {
    require(newAdminFee <= MAX_ADMIN_FEE, "LML:Fee is too high");
    self.adminFee = newAdminFee;

    emit NewAdminFee(newAdminFee);
  }

  /**
   * @notice update the swap fee
   * @dev fee cannot be higher than 1% of each swap
   * @param self Swap struct to update
   * @param newSwapFee new swap fee to be applied on future transactions
   */
  function setSwapFee(Swap storage self, uint256 newSwapFee) external {
    require(newSwapFee <= MAX_SWAP_FEE, "LML:Fee is too high");
    self.swapFee = newSwapFee;

    emit NewSwapFee(newSwapFee);
  }
}

// SPDX-License-Identifier: MIT
pragma solidity =0.8.7;

// interfaces
import {IgETH} from "../../interfaces/IgETH.sol";
import {ILiquidityModule} from "../../interfaces/modules/ILiquidityModule.sol";
// libraries
import {LiquidityModuleLib as LML} from "./libs/LiquidityModuleLib.sol";
import {AmplificationLib as AL} from "./libs/AmplificationLib.sol";
// contracts
import {ILPToken} from "../../interfaces/helpers/ILPToken.sol";
// external
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import {ERC1155HolderUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";
import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";

/**
 * @title LM: Liquidity Module
 *
 * @notice A StableSwap implementation for ERC1155 staking derivatives, gETH.
 * * Users become an LP (Liquidity Provider) by depositing their tokens
 * * in desired ratios for an exchange of the pool token that represents their share of the pool.
 * * Users can burn pool tokens and withdraw their share of token(s).
 * * Each time a swap between the pooled tokens happens, a set fee incurs which effectively gets
 * * distributed to the LPs.
 * * In case of emergencies, admin can pause additional deposits, swaps, or single-asset withdraws - which
 * * stops the ratio of the tokens in the pool from changing.
 * * Users can always withdraw their tokens via multi-asset withdraws.
 *
 * @dev There are no additional functionalities implemented seperately from the library.
 * * However, this module inherits and implements nonReentrant & whenNotPaused modifiers.
 * * LM has pausability and expects inheriting contract to provide the access control mechanism.
 *
 * @dev review: this module delegates its functionality to LML (LiquidityModuleLib).
 *
 * @dev 7 functions need to be overriden with access control when inherited:
 * * pause, unpause, setSwapFee, setAdminFee, withdrawAdminFees, rampA, stopRampA.
 *
 * @dev __LiquidityModule_init (or _unchained) call is NECESSARY when inherited.
 *
 * note This module utilizes modifiers but does not implement necessary admin checks; or pausability overrides.
 * * If a package inherits LM, should implement it's own logic around those.
 *
 * @author Ice Bear & Crash Bandicoot
 */
abstract contract LiquidityModule is
  ILiquidityModule,
  ERC1155HolderUpgradeable,
  PausableUpgradeable,
  ReentrancyGuardUpgradeable
{
  using LML for LML.Swap;
  using AL for LML.Swap;

  /**
   * @custom:section                           ** VARIABLES **
   *
   * @dev Do not add any other variables here. Modules do NOT have a gap.
   * Library's main struct has a gap, providing up to 16 storage slots for this module.
   */
  LML.Swap internal LIQUIDITY;

  /**
   * @custom:section                           ** EVENTS **
   */
  event TokenSwap(
    address indexed buyer,
    uint256 tokensSold,
    uint256 tokensBought,
    uint128 soldId,
    uint128 boughtId
  );
  event AddLiquidity(
    address indexed provider,
    uint256[2] tokenAmounts,
    uint256[2] fees,
    uint256 invariant,
    uint256 lpTokenSupply
  );
  event RemoveLiquidity(address indexed provider, uint256[2] tokenAmounts, uint256 lpTokenSupply);
  event RemoveLiquidityOne(
    address indexed provider,
    uint256 lpTokenAmount,
    uint256 lpTokenSupply,
    uint256 boughtId,
    uint256 tokensBought
  );
  event RemoveLiquidityImbalance(
    address indexed provider,
    uint256[2] tokenAmounts,
    uint256[2] fees,
    uint256 invariant,
    uint256 lpTokenSupply
  );
  event NewAdminFee(uint256 newAdminFee);
  event NewSwapFee(uint256 newSwapFee);
  event NewWithdrawFee(uint256 newWithdrawFee);
  event RampA(uint256 oldA, uint256 newA, uint256 initialTime, uint256 futureTime);
  event StopRampA(uint256 currentA, uint256 time);

  /**
   * @custom:section                           ** MODIFIERS **
   */

  /**
   * @notice Modifier to check deadline against current timestamp
   * @param deadline latest timestamp to accept this transaction
   */
  modifier deadlineCheck(uint256 deadline) {
    require(block.timestamp <= deadline, "LM:Deadline not met");
    _;
  }

  /**
   * @custom:section                           ** ABSTRACT FUNCTIONS **
   *
   * @dev these functions MUST be overriden for admin functionality.
   */

  /**
   * @dev -> external
   */
  function pause() external virtual override;

  function unpause() external virtual override;

  /**
   * @notice Update the swap fee to be applied on swaps
   * @param newSwapFee new swap fee to be applied on future transactions
   */
  function setSwapFee(uint256 newSwapFee) external virtual override;

  /**
   * @notice Update the admin fee. Admin fee takes portion of the swap fee.
   * @param newAdminFee new admin fee to be applied on future transactions
   */
  function setAdminFee(uint256 newAdminFee) external virtual override;

  /**
   * @notice Withdraw all admin fees to the contract owner
   */
  function withdrawAdminFees(address receiver) external virtual override;

  /**
   * @notice Start ramping up or down A parameter towards given futureA and futureTime
   * Checks if the change is too rapid, and commits the new A value only when it falls under
   * the limit range.
   * @param futureA the new A to ramp towards
   * @param futureTime timestamp when the new A should be reached
   */
  function rampA(uint256 futureA, uint256 futureTime) external virtual override;

  /**
   * @notice Stop ramping A immediately. Reverts if ramp A is already stopped.
   */
  function stopRampA() external virtual override;

  /**
   * @custom:section                           ** INITIALIZING **
   */

  function __LiquidityModule_init(
    address _gETH_position,
    address _lpToken_referance,
    uint256 _pooledTokenId,
    uint256 _A,
    uint256 _swapFee,
    string memory _poolName
  ) internal onlyInitializing {
    __ReentrancyGuard_init();
    __Pausable_init();
    __ERC1155Holder_init();
    __LiquidityModule_init_unchained(
      _gETH_position,
      _lpToken_referance,
      _pooledTokenId,
      _A,
      _swapFee,
      _poolName
    );
  }

  function __LiquidityModule_init_unchained(
    address _gETH_position,
    address _lpToken_referance,
    uint256 _pooledTokenId,
    uint256 _A,
    uint256 _swapFee,
    string memory _poolName
  ) internal onlyInitializing {
    require(_gETH_position != address(0), "LM:_gETH_position can not be zero");
    require(_lpToken_referance != address(0), "LM:_lpToken_referance can not be zero");
    require(_pooledTokenId != 0, "LM:_pooledTokenId can not be zero");
    require(_A != 0, "LM:_A can not be zero");
    require(_A < AL.MAX_A, "LM:_A exceeds maximum");
    require(_swapFee < LML.MAX_SWAP_FEE, "LM:_swapFee exceeds maximum");

    // Clone and initialize a LPToken contract
    ILPToken _lpToken = ILPToken(Clones.clone(_lpToken_referance));
    string memory name_prefix = "Geode LP Token: ";
    string memory symbol_suffix = "-LP";
    _lpToken.initialize(
      string(abi.encodePacked(name_prefix, _poolName)),
      string(abi.encodePacked(_poolName, symbol_suffix))
    );

    LIQUIDITY.gETH = IgETH(_gETH_position);
    LIQUIDITY.lpToken = _lpToken;
    LIQUIDITY.pooledTokenId = _pooledTokenId;
    LIQUIDITY.initialA = _A * AL.A_PRECISION;
    LIQUIDITY.futureA = _A * AL.A_PRECISION;
    LIQUIDITY.swapFee = _swapFee;

    // Do not trust middlewares. Protect LPs, gETH tokens from
    // issues that can be surfaced with future middlewares.
    LIQUIDITY.gETH.avoidMiddlewares(_pooledTokenId, true);
  }

  /**
   * @custom:section                           ** GETTER FUNCTIONS **
   *
   * @custom:visibility -> view-external
   */

  function LiquidityParams()
    external
    view
    virtual
    override
    returns (
      address gETH,
      address lpToken,
      uint256 pooledTokenId,
      uint256 initialA,
      uint256 futureA,
      uint256 initialATime,
      uint256 futureATime,
      uint256 swapFee,
      uint256 adminFee
    )
  {
    gETH = address(LIQUIDITY.gETH);
    lpToken = address(LIQUIDITY.lpToken);
    pooledTokenId = LIQUIDITY.pooledTokenId;
    initialA = LIQUIDITY.initialA;
    futureA = LIQUIDITY.futureA;
    initialATime = LIQUIDITY.initialATime;
    futureATime = LIQUIDITY.futureATime;
    swapFee = LIQUIDITY.swapFee;
    adminFee = LIQUIDITY.adminFee;
  }

  /**
   * @notice Return A, the amplification coefficient * n * (n - 1)
   * @dev See the StableSwap paper for details
   * @return A parameter
   */
  function getA() external view virtual override returns (uint256) {
    return LIQUIDITY.getA();
  }

  /**
   * @notice Return A in its raw precision form
   * @dev See the StableSwap paper for details
   * @return A parameter in its raw precision form
   */
  function getAPrecise() external view virtual override returns (uint256) {
    return LIQUIDITY.getAPrecise();
  }

  /**
   * @notice Debt, The amount of buyback for stable pricing (1=1).
   * @return debt the half of the D StableSwap invariant when debt is needed to be payed.
   * @dev result might change when price is in.
   */
  function getDebt() external view virtual override returns (uint256) {
    return LIQUIDITY.getDebt();
  }

  /**
   * @notice Return current balance of the pooled token at given index
   * @param index the index of the token
   * @return current balance of the pooled token at given index with token's native precision
   */
  function getBalance(uint8 index) external view virtual override returns (uint256) {
    return LIQUIDITY.balances[index];
  }

  /**
   * @notice Get the virtual override price, to help calculate profit
   * @return the virtual override price
   */
  function getVirtualPrice() external view virtual override returns (uint256) {
    return LIQUIDITY.getVirtualPrice();
  }

  /**
   * @notice Reads the accumulated amount of admin fees of the token with given index
   * @param index Index of the pooled token
   * @return admin's token balance in the token's precision
   */
  function getAdminBalance(uint256 index) external view virtual override returns (uint256) {
    return LIQUIDITY.getAdminBalance(index);
  }

  /**
   * @custom:section                           ** HELPER FUNCTIONS **
   *
   * @custom:visibility -> view-external
   */

  /**
   * @notice Calculate amount of tokens you receive on swap
   * @param tokenIndexFrom the token the user wants to sell
   * @param tokenIndexTo the token the user wants to buy
   * @param dx the amount of tokens the user wants to sell. If the token charges
   * a fee on transfers, use the amount that gets transferred after the fee.
   * @return amount of tokens the user will receive
   */
  function calculateSwap(
    uint8 tokenIndexFrom,
    uint8 tokenIndexTo,
    uint256 dx
  ) external view virtual override returns (uint256) {
    return LIQUIDITY.calculateSwap(tokenIndexFrom, tokenIndexTo, dx);
  }

  /**
   * @notice A simple method to calculate prices from deposits or
   * withdrawals, excluding fees but including slippage. This is
   * helpful as an input into the various "min" parameters on calls
   * to fight front-running
   *
   * @dev This shouldn't be used outside frontends for user estimates.
   *
   * @param amounts an array of token amounts to deposit or withdrawal,
   * corresponding to pooledTokens. The amount should be in each
   * pooled token's native precision. If a token charges a fee on transfers,
   * use the amount that gets transferred after the fee.
   * @param deposit whether this is a deposit or a withdrawal
   * @return token amount the user will receive
   */
  function calculateTokenAmount(
    uint256[2] calldata amounts,
    bool deposit
  ) external view virtual override returns (uint256) {
    return LIQUIDITY.calculateTokenAmount(amounts, deposit);
  }

  /**
   * @notice A simple method to calculate amount of each underlying
   * tokens that is returned upon burning given amount of LP tokens
   * @param amount the amount of LP tokens that would be burned on withdrawal
   * @return array of token balances that the user will receive
   */
  function calculateRemoveLiquidity(
    uint256 amount
  ) external view virtual override returns (uint256[2] memory) {
    return LIQUIDITY.calculateRemoveLiquidity(amount);
  }

  /**
   * @notice Calculate the amount of underlying token available to withdraw
   * when withdrawing via only single token
   * @param tokenAmount the amount of LP token to burn
   * @param tokenIndex index of which token will be withdrawn
   * @return availableTokenAmount calculated amount of underlying token
   * available to withdraw
   */
  function calculateRemoveLiquidityOneToken(
    uint256 tokenAmount,
    uint8 tokenIndex
  ) external view virtual override returns (uint256 availableTokenAmount) {
    return LIQUIDITY.calculateWithdrawOneToken(tokenAmount, tokenIndex);
  }

  /**
   * @custom:section                           ** STATE MODIFYING FUNCTIONS **
   *
   * @custom:visibility -> external
   */

  /**
   * @notice Swap two tokens using this pool
   * @param tokenIndexFrom the token the user wants to swap from
   * @param tokenIndexTo the token the user wants to swap to
   * @param dx the amount of tokens the user wants to swap from
   * @param minDy the min amount the user would like to receive, or revert.
   * @param deadline latest timestamp to accept this transaction
   */
  function swap(
    uint8 tokenIndexFrom,
    uint8 tokenIndexTo,
    uint256 dx,
    uint256 minDy,
    uint256 deadline
  )
    public
    payable
    virtual
    override
    nonReentrant
    whenNotPaused
    deadlineCheck(deadline)
    returns (uint256)
  {
    return LIQUIDITY.swap(tokenIndexFrom, tokenIndexTo, dx, minDy);
  }

  /**
   * @notice Add liquidity to the pool with the given amounts of tokens
   * @param amounts the amounts of each token to add, in their native precision
   * @param minToMint the minimum LP tokens adding this amount of liquidity
   * should mint, otherwise revert. Handy for front-running mitigation
   * @param deadline latest timestamp to accept this transaction
   * @return amount of LP token user minted and received
   */
  function addLiquidity(
    uint256[2] calldata amounts,
    uint256 minToMint,
    uint256 deadline
  )
    public
    payable
    virtual
    override
    nonReentrant
    whenNotPaused
    deadlineCheck(deadline)
    returns (uint256)
  {
    return LIQUIDITY.addLiquidity(amounts, minToMint);
  }

  /**
   * @notice Burn LP tokens to remove liquidity from the pool.
   * @dev Liquidity can always be removed, even when the pool is paused.
   * @param amount the amount of LP tokens to burn
   * @param minAmounts the minimum amounts of each token in the pool
   *        acceptable for this burn. Useful as a front-running mitigation
   * @param deadline latest timestamp to accept this transaction
   * @return amounts of tokens user received
   */
  function removeLiquidity(
    uint256 amount,
    uint256[2] calldata minAmounts,
    uint256 deadline
  ) public virtual override nonReentrant deadlineCheck(deadline) returns (uint256[2] memory) {
    return LIQUIDITY.removeLiquidity(amount, minAmounts);
  }

  /**
   * @notice Remove liquidity from the pool all in one token.
   * @param tokenAmount the amount of the token you want to receive
   * @param tokenIndex the index of the token you want to receive
   * @param minAmount the minimum amount to withdraw, otherwise revert
   * @param deadline latest timestamp to accept this transaction
   * @return amount of chosen token user received
   */
  function removeLiquidityOneToken(
    uint256 tokenAmount,
    uint8 tokenIndex,
    uint256 minAmount,
    uint256 deadline
  ) public virtual override nonReentrant whenNotPaused deadlineCheck(deadline) returns (uint256) {
    return LIQUIDITY.removeLiquidityOneToken(tokenAmount, tokenIndex, minAmount);
  }

  /**
   * @notice Remove liquidity from the pool, weighted differently than the
   * pool's current balances.
   * @param amounts how much of each token to withdraw
   * @param maxBurnAmount the max LP token provider is willing to pay to
   * remove liquidity. Useful as a front-running mitigation.
   * @param deadline latest timestamp to accept this transaction
   * @return amount of LP tokens burned
   */
  function removeLiquidityImbalance(
    uint256[2] calldata amounts,
    uint256 maxBurnAmount,
    uint256 deadline
  ) public virtual override nonReentrant whenNotPaused deadlineCheck(deadline) returns (uint256) {
    return LIQUIDITY.removeLiquidityImbalance(amounts, maxBurnAmount);
  }
}

