// SPDX-License-Identifier: MIT
pragma solidity =0.8.7;
import "./SwapUtils.sol";

/**
 * @title AmplificationUtils library
 * @notice A library to calculate and ramp the A parameter of a given `SwapUtils.Swap` struct.
 * This library assumes the struct is fully validated.
 */
library AmplificationUtils {
    event RampA(
        uint256 oldA,
        uint256 newA,
        uint256 initialTime,
        uint256 futureTime
    );
    event StopRampA(uint256 currentA, uint256 time);

    // Constant values used in ramping A calculations
    uint256 public constant A_PRECISION = 100;
    uint256 public constant MAX_A = 10**6;
    uint256 private constant MAX_A_CHANGE = 2;
    uint256 private constant MIN_RAMP_TIME = 14 days;

    /**
     * @notice Return A, the amplification coefficient * n * (n - 1)
     * @dev See the StableSwap paper for details
     * @param self Swap struct to read from
     * @return A parameter
     */
    function getA(SwapUtils.Swap storage self) internal view returns (uint256) {
        return _getAPrecise(self) / (A_PRECISION);
    }

    /**
     * @notice Return A in its raw precision
     * @dev See the StableSwap paper for details
     * @param self Swap struct to read from
     * @return A parameter in its raw precision form
     */
    function getAPrecise(SwapUtils.Swap storage self)
        internal
        view
        returns (uint256)
    {
        return _getAPrecise(self);
    }

    /**
     * @notice Return A in its raw precision
     * @dev See the StableSwap paper for details
     * @param self Swap struct to read from
     * @return A parameter in its raw precision form
     */
    function _getAPrecise(SwapUtils.Swap storage self)
        internal
        view
        returns (uint256)
    {
        uint256 t1 = self.futureATime; // time when ramp is finished
        uint256 a1 = self.futureA; // final A value when ramp is finished

        if (block.timestamp < t1) {
            uint256 t0 = self.initialATime; // time when ramp is started
            uint256 a0 = self.initialA; // initial A value when ramp is started
            if (a1 > a0) {
                // a0 + (a1 - a0) * (block.timestamp - t0) / (t1 - t0)
                return a0 + ((a1 - a0) * (block.timestamp - t0)) / (t1 - t0);
            } else {
                // a0 - (a0 - a1) * (block.timestamp - t0) / (t1 - t0)
                return a0 - ((a0 - a1) * (block.timestamp - t0)) / (t1 - t0);
            }
        } else {
            return a1;
        }
    }

    /**
     * @notice Start ramping up or down A parameter towards given futureA_ and futureTime_
     * Checks if the change is too rapid, and commits the new A value only when it falls under
     * the limit range.
     * @param self Swap struct to update
     * @param futureA_ the new A to ramp towards
     * @param futureTime_ timestamp when the new A should be reached
     */
    function rampA(
        SwapUtils.Swap storage self,
        uint256 futureA_,
        uint256 futureTime_
    ) internal {
        require(
            block.timestamp >= self.initialATime + 1 days,
            "Wait 1 day before starting ramp"
        );
        require(
            futureTime_ >= block.timestamp + MIN_RAMP_TIME,
            "Insufficient ramp time"
        );
        require(
            futureA_ > 0 && futureA_ < MAX_A,
            "futureA_ must be > 0 and < MAX_A"
        );

        uint256 initialAPrecise = _getAPrecise(self);
        uint256 futureAPrecise = futureA_ * A_PRECISION;

        if (futureAPrecise < initialAPrecise) {
            require(
                futureAPrecise * MAX_A_CHANGE >= initialAPrecise,
                "futureA_ is too small"
            );
        } else {
            require(
                futureAPrecise <= initialAPrecise * MAX_A_CHANGE,
                "futureA_ is too large"
            );
        }

        self.initialA = initialAPrecise;
        self.futureA = futureAPrecise;
        self.initialATime = block.timestamp;
        self.futureATime = futureTime_;

        emit RampA(
            initialAPrecise,
            futureAPrecise,
            block.timestamp,
            futureTime_
        );
    }

    /**
     * @notice Stops ramping A immediately. Once this function is called, rampA()
     * cannot be called for another 24 hours
     * @param self Swap struct to update
     */
    function stopRampA(SwapUtils.Swap storage self) internal {
        require(self.futureATime > block.timestamp, "Ramp is already stopped");

        uint256 currentA = _getAPrecise(self);
        self.initialA = currentA;
        self.futureA = currentA;
        self.initialATime = block.timestamp;
        self.futureATime = block.timestamp;

        emit StopRampA(currentA, block.timestamp);
    }
}

contracts/Portal/liquidityPool/utils/Mat.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.7;

/**
 * @title MathUtils library
 * @notice  Contains functions for calculating differences between two uint256.
 */
library MathUtils {
    /**
     * @notice Compares a and b and returns true if the difference between a and b
     *         is less than 1 or equal to each other.
     * @param a uint256 to compare with
     * @param b uint256 to compare with
     * @return True if the difference between a and b is less than 1 or equal,
     *         otherwise return false
     */
    function within1(uint256 a, uint256 b) internal pure returns (bool) {
        return (difference(a, b) <= 1);
    }

    /**
     * @notice Calculates absolute difference between a and b
     * @param a uint256 to compare with
     * @param b uint256 to compare with
     * @return Difference between a and b
     */
    function difference(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a > b) {
            return a - b;
        }
        return b - a;
    }
}

contracts/Portal/liquidityPool/utils/Swa.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.7;

import "./MathUtils.sol";
import "./AmplificationUtils.sol";
import {PERCENTAGE_DENOMINATOR} from "../../utils/globals.sol";
import "../../../interfaces/ILPToken.sol";
import "../../../interfaces/IgETH.sol";

/**
 * @title SwapUtils library
 *
 * @dev Contracts relying on this library must initialize SwapUtils.Swap struct then use this library
 * for SwapUtils.Swap struct. Note that this library contains both functions called by users and admins.
 * Admin functions should be protected within contracts using this library.
 *
 * @notice A library to be used within Swap.sol. Contains functions responsible for custody and AMM functionalities with some changes.
 * The main functionality of Withdrawal Pools is allowing the depositors to have instant withdrawals
 * relying on the Oracle Price, with the help of Liquidity Providers.
 * It is important to change the focus point (1-1) of the pricing algorithm with PriceIn and PriceOut functions.
 * Because the underlying price of the staked assets are expected to raise in time.
 * One can see this similar to accomplishing a "rebasing" logic, with the help of a trusted price source.
 *
 * @dev Whenever "Effective Balance" is mentioned it refers to the balance projected with the underlying price.
 */
library SwapUtils {
  using MathUtils for uint256;

  /*** EVENTS ***/

  event TokenSwap(
    address indexed buyer,
    uint256 tokensSold,
    uint256 tokensBought,
    uint128 soldId,
    uint128 boughtId
  );
  event AddLiquidity(
    address indexed provider,
    uint256[2] tokenAmounts,
    uint256[2] fees,
    uint256 invariant,
    uint256 lpTokenSupply
  );
  event RemoveLiquidity(
    address indexed provider,
    uint256[2] tokenAmounts,
    uint256 lpTokenSupply
  );
  event RemoveLiquidityOne(
    address indexed provider,
    uint256 lpTokenAmount,
    uint256 lpTokenSupply,
    uint256 boughtId,
    uint256 tokensBought
  );
  event RemoveLiquidityImbalance(
    address indexed provider,
    uint256[2] tokenAmounts,
    uint256[2] fees,
    uint256 invariant,
    uint256 lpTokenSupply
  );
  event NewAdminFee(uint256 newAdminFee);
  event NewSwapFee(uint256 newSwapFee);

  /**
   * @param gETH ERC1155 contract reference
   * @param lpToken address of the LP Token
   * @param pooledTokenId gETH ID of the pooled derivative
   * @param initialA the amplification coefficient * n * (n - 1)
   * @param futureA the amplification coef that will be effective after futureATime
   * @param initialATime variable around the ramp management of A
   * @param futureATime variable around the ramp management of A
   * @param swapFee fee as a percentage/PERCENTAGE_DENOMINATOR, will be deducted from resulting tokens of a swap
   * @param adminFee fee as a percentage/PERCENTAGE_DENOMINATOR, will be deducted from swapFee
   * @param balances the pool balance as [ETH, gETH]; the contract's actual token balance might differ
   * @param __gap keep the contract size at 16
   */
  struct Swap {
    IgETH gETH;
    ILPToken lpToken;
    uint256 pooledTokenId;
    uint256 initialA;
    uint256 futureA;
    uint256 initialATime;
    uint256 futureATime;
    uint256 swapFee;
    uint256 adminFee;
    uint256[2] balances;
    uint256[8] __gap;
  }

  // Struct storing variables used in calculations in the
  // calculateWithdrawOneTokenDY function to avoid stack too deep errors
  struct CalculateWithdrawOneTokenDYInfo {
    uint256 d0;
    uint256 d1;
    uint256 newY;
    uint256 feePerToken;
    uint256 preciseA;
  }

  // Struct storing variables used in calculations in the
  // {add,remove} Liquidity functions to avoid stack too deep errors
  struct ManageLiquidityInfo {
    ILPToken lpToken;
    uint256 d0;
    uint256 d1;
    uint256 d2;
    uint256 preciseA;
    uint256 totalSupply;
    uint256[2] balances;
  }

  // Max swap fee is 1% or 100bps of each swap
  uint256 public constant MAX_SWAP_FEE = 10 ** 8;

  // Max adminFee is 100% of the swapFee
  // adminFee does not add additional fee on top of swapFee
  // instead it takes a certain percentage of the swapFee.
  // Therefore it has no impact on users but only on the earnings of LPs
  uint256 public constant MAX_ADMIN_FEE = 10 ** 10;

  // Constant value used as max loop limit
  uint256 private constant MAX_LOOP_LIMIT = 256;

  /*** VIEW & PURE FUNCTIONS ***/

  function _getAPrecise(Swap storage self) internal view returns (uint256) {
    return AmplificationUtils._getAPrecise(self);
  }

  /**
   * @notice This function MULTIPLIES the Staked Ether token (gETH) balance with underlying relative price (pricePerShare),
   * to keep pricing around 1-OraclePrice instead of 1-1 like stableSwap pool.
   * @dev this function assumes prices are sent with the indexes that [ETH, gETH]
   * @param balance balance that will be taken into calculation
   * @param i if i is 0 it means we are dealing with ETH, if i is 1 it is gETH
   */
  function _pricedIn(
    Swap storage self,
    uint256 balance,
    uint256 i
  ) internal view returns (uint256) {
    return
      i == 1
        ? (balance * self.gETH.pricePerShare(self.pooledTokenId)) /
          self.gETH.denominator()
        : balance;
  }

  /**
   * @notice This function DIVIDES the Staked Ether token (gETH) balance with underlying relative price (pricePerShare),
   * to keep pricing around 1-OraclePrice instead of 1-1 like stableSwap pool.
   * @dev this function assumes prices are sent with the indexes that [ETH, gETH]
   * @param balance balance that will be taken into calculation
   * @param i if i is 0 it means we are dealing with ETH, if i is 1 it is gETH
   */
  function _pricedOut(
    Swap storage self,
    uint256 balance,
    uint256 i
  ) internal view returns (uint256) {
    return
      i == 1
        ? (balance * self.gETH.denominator()) /
          self.gETH.pricePerShare(self.pooledTokenId)
        : balance;
  }

  /**
   * @notice This function MULTIPLIES the Staked Ether token (gETH) balance with underlying relative price (pricePerShare),
   * to keep pricing around 1-OraclePrice instead of 1-1 like stableSwap pool.
   * @dev this function assumes prices are sent with the indexes that [ETH, gETH]
   * @param balances ARRAY of balances that will be taken into calculation
   */
  function _pricedInBatch(
    Swap storage self,
    uint256[2] memory balances
  ) internal view returns (uint256[2] memory _p) {
    _p[0] = balances[0];
    _p[1] =
      (balances[1] * self.gETH.pricePerShare(self.pooledTokenId)) /
      self.gETH.denominator();
    return _p;
  }

  /**
   * @notice This function DIVIDES the Staked Ether token (gETH) balance with underlying relative price (pricePerShare),
   * to keep pricing around 1-OraclePrice instead of 1-1 like stableSwap pool.
   * @dev this function assumes prices are sent with the indexes that [ETH, gETH]
   * @param balances ARRAY of balances that will be taken into calculation
   */
  function _pricedOutBatch(
    Swap storage self,
    uint256[2] memory balances
  ) internal view returns (uint256[2] memory _p) {
    _p[0] = balances[0];
    _p[1] =
      (balances[1] * self.gETH.denominator()) /
      self.gETH.pricePerShare(self.pooledTokenId);
    return _p;
  }

  /**
   * @notice Calculate the dy, the amount of selected token that user receives and
   * the fee of withdrawing in one token
   * @param tokenAmount the amount to withdraw in the pool's precision
   * @param tokenIndex which token will be withdrawn
   * @param self Swap struct to read from
   * @return the amount of token user will receive
   */
  function calculateWithdrawOneToken(
    Swap storage self,
    uint256 tokenAmount,
    uint8 tokenIndex
  ) external view returns (uint256) {
    (uint256 availableTokenAmount, ) = _calculateWithdrawOneToken(
      self,
      tokenAmount,
      tokenIndex,
      self.lpToken.totalSupply()
    );
    return availableTokenAmount;
  }

  function _calculateWithdrawOneToken(
    Swap storage self,
    uint256 tokenAmount,
    uint8 tokenIndex,
    uint256 totalSupply
  ) internal view returns (uint256, uint256) {
    uint256 dy;
    uint256 newY;
    uint256 currentY;

    (dy, newY, currentY) = calculateWithdrawOneTokenDY(
      self,
      tokenIndex,
      tokenAmount,
      totalSupply
    );

    uint256 dySwapFee = currentY - newY - dy;

    return (dy, dySwapFee);
  }

  /**
   * @notice Calculate the dy of withdrawing in one token
   * @param self Swap struct to read from
   * @param tokenIndex which token will be withdrawn
   * @param tokenAmount the amount to withdraw in the pools precision
   * @return the d and the new y after withdrawing one token
   */
  function calculateWithdrawOneTokenDY(
    Swap storage self,
    uint8 tokenIndex,
    uint256 tokenAmount,
    uint256 totalSupply
  ) internal view returns (uint256, uint256, uint256) {
    // Get the current D, then solve the stableswap invariant
    // y_i for D - tokenAmount

    require(tokenIndex < 2, "Token index out of range");

    CalculateWithdrawOneTokenDYInfo memory v = CalculateWithdrawOneTokenDYInfo(
      0,
      0,
      0,
      0,
      0
    );
    v.preciseA = _getAPrecise(self);
    v.d0 = getD(_pricedInBatch(self, self.balances), v.preciseA);
    v.d1 = v.d0 - ((tokenAmount * v.d0) / totalSupply);

    require(
      tokenAmount <= self.balances[tokenIndex],
      "Withdraw exceeds available"
    );

    v.newY = _pricedOut(
      self,
      getYD(v.preciseA, tokenIndex, _pricedInBatch(self, self.balances), v.d1),
      tokenIndex
    );

    uint256[2] memory xpReduced;

    v.feePerToken = self.swapFee / 2;
    for (uint256 i = 0; i < 2; ++i) {
      uint256 xpi = self.balances[i];
      xpReduced[i] =
        xpi -
        (((
          (i == tokenIndex)
            ? (xpi * v.d1) / v.d0 - v.newY
            : xpi - ((xpi * v.d1) / (v.d0))
        ) * (v.feePerToken)) / (PERCENTAGE_DENOMINATOR));
    }

    uint256 dy = xpReduced[tokenIndex] -
      _pricedOut(
        self,
        (getYD(v.preciseA, tokenIndex, _pricedInBatch(self, xpReduced), v.d1)),
        tokenIndex
      );
    dy = dy - 1;

    return (dy, v.newY, self.balances[tokenIndex]);
  }

  /**
   * @notice Get Debt, The amount of buyback for stable pricing.
   * @param xp a  set of pool balances. Array should be the same cardinality
   * as the pool.
   * @param a the amplification coefficient * n * (n - 1) in A_PRECISION.
   * See the StableSwap paper for details
   * @return debt the half of the D StableSwap invariant when debt is needed to be payed.
   */
  function _getDebt(
    Swap storage self,
    uint256[2] memory xp,
    uint256 a
  ) internal view returns (uint256) {
    uint256 halfD = getD(xp, a) / 2;
    if (xp[0] >= halfD) {
      return 0;
    } else {
      uint256 dy = xp[1] - halfD;
      uint256 feeHalf = (dy * self.swapFee) / PERCENTAGE_DENOMINATOR / 2;
      uint256 debt = halfD - xp[0] + feeHalf;
      return debt;
    }
  }

  /**
   * @return debt the half of the D StableSwap invariant when debt is needed to be payed.
   */
  function getDebt(Swap storage self) external view returns (uint256) {
    // might change when price is in.
    return
      _getDebt(self, _pricedInBatch(self, self.balances), _getAPrecise(self));
  }

  /**
   * @notice Calculate the price of a token in the pool with given
   *  balances and a particular D.
   *
   * @dev This is accomplished via solving the invariant iteratively.
   * See the StableSwap paper and Curve.fi implementation for further details.
   *
   * x_1**2 + x1 * (sum' - (A*n**n - 1) * D / (A * n**n)) = D ** (n + 1) / (n ** (2 * n) * prod' * A)
   * x_1**2 + b*x_1 = c
   * x_1 = (x_1**2 + c) / (2*x_1 + b)
   *
   * @param a the amplification coefficient * n * (n - 1). See the StableSwap paper for details.
   * @param tokenIndex Index of token we are calculating for.
   * @param xp a  set of pool balances. Array should be
   * the same cardinality as the pool.
   * @param d the stableswap invariant
   * @return the price of the token, in the same precision as in xp
   */
  function getYD(
    uint256 a,
    uint8 tokenIndex,
    uint256[2] memory xp,
    uint256 d
  ) internal pure returns (uint256) {
    uint256 numTokens = 2;
    require(tokenIndex < numTokens, "Token not found");

    uint256 c = d;
    uint256 s;
    uint256 nA = a * numTokens;

    for (uint256 i = 0; i < numTokens; ++i) {
      if (i != tokenIndex) {
        s = s + xp[i];
        c = (c * d) / (xp[i] * (numTokens));
        // If we were to protect the division loss we would have to keep the denominator separate
        // and divide at the end. However this leads to overflow with large numTokens or/and D.
        // c = c * D * D * D * ... overflow!
      }
    }
    c = (c * d * AmplificationUtils.A_PRECISION) / (nA * numTokens);

    uint256 b = s + ((d * AmplificationUtils.A_PRECISION) / nA);
    uint256 yPrev;
    uint256 y = d;
    for (uint256 i = 0; i < MAX_LOOP_LIMIT; ++i) {
      yPrev = y;
      y = ((y * y) + c) / (2 * y + b - d);
      if (y.within1(yPrev)) {
        return y;
      }
    }
    revert("Approximation did not converge");
  }

  /**
   * @notice Get D, the StableSwap invariant, based on a set of balances and a particular A.
   * @param xp a  set of pool balances. Array should be the same cardinality
   * as the pool.
   * @param a the amplification coefficient * n * (n - 1) in A_PRECISION.
   * See the StableSwap paper for details
   * @return the invariant, at the precision of the pool
   */
  function getD(
    uint256[2] memory xp,
    uint256 a
  ) internal pure returns (uint256) {
    uint256 numTokens = 2;
    uint256 s = xp[0] + xp[1];
    if (s == 0) {
      return 0;
    }

    uint256 prevD;
    uint256 d = s;
    uint256 nA = a * numTokens;

    for (uint256 i = 0; i < MAX_LOOP_LIMIT; ++i) {
      uint256 dP = (d ** (numTokens + 1)) /
        (numTokens ** numTokens * xp[0] * xp[1]);
      prevD = d;
      d =
        ((((nA * s) / AmplificationUtils.A_PRECISION) + dP * numTokens) * (d)) /
        (((nA - AmplificationUtils.A_PRECISION) * (d)) /
          (AmplificationUtils.A_PRECISION) +
          ((numTokens + 1) * dP));

      if (d.within1(prevD)) {
        return d;
      }
    }

    // Convergence should occur in 4 loops or less. If this is reached, there may be something wrong
    // with the pool. If this were to occur repeatedly, LPs should withdraw via `removeLiquidity()`
    // function which does not rely on D.
    revert("D does not converge");
  }

  /**
   * @notice Get the virtual price, to help calculate profit
   * @param self Swap struct to read from
   * @return the virtual price
   */
  function getVirtualPrice(Swap storage self) external view returns (uint256) {
    uint256 d = getD(_pricedInBatch(self, self.balances), _getAPrecise(self));
    ILPToken lpToken = self.lpToken;
    uint256 supply = lpToken.totalSupply();
    if (supply > 0) {
      return (d * 10 ** 18) / supply;
    }
    return 0;
  }

  /**
   * @notice Calculate the new balances of the tokens given the indexes of the token
   * that is swapped from (FROM) and the token that is swapped to (TO).
   * This function is used as a helper function to calculate how much TO token
   * the user should receive on swap.
   *
   * @param preciseA precise form of amplification coefficient
   * @param tokenIndexFrom index of FROM token
   * @param tokenIndexTo index of TO token
   * @param x the new total amount of FROM token
   * @param xp balances of the tokens in the pool
   * @return the amount of TO token that should remain in the pool
   */
  function getY(
    uint256 preciseA,
    uint8 tokenIndexFrom,
    uint8 tokenIndexTo,
    uint256 x,
    uint256[2] memory xp
  ) internal pure returns (uint256) {
    uint256 numTokens = 2;
    require(tokenIndexFrom != tokenIndexTo, "Can't compare token to itself");
    require(
      tokenIndexFrom < numTokens && tokenIndexTo < numTokens,
      "Tokens must be in pool"
    );

    uint256 d = getD(xp, preciseA);
    uint256 c = d;
    uint256 s = x;
    uint256 nA = numTokens * (preciseA);

    c = (c * d) / (x * numTokens);
    c = (c * d * (AmplificationUtils.A_PRECISION)) / (nA * numTokens);
    uint256 b = s + ((d * AmplificationUtils.A_PRECISION) / nA);

    uint256 yPrev;
    uint256 y = d;
    for (uint256 i = 0; i < MAX_LOOP_LIMIT; ++i) {
      yPrev = y;
      y = ((y * y) + c) / (2 * y + b - d);
      if (y.within1(yPrev)) {
        return y;
      }
    }
    revert("Approximation did not converge");
  }

  /**
   * @notice Externally calculates a swap between two tokens.
   * @param self Swap struct to read from
   * @param tokenIndexFrom the token to sell
   * @param tokenIndexTo the token to buy
   * @param dx the number of tokens to sell. If the token charges a fee on transfers,
   * use the amount that gets transferred after the fee.
   * @return dy the number of tokens the user will get
   */
  function calculateSwap(
    Swap storage self,
    uint8 tokenIndexFrom,
    uint8 tokenIndexTo,
    uint256 dx
  ) external view returns (uint256 dy) {
    (dy, ) = _calculateSwap(
      self,
      tokenIndexFrom,
      tokenIndexTo,
      dx,
      self.balances
    );
  }

  /**
   * @notice Internally calculates a swap between two tokens.
   *
   * @dev The caller is expected to transfer the actual amounts (dx and dy)
   * using the token contracts.
   *
   * @param self Swap struct to read from
   * @param tokenIndexFrom the token to sell
   * @param tokenIndexTo the token to buy
   * @param dx the number of tokens to sell. If the token charges a fee on transfers,
   * use the amount that gets transferred after the fee.
   * @return dy the number of tokens the user will get
   * @return dyFee the associated fee
   */
  function _calculateSwap(
    Swap storage self,
    uint8 tokenIndexFrom,
    uint8 tokenIndexTo,
    uint256 dx,
    uint256[2] memory balances
  ) internal view returns (uint256 dy, uint256 dyFee) {
    require(
      tokenIndexFrom < balances.length && tokenIndexTo < balances.length,
      "Token index out of range"
    );
    uint256 x = _pricedIn(self, dx + balances[tokenIndexFrom], tokenIndexFrom);

    uint256[2] memory pricedBalances = _pricedInBatch(self, balances);

    uint256 y = _pricedOut(
      self,
      getY(_getAPrecise(self), tokenIndexFrom, tokenIndexTo, x, pricedBalances),
      tokenIndexTo // => not id, index !!!
    );
    dy = balances[tokenIndexTo] - y - 1;
    dyFee = (dy * self.swapFee) / (PERCENTAGE_DENOMINATOR);
    dy = dy - dyFee;
  }

  /**
   * @notice Uses _calculateRemoveLiquidity with Effective Balances,
   * then projects the prices to the token amounts
   * to get Real Balances, before removing them from pool.
   */
  function calculateRemoveLiquidity(
    Swap storage self,
    uint256 amount
  ) external view returns (uint256[2] memory) {
    return
      _pricedOutBatch(
        self,
        _calculateRemoveLiquidity(
          _pricedInBatch(self, self.balances),
          amount,
          self.lpToken.totalSupply()
        )
      );
  }

  /**
   * @notice A simple method to calculate amount of each underlying
   * tokens that is returned upon burning given amount of
   * LP tokens
   *
   * @param amount the amount of LP tokens that would to be burned on
   * withdrawal
   * @return amounts of tokens user will receive as an array [ETH, gETH]
   */
  function _calculateRemoveLiquidity(
    uint256[2] memory balances,
    uint256 amount,
    uint256 totalSupply
  ) internal pure returns (uint256[2] memory amounts) {
    require(amount <= totalSupply, "Cannot exceed total supply");

    amounts[0] = (balances[0] * amount) / totalSupply;
    amounts[1] = (balances[1] * amount) / totalSupply;

    return amounts;
  }

  /**
   * @notice A simple method to calculate prices from deposits or
   * withdrawals, excluding fees but including slippage. This is
   * helpful as an input into the various "min" parameters on calls
   * to fight front-running
   *
   * @dev This shouldn't be used outside frontends for user estimates.
   *
   * @param self Swap struct to read from
   * @param amounts an array of token amounts to deposit or withdrawal,
   * corresponding to pooledTokens. The amount should be in each
   * pooled token's native precision. If a token charges a fee on transfers,
   * use the amount that gets transferred after the fee.
   * @param deposit whether this is a deposit or a withdrawal
   * @return if deposit was true, total amount of lp token that will be minted and if
   * deposit was false, total amount of lp token that will be burned
   */
  function calculateTokenAmount(
    Swap storage self,
    uint256[2] calldata amounts,
    bool deposit
  ) external view returns (uint256) {
    uint256 a = _getAPrecise(self);
    uint256[2] memory balances = self.balances;

    uint256 d0 = getD(_pricedInBatch(self, balances), a);
    for (uint256 i = 0; i < balances.length; ++i) {
      if (deposit) {
        balances[i] = balances[i] + amounts[i];
      } else {
        require(
          amounts[i] <= balances[i],
          "Cannot withdraw more than available"
        );
        balances[i] = balances[i] - amounts[i];
      }
    }
    uint256 d1 = getD(_pricedInBatch(self, balances), a);
    uint256 totalSupply = self.lpToken.totalSupply();

    if (deposit) {
      return ((d1 - d0) * totalSupply) / d0;
    } else {
      return ((d0 - d1) * totalSupply) / d0;
    }
  }

  /**
   * @notice return accumulated amount of admin fees of the token with given index
   * @param self Swap struct to read from
   * @param index Index of the pooled token
   * @return admin balance in the token's precision
   */
  function getAdminBalance(
    Swap storage self,
    uint256 index
  ) external view returns (uint256) {
    require(index < 2, "Token index out of range");
    if (index == 0) return address(this).balance - (self.balances[index]);

    if (index == 1)
      return
        self.gETH.balanceOf(address(this), self.pooledTokenId) -
        (self.balances[index]);
    return 0;
  }

  /*** STATE MODIFYING FUNCTIONS ***/

  /**
   * @notice swap two tokens in the pool
   * @param self Swap struct to read from and write to
   * @param tokenIndexFrom the token the user wants to sell
   * @param tokenIndexTo the token the user wants to buy
   * @param dx the amount of tokens the user wants to sell
   * @param minDy the min amount the user would like to receive, or revert.
   * @return amount of token user received on swap
   */
  function swap(
    Swap storage self,
    uint8 tokenIndexFrom,
    uint8 tokenIndexTo,
    uint256 dx,
    uint256 minDy
  ) external returns (uint256) {
    IgETH gETHReference = self.gETH;
    if (tokenIndexFrom == 0) {
      // Means user is selling some ETH to the pool to get some gETH.
      // In which case, we need to send exactly that amount of ETH.
      require(dx == msg.value, "Cannot swap more/less than you sent");
    }
    if (tokenIndexFrom == 1) {
      // Means user is selling some gETH to the pool to get some ETH.

      require(
        dx <= gETHReference.balanceOf(msg.sender, self.pooledTokenId),
        "Cannot swap more than you own"
      );

      // Transfer tokens first
      uint256 beforeBalance = gETHReference.balanceOf(
        address(this),
        self.pooledTokenId
      );
      gETHReference.safeTransferFrom(
        msg.sender,
        address(this),
        self.pooledTokenId,
        dx,
        ""
      );

      // Use the actual transferred amount for AMM math
      dx =
        gETHReference.balanceOf(address(this), self.pooledTokenId) -
        beforeBalance;
    }

    uint256 dy;
    uint256 dyFee;
    // Meaning the real balances *without* any effect of underlying price
    // However, when we call _calculateSwap, it uses pricedIn function before calculation,
    // and pricedOut function after the calculation. So, we don't need to use priceOut here.
    uint256[2] memory balances = self.balances;
    (dy, dyFee) = _calculateSwap(
      self,
      tokenIndexFrom,
      tokenIndexTo,
      dx,
      balances
    );

    require(dy >= minDy, "Swap didn't result in min tokens");
    uint256 dyAdminFee = (dyFee * self.adminFee) / PERCENTAGE_DENOMINATOR;

    // To prevent any Reentrancy, balances are updated before transfering the tokens.
    self.balances[tokenIndexFrom] = balances[tokenIndexFrom] + dx;
    self.balances[tokenIndexTo] = balances[tokenIndexTo] - dy - dyAdminFee;

    if (tokenIndexTo == 0) {
      // Means contract is going to send Idle Ether (ETH)
      (bool sent, ) = payable(msg.sender).call{value: dy}("");
      require(sent, "SwapUtils: Failed to send Ether");
    }
    if (tokenIndexTo == 1) {
      // Means contract is going to send staked ETH (gETH)
      gETHReference.safeTransferFrom(
        address(this),
        msg.sender,
        self.pooledTokenId,
        dy,
        ""
      );
    }

    emit TokenSwap(msg.sender, dx, dy, tokenIndexFrom, tokenIndexTo);

    return dy;
  }

  /**
   * @notice Add liquidity to the pool
   * @param self Swap struct to read from and write to
   * @param amounts the amounts of each token to add, in their native precision
   * @param minToMint the minimum LP tokens adding this amount of liquidity
   * should mint, otherwise revert. Handy for front-running mitigation
   * allowed addresses. If the pool is not in the guarded launch phase, this parameter will be ignored.
   * @return amount of LP token user received
   */
  function addLiquidity(
    Swap storage self,
    uint256[2] memory amounts,
    uint256 minToMint
  ) external returns (uint256) {
    require(
      amounts[0] == msg.value,
      "SwapUtils: received less or more ETH than expected"
    );
    IgETH gETHReference = self.gETH;
    // current state
    ManageLiquidityInfo memory v = ManageLiquidityInfo(
      self.lpToken,
      0,
      0,
      0,
      _getAPrecise(self),
      0,
      self.balances
    );
    v.totalSupply = v.lpToken.totalSupply();
    if (v.totalSupply != 0) {
      v.d0 = getD(_pricedInBatch(self, v.balances), v.preciseA);
    }

    uint256[2] memory newBalances;
    newBalances[0] = v.balances[0] + msg.value;

    for (uint256 i = 0; i < 2; ++i) {
      require(
        v.totalSupply != 0 || amounts[i] > 0,
        "Must supply all tokens in pool"
      );
    }

    {
      // Transfer tokens first
      uint256 beforeBalance = gETHReference.balanceOf(
        address(this),
        self.pooledTokenId
      );
      gETHReference.safeTransferFrom(
        msg.sender,
        address(this),
        self.pooledTokenId,
        amounts[1],
        ""
      );

      // Update the amounts[] with actual transfer amount
      amounts[1] =
        gETHReference.balanceOf(address(this), self.pooledTokenId) -
        beforeBalance;

      newBalances[1] = v.balances[1] + amounts[1];
    }

    // invariant after change
    v.d1 = getD(_pricedInBatch(self, newBalances), v.preciseA);
    require(v.d1 > v.d0, "D should increase");

    // updated to reflect fees and calculate the user's LP tokens
    v.d2 = v.d1;
    uint256[2] memory fees;

    if (v.totalSupply != 0) {
      uint256 feePerToken = self.swapFee / 2;
      for (uint256 i = 0; i < 2; ++i) {
        uint256 idealBalance = (v.d1 * v.balances[i]) / v.d0;
        fees[i] =
          (feePerToken * (idealBalance.difference(newBalances[i]))) /
          (PERCENTAGE_DENOMINATOR);
        self.balances[i] =
          newBalances[i] -
          ((fees[i] * (self.adminFee)) / (PERCENTAGE_DENOMINATOR));
        newBalances[i] = newBalances[i] - (fees[i]);
      }
      v.d2 = getD(_pricedInBatch(self, newBalances), v.preciseA);
    } else {
      // the initial depositor doesn't pay fees
      self.balances = newBalances;
    }

    uint256 toMint;
    if (v.totalSupply == 0) {
      toMint = v.d1;
    } else {
      toMint = ((v.d2 - v.d0) * v.totalSupply) / v.d0;
    }

    require(toMint >= minToMint, "Couldn't mint min requested");

    // mint the user's LP tokens
    v.lpToken.mint(msg.sender, toMint);

    emit AddLiquidity(msg.sender, amounts, fees, v.d1, v.totalSupply + toMint);
    return toMint;
  }

  /**
   * @notice Burn LP tokens to remove liquidity from the pool.
   * @dev Liquidity can always be removed, even when the pool is paused.
   * @param self Swap struct to read from and write to
   * @param amount the amount of LP tokens to burn
   * @param minAmounts the minimum amounts of each token in the pool
   * acceptable for this burn. Useful as a front-running mitigation
   * @return amounts of tokens the user received
   */
  function removeLiquidity(
    Swap storage self,
    uint256 amount,
    uint256[2] calldata minAmounts
  ) external returns (uint256[2] memory) {
    ILPToken lpToken = self.lpToken;
    IgETH gETHReference = self.gETH;
    require(amount <= lpToken.balanceOf(msg.sender), ">LP.balanceOf");

    uint256[2] memory balances = self.balances;
    uint256 totalSupply = lpToken.totalSupply();

    uint256[2] memory amounts = _pricedOutBatch(
      self,
      _calculateRemoveLiquidity(
        _pricedInBatch(self, balances),
        amount,
        totalSupply
      )
    );

    for (uint256 i = 0; i < amounts.length; ++i) {
      require(amounts[i] >= minAmounts[i], "amounts[i] < minAmounts[i]");
      self.balances[i] = balances[i] - amounts[i];
    }

    // To prevent any Reentrancy, LP tokens are burned before transfering the tokens.
    lpToken.burnFrom(msg.sender, amount);

    (bool sent, ) = payable(msg.sender).call{value: amounts[0]}("");
    require(sent, "SwapUtils: Failed to send Ether");

    gETHReference.safeTransferFrom(
      address(this),
      msg.sender,
      self.pooledTokenId,
      amounts[1],
      ""
    );

    emit RemoveLiquidity(msg.sender, amounts, totalSupply - amount);
    return amounts;
  }

  /**
   * @notice Remove liquidity from the pool all in one token.
   * @param self Swap struct to read from and write to
   * @param tokenAmount the amount of the lp tokens to burn
   * @param tokenIndex the index of the token you want to receive
   * @param minAmount the minimum amount to withdraw, otherwise revert
   * @return amount chosen token that user received
   */
  function removeLiquidityOneToken(
    Swap storage self,
    uint256 tokenAmount,
    uint8 tokenIndex,
    uint256 minAmount
  ) external returns (uint256) {
    ILPToken lpToken = self.lpToken;
    IgETH gETHReference = self.gETH;

    require(tokenAmount <= lpToken.balanceOf(msg.sender), ">LP.balanceOf");
    require(tokenIndex < 2, "Token not found");

    uint256 totalSupply = lpToken.totalSupply();

    (uint256 dy, uint256 dyFee) = _calculateWithdrawOneToken(
      self,
      tokenAmount,
      tokenIndex,
      totalSupply
    );

    require(dy >= minAmount, "dy < minAmount");

    // To prevent any Reentrancy, LP tokens are burned before transfering the tokens.
    self.balances[tokenIndex] =
      self.balances[tokenIndex] -
      (dy + ((dyFee * (self.adminFee)) / (PERCENTAGE_DENOMINATOR)));
    lpToken.burnFrom(msg.sender, tokenAmount);

    if (tokenIndex == 0) {
      (bool sent, ) = payable(msg.sender).call{value: dy}("");
      require(sent, "SwapUtils: Failed to send Ether");
    }
    if (tokenIndex == 1) {
      gETHReference.safeTransferFrom(
        address(this),
        msg.sender,
        self.pooledTokenId,
        dy,
        ""
      );
    }

    emit RemoveLiquidityOne(
      msg.sender,
      tokenAmount,
      totalSupply,
      tokenIndex,
      dy
    );

    return dy;
  }

  /**
   * @notice Remove liquidity from the pool, weighted differently than the
   * pool's current balances.
   *
   * @param self Swap struct to read from and write to
   * @param amounts how much of each token to withdraw
   * @param maxBurnAmount the max LP token provider is willing to pay to
   * remove liquidity. Useful as a front-running mitigation.
   * @return actual amount of LP tokens burned in the withdrawal
   */
  function removeLiquidityImbalance(
    Swap storage self,
    uint256[2] memory amounts,
    uint256 maxBurnAmount
  ) public returns (uint256) {
    IgETH gETHReference = self.gETH;

    ManageLiquidityInfo memory v = ManageLiquidityInfo(
      self.lpToken,
      0,
      0,
      0,
      _getAPrecise(self),
      0,
      self.balances
    );
    v.totalSupply = v.lpToken.totalSupply();

    require(
      maxBurnAmount <= v.lpToken.balanceOf(msg.sender) && maxBurnAmount != 0,
      ">LP.balanceOf"
    );

    uint256 feePerToken = self.swapFee / 2;
    uint256[2] memory fees;

    {
      uint256[2] memory balances1;

      v.d0 = getD(_pricedInBatch(self, v.balances), v.preciseA);
      for (uint256 i = 0; i < 2; ++i) {
        require(
          amounts[i] <= v.balances[i],
          "Cannot withdraw more than available"
        );
        balances1[i] = v.balances[i] - amounts[i];
      }
      v.d1 = getD(_pricedInBatch(self, balances1), v.preciseA);

      for (uint256 i = 0; i < 2; ++i) {
        uint256 idealBalance = (v.d1 * v.balances[i]) / v.d0;
        uint256 difference = idealBalance.difference(balances1[i]);
        fees[i] = (feePerToken * difference) / PERCENTAGE_DENOMINATOR;
        uint256 adminFee = self.adminFee;
        {
          self.balances[i] =
            balances1[i] -
            ((fees[i] * adminFee) / PERCENTAGE_DENOMINATOR);
        }
        balances1[i] = balances1[i] - fees[i];
      }

      v.d2 = getD(_pricedInBatch(self, balances1), v.preciseA);
    }

    uint256 tokenAmount = ((v.d0 - v.d2) * (v.totalSupply)) / v.d0;
    require(tokenAmount != 0, "Burnt amount cannot be zero");
    tokenAmount = tokenAmount + 1;

    require(tokenAmount <= maxBurnAmount, "tokenAmount > maxBurnAmount");

    // To prevent any Reentrancy, LP tokens are burned before transfering the tokens.
    v.lpToken.burnFrom(msg.sender, tokenAmount);

    (bool sent, ) = payable(msg.sender).call{value: amounts[0]}("");
    require(sent, "SwapUtils: Failed to send Ether");

    gETHReference.safeTransferFrom(
      address(this),
      msg.sender,
      self.pooledTokenId,
      amounts[1],
      ""
    );

    emit RemoveLiquidityImbalance(
      msg.sender,
      amounts,
      fees,
      v.d1,
      v.totalSupply - tokenAmount
    );

    return tokenAmount;
  }

  /**
   * @notice withdraw all admin fees to a given address
   * @param self Swap struct to withdraw fees from
   * @param to Address to send the fees to
   */
  function withdrawAdminFees(Swap storage self, address to) external {
    IgETH gETHReference = self.gETH;
    uint256 tokenBalance = gETHReference.balanceOf(
      address(this),
      self.pooledTokenId
    ) - self.balances[1];
    if (tokenBalance != 0) {
      gETHReference.safeTransferFrom(
        address(this),
        to,
        self.pooledTokenId,
        tokenBalance,
        ""
      );
    }

    uint256 etherBalance = address(this).balance - self.balances[0];
    if (etherBalance != 0) {
      (bool sent, ) = payable(msg.sender).call{value: etherBalance}("");
      require(sent, "SwapUtils: Failed to send Ether");
    }
  }

  /**
   * @notice Sets the admin fee
   * @dev adminFee cannot be higher than 100% of the swap fee
   * @param self Swap struct to update
   * @param newAdminFee new admin fee to be applied on future transactions
   */
  function setAdminFee(Swap storage self, uint256 newAdminFee) external {
    require(newAdminFee <= MAX_ADMIN_FEE, "Fee is too high");
    self.adminFee = newAdminFee;

    emit NewAdminFee(newAdminFee);
  }

  /**
   * @notice update the swap fee
   * @dev fee cannot be higher than 1% of each swap
   * @param self Swap struct to update
   * @param newSwapFee new swap fee to be applied on future transactions
   */
  function setSwapFee(Swap storage self, uint256 newSwapFee) external {
    require(newSwapFee <= MAX_SWAP_FEE, "Fee is too high");
    self.swapFee = newSwapFee;

    emit NewSwapFee(newSwapFee);
  }
}

contracts/Portal/Portal.sol.content:
// SPDX-License-Identifier: MIT

//                 
//       
//                          
//                            
//                     
//                            
//

pragma solidity =0.8.7;
import "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import "../interfaces/IgETH.sol";
import "../interfaces/IPortal.sol";
import "../interfaces/IWithdrawalContract.sol";

import {ID_TYPE, PERCENTAGE_DENOMINATOR} from "./utils/globals.sol";

/**
 * @author Icebear & Crash Bandicoot
 * @title Geode Portal : Configurable Trustless Staking Pools
 * *
 * @dev Portal doesn't have any functionality other than hosting and combining the functionalities of some libraries:
 * Provides a first of its kind trustless implementation on Staking Derivatives: gETH
 * The Portal utilizes an isolated storage which allows storing infinitely many different
 * * types of dynamic structs, easily with ID and KEY pairs.
 * Portal is secured by The Dual Governance and Limited Upgradability.
 * Portal hosts The Staking Library allowing the creation and maintanence of configurable
 * * staking pools and validator creation process.
 * Underlying validator balances for each pool is secured with unique Withdrawal Contracts.
 *
 * @dev TYPE: seperates the proposals and related functionality between different ID types on the Isolated Storage.
 * * Currently RESERVED TYPES are written on globals.sol.
 *
 * @dev authentication:
 * * geodeUtils has OnlyGovernance, OnlySenate and OnlyController checks with modifiers.
 * * stakeUtils has "authenticate()" function which checks for Maintainer, Controllers, and TYPE.
 * * oracleutils has OnlyOracle checks with a modifier.
 * * Portal has an OnlyGovernance check on : pause, unpause, pausegETH, unpausegETH, setEarlyExitFee, releasePrisoned.
 *
 * @dev first review DataStoreUtils
 * @dev then review GeodeUtils
 * @dev then review StakeUtils
 * @dev then review OracleUtils
 *
 * note ctrl+k+2 and ctrl+k+1 then scroll while reading the function names and the comments.
 */

contract Portal is
  IPortal,
  ContextUpgradeable,
  ReentrancyGuardUpgradeable,
  PausableUpgradeable,
  ERC1155HolderUpgradeable,
  UUPSUpgradeable
{
  using DataStoreUtils for DataStoreUtils.IsolatedStorage;
  using GeodeUtils for GeodeUtils.DualGovernance;
  using StakeUtils for StakeUtils.PooledStaking;

  /**
   * @notice                                     ** EVENTS **
   *
   * @dev following events are added to help fellow devs with a better ABI
   */

  /**
   * @dev GeodeUtils events
   */
  event GovernanceFeeUpdated(uint256 newFee);
  event ControllerChanged(uint256 indexed id, address newCONTROLLER);
  event Proposed(
    uint256 id,
    address CONTROLLER,
    uint256 indexed TYPE,
    uint256 deadline
  );
  event ProposalApproved(uint256 id);
  event ElectorTypeSet(uint256 TYPE, bool isElector);
  event Vote(uint256 indexed proposalId, uint256 indexed voterId);
  event NewSenate(address senate, uint256 senateExpiry);

  /**
   * @dev StakeUtils events
   */
  event IdInitiated(uint256 indexed id, uint256 indexed TYPE);
  event MaintainerChanged(uint256 indexed id, address newMaintainer);
  event FeeSwitched(uint256 indexed id, uint256 fee, uint256 effectiveAfter);
  event ValidatorPeriodSwitched(
    uint256 indexed id,
    uint256 period,
    uint256 effectiveAfter
  );
  event OperatorApproval(
    uint256 indexed poolId,
    uint256 indexed operatorId,
    uint256 allowance
  );
  event Prisoned(uint256 indexed id, bytes proof, uint256 releaseTimestamp);
  event Released(uint256 indexed id);
  event Deposit(uint256 indexed poolId, uint256 boughtgETH, uint256 mintedgETH);
  event ProposalStaked(
    uint256 indexed poolId,
    uint256 operatorId,
    bytes[] pubkeys
  );
  event BeaconStaked(bytes[] pubkeys);

  /**
   * @dev OracleUtils events
   */
  event Alienated(bytes indexed pubkey);
  event VerificationIndexUpdated(uint256 validatorVerificationIndex);
  event FeeTheft(uint256 indexed id, bytes proofs);
  event OracleReported(bytes32 merkleRoot, uint256 monopolyThreshold);

  /**
   * @dev Portal Native events
   */
  event ContractVersionSet(uint256 version);

  /**
   * @notice                                     ** VARIABLES **
   */
  DataStoreUtils.IsolatedStorage private DATASTORE;
  GeodeUtils.DualGovernance private GEODE;
  StakeUtils.PooledStaking private STAKER;

  /**
   * @notice CONTRACT_VERSION always refers to the upgrade proposal' (TYPE2) ID.
   * @dev Does NOT increase uniformly like one might expect.
   */
  uint256 public CONTRACT_VERSION;

  /**
   * @notice                                     ** PORTAL SPECIFIC **
   */

  /**
   * @notice initializer function that sets initial parameters like,
   * Oracle Address, Governance, fee etc.
   * Then, creates proposals for the Withdrawal Contract, Liquidity Pools and gETHInterfaces.
   * Finally, it creates an Upgrade Proposal for itself and approves it, setting its version.
   */
  function initialize(
    address _GOVERNANCE,
    address _SENATE,
    address _gETH,
    address _ORACLE_POSITION,
    address _DEFAULT_WITHDRAWAL_CONTRACT_MODULE,
    address _DEFAULT_LP_MODULE,
    address _DEFAULT_LP_TOKEN_MODULE,
    address[] calldata _ALLOWED_GETH_INTERFACE_MODULES,
    bytes[] calldata _ALLOWED_GETH_INTERFACE_MODULE_NAMES,
    uint256 _GOVERNANCE_FEE
  ) public virtual override initializer {
    __ReentrancyGuard_init();
    __Pausable_init();
    __ERC1155Holder_init();
    __UUPSUpgradeable_init();

    require(_GOVERNANCE != address(0), "PORTAL: GOVERNANCE can NOT be ZERO");
    require(_SENATE != address(0), "PORTAL: SENATE can NOT be ZERO");
    require(_gETH != address(0), "PORTAL: gETH can NOT be ZERO");
    require(
      _ORACLE_POSITION != address(0),
      "PORTAL: ORACLE_POSITION can NOT be ZERO"
    );
    require(
      _DEFAULT_LP_MODULE != address(0),
      "PORTAL: DEFAULT_LP can NOT be ZERO"
    );
    require(
      _DEFAULT_LP_TOKEN_MODULE != address(0),
      "PORTAL: DEFAULT_LP_TOKEN can NOT be ZERO"
    );
    require(
      _DEFAULT_WITHDRAWAL_CONTRACT_MODULE != address(0),
      "PORTAL: WITHDRAWAL_CONTRACT_POSITION can NOT be ZERO"
    );
    require(
      _ALLOWED_GETH_INTERFACE_MODULES.length ==
        _ALLOWED_GETH_INTERFACE_MODULE_NAMES.length,
      "PORTAL: wrong _ALLOWED_GETH_INTERFACE_MODULES"
    );
    GEODE.GOVERNANCE = msg.sender;
    GEODE.SENATE = msg.sender;
    GEODE.SENATE_EXPIRY = type(uint256).max;

    GEODE.setGovernanceFee(_GOVERNANCE_FEE);
    GEODE.setElectorType(DATASTORE, ID_TYPE.POOL, true);

    STAKER.gETH = IgETH(_gETH);
    STAKER.MONOPOLY_THRESHOLD = type(uint256).max;

    STAKER.ORACLE_POSITION = _ORACLE_POSITION;
    STAKER.DAILY_PRICE_INCREASE_LIMIT = (5 * PERCENTAGE_DENOMINATOR) / 100;
    STAKER.DAILY_PRICE_DECREASE_LIMIT = (5 * PERCENTAGE_DENOMINATOR) / 100;

    {
      uint256 liqPoolVersion = GEODE.newProposal(
        DATASTORE,
        _DEFAULT_LP_MODULE,
        ID_TYPE.MODULE_LIQUDITY_POOL,
        "v1",
        1 days
      );
      approveProposal(liqPoolVersion);
    }

    {
      uint256 lpTokenVersion = GEODE.newProposal(
        DATASTORE,
        _DEFAULT_LP_TOKEN_MODULE,
        ID_TYPE.MODULE_LIQUDITY_POOL_TOKEN,
        "v1",
        1 days
      );
      approveProposal(lpTokenVersion);
    }

    {
      uint256 withdrawalContractVersion = GEODE.newProposal(
        DATASTORE,
        _DEFAULT_WITHDRAWAL_CONTRACT_MODULE,
        ID_TYPE.MODULE_WITHDRAWAL_CONTRACT,
        "v1",
        1 days
      );
      approveProposal(withdrawalContractVersion);
    }

    {
      uint256 gETHInterfaceVersion;
      for (uint256 i = 0; i < _ALLOWED_GETH_INTERFACE_MODULES.length; ) {
        require(
          _ALLOWED_GETH_INTERFACE_MODULES[i] != address(0),
          "PORTAL: GETH_INTERFACE_MODULE can NOT be ZERO"
        );

        gETHInterfaceVersion = GEODE.newProposal(
          DATASTORE,
          _ALLOWED_GETH_INTERFACE_MODULES[i],
          ID_TYPE.MODULE_GETH_INTERFACE,
          _ALLOWED_GETH_INTERFACE_MODULE_NAMES[i],
          1 days
        );

        approveProposal(gETHInterfaceVersion);
        unchecked {
          i += 1;
        }
      }
    }

    {
      uint256 portalVersion = GEODE.newProposal(
        DATASTORE,
        address(this),
        ID_TYPE.CONTRACT_UPGRADE,
        "v1",
        1 days
      );
      approveProposal(portalVersion);
      _setContractVersion(portalVersion);
    }

    GEODE.GOVERNANCE = _GOVERNANCE;
    GEODE.SENATE = _SENATE;
  }

  /**
   * @dev  ->  modifier
   */

  modifier onlyGovernance() {
    require(msg.sender == GEODE.getGovernance(), "Portal: ONLY GOVERNANCE");
    _;
  }

  /**
   * @dev  ->  internal
   */

  /**
   * @dev required by the OZ UUPS module
   * note that there is no Governance check, as upgrades are effective
   * * right after the Senate approval
   */
  function _authorizeUpgrade(
    address proposed_implementation
  ) internal virtual override {
    require(proposed_implementation != address(0));
    require(
      GEODE.isUpgradeAllowed(proposed_implementation),
      "Portal: is not allowed to upgrade"
    );
  }

  function _setContractVersion(uint256 versionId) internal virtual {
    CONTRACT_VERSION = versionId;
    GEODE.approvedVersion = address(0);
    emit ContractVersionSet(getContractVersion());
  }

  /**
   * @dev  ->  view
   */

  function getContractVersion() public view virtual override returns (uint256) {
    return CONTRACT_VERSION;
  }

  /**
   * @dev  ->  external
   */

  function pause() external virtual override onlyGovernance {
    _pause();
  }

  function unpause() external virtual override onlyGovernance {
    _unpause();
  }

  /**
   * @notice                                     ** gETH  **
   */

  /**
   * @dev  ->  view
   */

  /**
   * @notice get the position of ERC1155
   */
  function gETH() external view virtual override returns (address) {
    return address(STAKER.gETH);
  }

  /**
   * @notice read the list of interfaces for a gETH ID
   */
  function gETHInterfaces(
    uint256 id,
    uint256 index
  ) external view virtual override returns (address) {
    return StakeUtils.gETHInterfaces(DATASTORE, id, index);
  }

  /**
   * @dev  ->  external
   */

  function pausegETH() external virtual override onlyGovernance {
    STAKER.gETH.pause();
  }

  function unpausegETH() external virtual override onlyGovernance {
    STAKER.gETH.unpause();
  }

  /**
   * @notice                                     ** DATASTORE **
   */

  /**
   * @dev  ->  view
   */

  /**
   * @dev useful for outside reach, shouldn't be used within contracts as a referance
   * @return allIdsByType is an array of IDs of the given TYPE from Datastore,
   * returns a specific index
   */
  function allIdsByType(
    uint256 _type,
    uint256 _index
  ) external view virtual override returns (uint256) {
    return DATASTORE.allIdsByType[_type][_index];
  }

  /**
   * @notice useful view function for string inputs - returns same with the DATASTOREUTILS.generateId
   * @dev id is keccak(name, type)
   */
  function generateId(
    string calldata _name,
    uint256 _type
  ) external pure virtual override returns (uint256 id) {
    id = uint256(keccak256(abi.encodePacked(_name, _type)));
  }

  /**
   * @notice useful view function for string inputs - returns same with the DATASTOREUTILS.generateId
   */
  function getKey(
    uint256 _id,
    bytes32 _param
  ) external pure virtual override returns (bytes32 key) {
    return DataStoreUtils.getKey(_id, _param);
  }

  function readUint(
    uint256 id,
    bytes32 key
  ) external view virtual override returns (uint256 data) {
    data = DATASTORE.readUint(id, key);
  }

  function readAddress(
    uint256 id,
    bytes32 key
  ) external view virtual override returns (address data) {
    data = DATASTORE.readAddress(id, key);
  }

  function readBytes(
    uint256 id,
    bytes32 key
  ) external view virtual override returns (bytes memory data) {
    data = DATASTORE.readBytes(id, key);
  }

  function readUintArray(
    uint256 id,
    bytes32 key,
    uint256 index
  ) external view virtual override returns (uint256 data) {
    data = DATASTORE.readUintArray(id, key, index);
  }

  function readBytesArray(
    uint256 id,
    bytes32 key,
    uint256 index
  ) external view virtual override returns (bytes memory data) {
    data = DATASTORE.readBytesArray(id, key, index);
  }

  function readAddressArray(
    uint256 id,
    bytes32 key,
    uint256 index
  ) external view virtual override returns (address data) {
    data = DATASTORE.readAddressArray(id, key, index);
  }

  /**
   * @notice                                     ** GEODE **
   */

  /**
   * @dev  ->  view
   */

  function GeodeParams()
    external
    view
    virtual
    override
    returns (
      address SENATE,
      address GOVERNANCE,
      uint256 SENATE_EXPIRY,
      uint256 GOVERNANCE_FEE
    )
  {
    SENATE = GEODE.getSenate();
    GOVERNANCE = GEODE.getGovernance();
    SENATE_EXPIRY = GEODE.getSenateExpiry();
    GOVERNANCE_FEE = GEODE.getGovernanceFee();
  }

  function getProposal(
    uint256 id
  )
    external
    view
    virtual
    override
    returns (GeodeUtils.Proposal memory proposal)
  {
    proposal = GEODE.getProposal(id);
  }

  function isElector(
    uint256 _TYPE
  ) external view virtual override returns (bool) {
    return GEODE._electorTypes[_TYPE];
  }

  function isUpgradeAllowed(
    address proposedImplementation
  ) external view virtual override returns (bool) {
    return GEODE.isUpgradeAllowed(proposedImplementation);
  }

  /**
   * @dev  ->  external
   */

  /**
   * @dev Governance Functions
   */

  /**
   * @notice only parameter of GeodeUtils that can be mutated is the fee
   */
  function setGovernanceFee(uint256 newFee) external virtual override {
    GEODE.setGovernanceFee(newFee);
  }

  function setElectorType(
    uint256 _TYPE,
    bool _isElector
  ) external virtual override {
    GEODE.setElectorType(DATASTORE, _TYPE, _isElector);
  }

  function newProposal(
    address _CONTROLLER,
    uint256 _TYPE,
    bytes calldata _NAME,
    uint256 duration
  ) external virtual override {
    GEODE.newProposal(DATASTORE, _CONTROLLER, _TYPE, _NAME, duration);
  }

  /**
   * @dev Senate Functions
   */

  /**
   * @notice approves a specific proposal
   * @dev OnlySenate is checked inside the GeodeUtils
   */
  function approveProposal(uint256 id) public virtual override {
    (uint256 _type, ) = GEODE.approveProposal(DATASTORE, id);
    if (
      _type == ID_TYPE.MODULE_WITHDRAWAL_CONTRACT ||
      _type == ID_TYPE.MODULE_LIQUDITY_POOL ||
      _type == ID_TYPE.MODULE_LIQUDITY_POOL_TOKEN
    ) {
      STAKER._defaultModules[_type] = id;
    } else if (_type == ID_TYPE.MODULE_GETH_INTERFACE) {
      STAKER._allowedModules[_type][id] = true;
    }
  }

  /**
   * @notice changes the Senate's address without extending the expiry
   * @dev OnlySenate is checked inside the GeodeUtils
   */
  function changeSenate(address _newSenate) external virtual override {
    GEODE.changeSenate(_newSenate);
  }

  /**
   * @dev CONTROLLER Functions
   */

  function changeIdCONTROLLER(
    uint256 id,
    address newCONTROLLER
  ) external virtual override whenNotPaused {
    GeodeUtils.changeIdCONTROLLER(DATASTORE, id, newCONTROLLER);
  }

  function approveSenate(
    uint256 proposalId,
    uint256 electorId
  ) external virtual override {
    GEODE.approveSenate(DATASTORE, proposalId, electorId);
  }

  /**
   * @notice                                     ** THE STAKING LIBRARY **
   */

  /**
   * @dev  ->  view
   */

  function StakingParams()
    external
    view
    virtual
    override
    returns (
      uint256 VALIDATORS_INDEX,
      uint256 VERIFICATION_INDEX,
      uint256 MONOPOLY_THRESHOLD,
      uint256 EARLY_EXIT_FEE,
      uint256 ORACLE_UPDATE_TIMESTAMP,
      uint256 DAILY_PRICE_INCREASE_LIMIT,
      uint256 DAILY_PRICE_DECREASE_LIMIT,
      bytes32 PRICE_MERKLE_ROOT,
      address ORACLE_POSITION
    )
  {
    VALIDATORS_INDEX = STAKER.VALIDATORS_INDEX;
    VERIFICATION_INDEX = STAKER.VERIFICATION_INDEX;
    MONOPOLY_THRESHOLD = STAKER.MONOPOLY_THRESHOLD;
    EARLY_EXIT_FEE = STAKER.EARLY_EXIT_FEE;
    ORACLE_UPDATE_TIMESTAMP = STAKER.ORACLE_UPDATE_TIMESTAMP;
    DAILY_PRICE_INCREASE_LIMIT = STAKER.DAILY_PRICE_INCREASE_LIMIT;
    DAILY_PRICE_DECREASE_LIMIT = STAKER.DAILY_PRICE_DECREASE_LIMIT;
    PRICE_MERKLE_ROOT = STAKER.PRICE_MERKLE_ROOT;
    ORACLE_POSITION = STAKER.ORACLE_POSITION;
  }

  function getValidator(
    bytes calldata pubkey
  ) external view virtual override returns (StakeUtils.Validator memory) {
    return STAKER._validators[pubkey];
  }

  function getValidatorByPool(
    uint256 poolId,
    uint256 index
  ) external view virtual override returns (bytes memory) {
    return DATASTORE.readBytesArray(poolId, "validators", index);
  }

  function getMaintenanceFee(
    uint256 id
  ) external view virtual override returns (uint256 fee) {
    fee = StakeUtils.getMaintenanceFee(DATASTORE, id);
  }

  function isPrisoned(
    uint256 operatorId
  ) external view virtual override returns (bool) {
    return StakeUtils.isPrisoned(DATASTORE, operatorId);
  }

  function isPrivatePool(
    uint256 poolId
  ) external view virtual override returns (bool) {
    return StakeUtils.isPrivatePool(DATASTORE, poolId);
  }

  function isPriceValid(
    uint256 poolId
  ) external view virtual override returns (bool) {
    return STAKER.isPriceValid(poolId);
  }

  function isMintingAllowed(
    uint256 poolId
  ) external view virtual override returns (bool) {
    return STAKER.isMintingAllowed(DATASTORE, poolId);
  }

  function canStake(
    bytes calldata pubkey
  ) external view virtual override returns (bool) {
    return STAKER.canStake(DATASTORE, pubkey);
  }

  function getDefaultModule(
    uint256 _type
  ) external view virtual override returns (uint256 _version) {
    _version = STAKER._defaultModules[_type];
  }

  function isAllowedModule(
    uint256 _type,
    uint256 _id
  ) external view virtual override returns (bool) {
    return STAKER._allowedModules[_type][_id];
  }

  /**
   * @dev  ->  external
   */

  /**
   * @dev MODULES
   */

  function fetchWithdrawalContractUpgradeProposal(
    uint256 id
  ) external virtual override returns (uint256 withdrawalContractVersion) {
    withdrawalContractVersion = STAKER._defaultModules[
      ID_TYPE.MODULE_WITHDRAWAL_CONTRACT
    ];

    StakeUtils.withdrawalContractById(DATASTORE, id).newProposal(
      DATASTORE.readAddress(withdrawalContractVersion, "CONTROLLER"),
      ID_TYPE.CONTRACT_UPGRADE,
      DATASTORE.readBytes(withdrawalContractVersion, "NAME"),
      4 weeks
    );
  }

  function deployLiquidityPool(
    uint256 poolId
  ) external virtual override whenNotPaused {
    STAKER.deployLiquidityPool(DATASTORE, poolId, GEODE.getGovernance());
  }

  function setPoolVisibility(
    uint256 poolId,
    bool isPrivate
  ) external virtual override whenNotPaused {
    StakeUtils.setPoolVisibility(DATASTORE, poolId, isPrivate);
  }

  function setWhitelist(
    uint256 poolId,
    address whitelist
  ) external virtual override whenNotPaused {
    StakeUtils.setWhitelist(DATASTORE, poolId, whitelist);
  }

  /**
   * @dev INITIATORS
   */

  function initiateOperator(
    uint256 id,
    uint256 fee,
    uint256 validatorPeriod,
    address maintainer
  ) external payable virtual override whenNotPaused {
    StakeUtils.initiateOperator(
      DATASTORE,
      id,
      fee,
      validatorPeriod,
      maintainer
    );
  }

  function initiatePool(
    uint256 fee,
    uint256 interfaceVersion,
    address maintainer,
    bytes calldata NAME,
    bytes calldata interface_data,
    bool[3] calldata config
  ) external payable virtual override whenNotPaused {
    STAKER.initiatePool(
      DATASTORE,
      fee,
      interfaceVersion,
      maintainer,
      GEODE.getGovernance(),
      NAME,
      interface_data,
      config
    );
  }

  /**
   * @dev MAINTAINERS
   */

  function changeMaintainer(
    uint256 id,
    address newMaintainer
  ) external virtual override whenNotPaused {
    StakeUtils.changeMaintainer(DATASTORE, id, newMaintainer);
  }

  /**
   * @dev MAINTENANCE FEE
   */
  function switchMaintenanceFee(
    uint256 id,
    uint256 newFee
  ) external virtual override whenNotPaused {
    StakeUtils.switchMaintenanceFee(DATASTORE, id, newFee);
  }

  /**
   * @dev INTERNAL WALLET
   */

  function increaseWalletBalance(
    uint256 id
  ) external payable virtual override returns (bool success) {
    success = StakeUtils.increaseWalletBalance(DATASTORE, id);
  }

  function decreaseWalletBalance(
    uint256 id,
    uint256 value
  )
    external
    virtual
    override
    whenNotPaused
    nonReentrant
    returns (bool success)
  {
    success = StakeUtils.decreaseWalletBalance(DATASTORE, id, value);
  }

  /**
   * @dev OPERATORS
   */

  function switchValidatorPeriod(
    uint256 id,
    uint256 newPeriod
  ) external virtual override whenNotPaused {
    StakeUtils.switchValidatorPeriod(DATASTORE, id, newPeriod);
  }

  function blameOperator(
    bytes calldata pk
  ) external virtual override whenNotPaused {
    STAKER.blameOperator(DATASTORE, pk);
  }

  function setEarlyExitFee(
    uint256 fee
  ) external virtual override whenNotPaused onlyGovernance {
    require(fee < StakeUtils.MAX_EARLY_EXIT_FEE);
    STAKER.EARLY_EXIT_FEE = fee;
  }

  /**
   * @dev PRISON
   */

  /**
   * @notice releases an imprisoned operator immidately
   * @dev in different situations such as a faulty imprisonment or coordinated testing periods
   * * Governance can release the prisoners
   * @dev onlyGovernance SHOULD be checked in Portal
   */
  function releasePrisoned(
    uint256 operatorId
  ) external virtual override whenNotPaused onlyGovernance {
    DATASTORE.writeUint(operatorId, "released", block.timestamp);

    emit Released(operatorId);
  }

  /**
   * @dev OPERATOR APPROVALS
   */

  function approveOperators(
    uint256 poolId,
    uint256[] calldata operatorIds,
    uint256[] calldata allowances
  ) external virtual override whenNotPaused {
    StakeUtils.batchApproveOperators(
      DATASTORE,
      poolId,
      operatorIds,
      allowances
    );
  }

  /**
   * @dev STAKING
   */

  function deposit(
    uint256 poolId,
    uint256 mingETH,
    uint256 deadline,
    uint256 price,
    bytes32[] calldata priceProof,
    address receiver
  ) external payable virtual override whenNotPaused nonReentrant {
    if (!STAKER.isPriceValid(poolId)) {
      OracleUtils.priceSync(DATASTORE, STAKER, poolId, price, priceProof);
    }
    STAKER.deposit(DATASTORE, poolId, mingETH, deadline, receiver);
  }

  function proposeStake(
    uint256 poolId,
    uint256 operatorId,
    bytes[] calldata pubkeys,
    bytes[] calldata signatures1,
    bytes[] calldata signatures31
  ) external virtual override whenNotPaused nonReentrant {
    STAKER.proposeStake(
      DATASTORE,
      poolId,
      operatorId,
      pubkeys,
      signatures1,
      signatures31
    );
  }

  function beaconStake(
    uint256 operatorId,
    bytes[] calldata pubkeys
  ) external virtual override whenNotPaused nonReentrant {
    STAKER.beaconStake(DATASTORE, operatorId, pubkeys);
  }

  /**
   * @notice                                     ** ORACLE **
   */

  /**
   * @dev  ->  external
   */

  function updateVerificationIndex(
    uint256 validatorVerificationIndex,
    bytes[] calldata alienatedPubkeys
  ) external virtual override whenNotPaused {
    OracleUtils.updateVerificationIndex(
      DATASTORE,
      STAKER,
      validatorVerificationIndex,
      alienatedPubkeys
    );
  }

  function regulateOperators(
    uint256[] calldata feeThefts,
    bytes[] calldata stolenBlocks
  ) external virtual override whenNotPaused {
    OracleUtils.regulateOperators(DATASTORE, STAKER, feeThefts, stolenBlocks);
  }

  function reportOracle(
    bytes32 priceMerkleRoot,
    uint256 allValidatorsCount
  ) external virtual override whenNotPaused {
    OracleUtils.reportOracle(STAKER, priceMerkleRoot, allValidatorsCount);
  }

  function priceSync(
    uint256 poolId,
    uint256 price,
    bytes32[] calldata priceProofs
  ) external virtual override whenNotPaused {
    OracleUtils.priceSync(DATASTORE, STAKER, poolId, price, priceProofs);
  }

  function priceSyncBatch(
    uint256[] calldata poolIds,
    uint256[] calldata prices,
    bytes32[][] calldata priceProofs
  ) external virtual override whenNotPaused {
    OracleUtils.priceSyncBatch(DATASTORE, STAKER, poolIds, prices, priceProofs);
  }

  /**
   * @notice fallback functions
   */
  function Do_we_care() external pure returns (bool) {
    return true;
  }

  fallback() external payable {}

  receive() external payable {}

  /**
   * @notice keep the contract size at 50
   */
  uint256[46] private __gap;
}

contracts/Portal/utils/DataStoreUtilsLib.content:
// SPDX-License-Identifier: MIT

pragma solidity =0.8.7;

/**
 * @author Icebear & Crash Bandicoot
 * @title Isolated Storage Layout
 * A Storage Management Library for Dynamic Structs
 *
 * * DataStoreUtils is a storage management tool designed to create a safe and scalable
 * * storage layout with the help of data types, IDs and keys.
 *
 * * Focusing on upgradable contracts with multiple user types to create a
 * * sustainable development environment.
 * * In summary, extra gas cost that would be saved with Storage packing are
 * * ignored to create upgradable structs.
 *
 * @dev Distinct id and key pairs SHOULD return different storage slots
 * @dev TYPEs are defined in globals.sol
 *
 * @dev IDs are the representation of an entity with any given key as properties.
 * @dev While it is good practice to keep record,
 * * TYPE for ID is NOT mandatory, an ID might not have an explicit type.
 * * Thus there is no checks of types or keys.
 */

library DataStoreUtils {
  /**
   * @notice Main Struct for reading and writing operations for given (id, key) pairs
   * @param allIdsByType type => id[], optional categorization for IDs, requires direct access
   * @param uintData keccak(id, key) =>  returns uint256
   * @param bytesData keccak(id, key) => returns bytes
   * @param addressData keccak(id, key) =>  returns address
   * @param __gap keep the struct size at 16
   * @dev any other storage type can be expressed as uint or bytes
   */
  struct IsolatedStorage {
    mapping(uint256 => uint256[]) allIdsByType;
    mapping(bytes32 => uint256) uintData;
    mapping(bytes32 => bytes) bytesData;
    mapping(bytes32 => address) addressData;
    uint256[12] __gap;
  }

  /**
   *                              ** HELPERS **
   **/

  /**
   * @notice generaliazed method of generating an ID
   * @dev Some TYPEs may require permissionless creation. Allowing anyone to claim any ID,
   * meaning malicious actors can claim names to mislead people. To prevent this
   * TYPEs will be considered during ID generation.
   */
  function generateId(
    bytes memory _name,
    uint256 _type
  ) internal pure returns (uint256 id) {
    id = uint256(keccak256(abi.encodePacked(_name, _type)));
  }

  /**
   * @notice hashes given id and a parameter to be used as key in getters and setters
   * @return key bytes32 hash of id and parameter to be stored
   **/
  function getKey(
    uint256 id,
    bytes32 param
  ) internal pure returns (bytes32 key) {
    key = keccak256(abi.encodePacked(id, param));
  }

  /**
   *                              ** DATA GETTERS **
   **/

  function readUint(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key
  ) internal view returns (uint256 data) {
    data = self.uintData[getKey(_id, _key)];
  }

  function readBytes(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key
  ) internal view returns (bytes memory data) {
    data = self.bytesData[getKey(_id, _key)];
  }

  function readAddress(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key
  ) internal view returns (address data) {
    data = self.addressData[getKey(_id, _key)];
  }

  /**
   *                              ** ARRAY GETTERS **
   **/

  function readUintArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _index
  ) internal view returns (uint256 data) {
    data = self.uintData[getKey(_index, getKey(_id, _key))];
  }

  function readBytesArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _index
  ) internal view returns (bytes memory data) {
    data = self.bytesData[getKey(_index, getKey(_id, _key))];
  }

  function readAddressArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _index
  ) internal view returns (address data) {
    data = self.addressData[getKey(_index, getKey(_id, _key))];
  }

  /**
   *                              ** DATA SETTERS **
   **/

  function writeUint(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _data
  ) internal {
    self.uintData[getKey(_id, _key)] = _data;
  }

  function addUint(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _addend
  ) internal {
    self.uintData[getKey(_id, _key)] += _addend;
  }

  function subUint(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _minuend
  ) internal {
    self.uintData[getKey(_id, _key)] -= _minuend;
  }

  function writeBytes(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    bytes memory _data
  ) internal {
    self.bytesData[getKey(_id, _key)] = _data;
  }

  function writeAddress(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    address _data
  ) internal {
    self.addressData[getKey(_id, _key)] = _data;
  }

  /**
   *                              ** ARRAY SETTERS **
   **/

  function appendUintArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256 _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    self.uintData[getKey(self.uintData[arrayKey]++, arrayKey)] = _data;
  }

  function appendBytesArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    bytes memory _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    self.bytesData[getKey(self.uintData[arrayKey]++, arrayKey)] = _data;
  }

  function appendAddressArray(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    address _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    self.addressData[getKey(self.uintData[arrayKey]++, arrayKey)] = _data;
  }

  /**
   *                              ** BATCH ARRAY SETTERS **
   **/

  function appendUintArrayBatch(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    uint256[] memory _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    uint256 arrayLen = self.uintData[arrayKey];
    for (uint256 i; i < _data.length; ) {
      self.uintData[getKey(arrayLen++, arrayKey)] = _data[i];
      unchecked {
        i += 1;
      }
    }
    self.uintData[arrayKey] = arrayLen;
  }

  function appendBytesArrayBatch(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    bytes[] memory _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    uint256 arrayLen = self.uintData[arrayKey];
    for (uint256 i; i < _data.length; ) {
      self.bytesData[getKey(arrayLen++, arrayKey)] = _data[i];
      unchecked {
        i += 1;
      }
    }
    self.uintData[arrayKey] = arrayLen;
  }

  function appendAddressArrayBatch(
    IsolatedStorage storage self,
    uint256 _id,
    bytes32 _key,
    address[] memory _data
  ) internal {
    bytes32 arrayKey = getKey(_id, _key);
    uint256 arrayLen = self.uintData[arrayKey];
    for (uint256 i; i < _data.length; ) {
      self.addressData[getKey(arrayLen++, arrayKey)] = _data[i];
      unchecked {
        i += 1;
      }
    }
    self.uintData[arrayKey] = arrayLen;
  }
}

