// SPDX-License-Identifier: MIT
pragma solidity =0.8.20;

// internal - interfaces
import {IDepositContract} from "../../../interfaces/helpers/IDepositContract.sol";
// internal - libraries
import {BytesLib} from "../../../helpers/BytesLib.sol";

/**
 * @title DCL: Deposit Contract Library
 *
 * @notice Includes constants & functions that helps to the deposit process.
 *
 * @dev DEPOSIT_CONTRACT is chain specific and should be changed before deployments.
 *
 * @dev This is an internal library, requires NO deployment.
 *
 * @author Ice Bear & Crash Bandicoot
 */

library DepositContractLib {
  /**
   * @custom:section                           ** CONSTANTS **
   */

  IDepositContract internal constant DEPOSIT_CONTRACT =
    IDepositContract(0x4242424242424242424242424242424242424242);
  uint256 internal constant PUBKEY_LENGTH = 48;
  uint256 internal constant SIGNATURE_LENGTH = 96;
  uint256 internal constant WITHDRAWAL_CREDENTIALS_LENGTH = 32;
  uint256 internal constant DEPOSIT_AMOUNT = 32 ether;
  uint256 internal constant DEPOSIT_AMOUNT_PRESTAKE = 1 ether;
  uint256 internal constant MAX_DEPOSITS_PER_CALL = 50;

  /**
   * @custom:section                           ** FUNCTIONS **
   */

  /**
   * @dev Padding memory array with zeroes up to 64 bytes on the right
   * @param _b Memory array of size 32 .. 64
   */
  function _pad64(bytes memory _b) internal pure returns (bytes memory) {
    assert(_b.length >= 32 && _b.length <= 64);
    if (64 == _b.length) return _b;

    bytes memory zero32 = new bytes(32);
    assembly {
      mstore(add(zero32, 0x20), 0)
    }

    if (32 == _b.length) return BytesLib.concat(_b, zero32);
    else return BytesLib.concat(_b, BytesLib.slice(zero32, 0, uint256(64 - _b.length)));
  }

  /**
   * @dev Converting value to little endian bytes and padding up to 32 bytes on the right
   * @param _value Number less than `2**64` for compatibility reasons
   */
  function _toLittleEndian64(uint256 _value) internal pure returns (uint256 result) {
    result = 0;
    uint256 temp_value = _value;
    for (uint256 i; i < 8; ++i) {
      result = (result << 8) | (temp_value & 0xFF);
      temp_value >>= 8;
    }

    assert(0 == temp_value); // fully converted
    result <<= (24 * 8);
  }

  function _getDepositDataRoot(
    bytes memory _pubkey,
    bytes memory _withdrawalCredentials,
    bytes memory _signature,
    uint256 _stakeAmount
  ) internal pure returns (bytes32) {
    require(_stakeAmount >= 1 ether, "DepositContract: deposit value too low");
    require(_stakeAmount % 1 gwei == 0, "DepositContract: deposit value not multiple of gwei");

    uint256 deposit_amount = _stakeAmount / 1 gwei;
    bytes32 pubkeyRoot = sha256(_pad64(_pubkey));
    bytes32 signatureRoot = sha256(
      abi.encodePacked(
        sha256(BytesLib.slice(_signature, 0, 64)),
        sha256(_pad64(BytesLib.slice(_signature, 64, SIGNATURE_LENGTH - 64)))
      )
    );

    bytes32 depositDataRoot = sha256(
      abi.encodePacked(
        sha256(abi.encodePacked(pubkeyRoot, _withdrawalCredentials)),
        sha256(abi.encodePacked(_toLittleEndian64(deposit_amount), signatureRoot))
      )
    );

    return depositDataRoot;
  }

  /**
   * @notice converts an address to withdrawal credential, used on validator creation
   */
  function addressToWC(address wcAddress) internal pure returns (bytes memory) {
    uint256 w = 1 << 248;

    return abi.encodePacked(bytes32(w) | bytes32(uint256(uint160(address(wcAddress)))));
  }

  /**
   * @notice deposit to DEPOSIT_CONTRACT and initiate a validator.
   */
  function depositValidator(
    bytes calldata pubkey,
    bytes memory withdrawalCredential,
    bytes memory signature,
    uint256 amount
  ) internal {
    DEPOSIT_CONTRACT.deposit{value: amount}(
      pubkey,
      withdrawalCredential,
      signature,
      _getDepositDataRoot(pubkey, withdrawalCredential, signature, amount)
    );
  }
}

contracts/modules/StakeModule/libs/Initi.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity =0.8.20;

// external - contracts
import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";
import {ERC1967Proxy} from "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
// internal - globals
import {ID_TYPE} from "../../../globals/id_type.sol";
import {RESERVED_KEY_SPACE as rks} from "../../../globals/reserved_key_space.sol";
// internal - interfaces
import {IgETHMiddleware} from "../../../interfaces/middlewares/IgETHMiddleware.sol";
import {IGeodePackage} from "../../../interfaces/packages/IGeodePackage.sol";
// internal - structs
import {DataStoreModuleStorage} from "../../DataStoreModule/structs/storage.sol";
import {StakeModuleStorage} from "../structs/storage.sol";
// internal - libraries
import {DataStoreModuleLib as DSML} from "../../DataStoreModule/libs/DataStoreModuleLib.sol";
import {DepositContractLib as DCL} from "./DepositContractLib.sol";
import {StakeModuleLib as SML} from "./StakeModuleLib.sol";

/**
 * @title IEL: Initiator Extension Library
 *
 * @notice An extension to SML.
 * @notice This library is responsible from:
 * * 1. Node Operator Initiator for permissioned IDs
 * * 2. Configurable Staking Pools Initiator and its helpers.
 * * 3. Bound Liquidity Pool deployment after pool initiation.
 *
 * @dev review: DataStoreModule for the id based isolated storage logic.
 * @dev review: StakeModuleLib for base staking logic.
 *
 * @dev This library utilizes the '_authenticate' function on the external deployLiquidityPool,
 *  Compared to gETHMiddleware(optional) and WithdrawalContract(mandatory), LP be activated after
 * the pool initiation.
 *
 * @dev This is an external library, requires deployment.
 *
 * @author Ice Bear & Crash Bandicoot
 */

library InitiatorExtensionLib {
  using DSML for DataStoreModuleStorage;
  using SML for StakeModuleStorage;

  /**
   * @custom:section                           ** EVENTS **
   */
  event InitiationDepositSet(uint256 initiationDeposit);
  event IdInitiated(uint256 id, uint256 indexed TYPE);
  event MiddlewareDeployed(uint256 poolId, uint256 version);
  event PackageDeployed(uint256 poolId, uint256 packageType, address instance);

  /**
   * @custom:section                           ** GOVERNING **
   *
   * @custom:visibility -> external
   * @dev IMPORTANT! These functions should be governed by a governance! Which is not done here!
   */

  /**
   * @notice Set the required amount for a pool initiation.
   * @dev note that, could have been used to prevent pool creation if there were no limits.
   */
  function setInitiationDeposit(
    StakeModuleStorage storage self,
    uint256 initiationDeposit
  ) external {
    require(initiationDeposit <= DCL.DEPOSIT_AMOUNT);

    self.INITIATION_DEPOSIT = initiationDeposit;

    emit InitiationDepositSet(initiationDeposit);
  }

  /**
   * @custom:section                           ** OPERATOR INITIATOR **
   *
   * @custom:visibility -> external
   */

  /**
   * @notice initiates ID as a Permissionned Node Operator
   * @notice requires ID to be approved as a node operator with a specific CONTROLLER
   * @param fee as a percentage limited by MAX_MAINTENANCE_FEE, PERCENTAGE_DENOMINATOR represents 100%
   * @param validatorPeriod the expected maximum staking interval. This value should between
   * * MIN_VALIDATOR_PERIOD and MAX_VALIDATOR_PERIOD values defined as constants above.
   * Operator can unstake at any given point before this period ends.
   * If operator disobeys this rule, it can be prisoned with blameProposal()
   * @param maintainer an address that automates daily operations, a script, a contract...
   * @dev operators can fund their internal wallet on initiation by simply sending some ether.
   */
  function initiateOperator(
    DataStoreModuleStorage storage DATASTORE,
    uint256 id,
    uint256 fee,
    uint256 validatorPeriod,
    address maintainer
  ) external {
    require(DATASTORE.readUint(id, rks.initiated) == 0, "SML:already initiated");
    require(DATASTORE.readUint(id, rks.TYPE) == ID_TYPE.OPERATOR, "SML:TYPE not allowed");
    require(msg.sender == DATASTORE.readAddress(id, rks.CONTROLLER), "SML:sender not CONTROLLER");

    DATASTORE.writeUint(id, rks.initiated, block.timestamp);

    SML._setMaintenanceFee(DATASTORE, id, fee);
    SML._setValidatorPeriod(DATASTORE, id, validatorPeriod);
    SML._setMaintainer(DATASTORE, id, maintainer);
    SML._increaseWalletBalance(DATASTORE, id, msg.value);

    emit IdInitiated(id, ID_TYPE.OPERATOR);
  }

  /**
   * @custom:section                           ** STAKING POOL INITIATOR **
   *
   * @dev this section also contains the helper functions for packages and middlewares.
   */

  /**
   * @notice Creates a Configurable Trustless Staking Pool!
   * @param fee as a percentage limited by MAX_MAINTENANCE_FEE, PERCENTAGE_DENOMINATOR is 100%
   * @param middlewareVersion Pool creators can choose any allowed version as their gETHMiddleware
   * @param maintainer an address that automates daily operations, a script, a contract... not so critical.
   * @param name is utilized while generating an ID for the Pool, similar to any other ID generation.
   * @param middleware_data middlewares might require additional data on initialization; like name, symbol, etc.
   * @param config array(3)= [private(true) or public(false), deploy a middleware(if true), deploy liquidity pool(if true)]
   * @dev checking only initiated is enough to validate that ID is not used. no need to check TYPE, CONTROLLER etc.
   * @dev requires INITIATION_DEPOSIT worth of funds (currently 1 validator) to be deposited on initiation, prevent sybil attacks.
   */
  function initiatePool(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 fee,
    uint256 middlewareVersion,
    address maintainer,
    bytes calldata name,
    bytes calldata middleware_data,
    bool[3] calldata config
  ) external returns (uint256 poolId) {
    require(msg.value == self.INITIATION_DEPOSIT, "SML:need 1 validator worth of funds");

    poolId = DSML.generateId(name, ID_TYPE.POOL);
    require(DATASTORE.readUint(poolId, rks.initiated) == 0, "SML:already initiated");
    require(poolId > 1e9, "SML:Wow! Low poolId");

    DATASTORE.writeUint(poolId, rks.initiated, block.timestamp);

    DATASTORE.writeUint(poolId, rks.TYPE, ID_TYPE.POOL);
    DATASTORE.writeAddress(poolId, rks.CONTROLLER, msg.sender);
    DATASTORE.writeBytes(poolId, rks.NAME, name);
    DATASTORE.allIdsByType[ID_TYPE.POOL].push(poolId);

    SML._setMaintainer(DATASTORE, poolId, maintainer);
    SML._setMaintenanceFee(DATASTORE, poolId, fee);

    // deploy a withdrawal Contract - mandatory
    _deployWithdrawalContract(self, DATASTORE, poolId);

    if (config[0]) {
      // set pool to private
      SML.setPoolVisibility(DATASTORE, poolId, true);
    }
    if (config[1]) {
      // deploy a gETH middleware(erc20 etc.) - optional
      _deploygETHMiddleware(self, DATASTORE, poolId, middlewareVersion, middleware_data);
    }
    if (config[2]) {
      // deploy a bound liquidity pool - optional
      _deployLiquidityPool(self, DATASTORE, poolId);
    }

    // initially 1 ETHER = 1 ETHER
    self.gETH.setPricePerShare(1 ether, poolId);

    // mint gETH and send back to the caller
    uint256 mintedgETH = SML._mintgETH(self, DATASTORE, poolId, msg.value);
    self.gETH.safeTransferFrom(address(this), msg.sender, poolId, mintedgETH, "");

    emit IdInitiated(poolId, ID_TYPE.POOL);
  }

  /**
   * @custom:section                           ** POOL INITIATOR HELPERS **
   */

  /**
   * @custom:subsection                        ** gETH MIDDLEWARES **
   *
   * @custom:visibility -> internal
   */

  /**
   * @notice internal function to set a gETHMiddleware
   * @param _middleware address of the new gETHMiddleware for given ID
   * @dev every middleware has a unique index within the middlewares dynamic array.
   * @dev if ever unset, SHOULD replace the implementation with address(0) for obvious security reasons.
   */
  function _setgETHMiddleware(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 id,
    address _middleware
  ) internal {
    require(!self.gETH.isMiddleware(_middleware, id), "SML:already middleware");

    DATASTORE.appendAddressArray(id, rks.middlewares, _middleware);

    self.gETH.setMiddleware(_middleware, id, true);
  }

  /**
   * @notice deploys a new gETHMiddleware by cloning (no upgradability)
   * @param _id gETH id, also required for IgETHMiddleware.initialize
   * @param _versionId provided version id, can use any as a middleware if allowed for TYPE = MIDDLEWARE_GETH
   * @param _middleware_data middlewares might require additional data on initialization; like name, symbol, etc.
   * @dev currrently, cannot deploy a middleware after initiation, thus only used by the initiator.
   * @dev currrently, cannot unset a middleware.
   */
  function _deploygETHMiddleware(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 _id,
    uint256 _versionId,
    bytes calldata _middleware_data
  ) internal {
    require(_versionId > 0, "SML:versionId cannot be 0");
    require(self.middlewares[ID_TYPE.MIDDLEWARE_GETH][_versionId], "SML:not a middleware");

    address newgETHMiddleware = Clones.clone(DATASTORE.readAddress(_versionId, rks.CONTROLLER));

    IgETHMiddleware(newgETHMiddleware).initialize(_id, address(self.gETH), _middleware_data);

    _setgETHMiddleware(self, DATASTORE, _id, newgETHMiddleware);

    // isolate the contract from middleware risk for ID
    self.gETH.avoidMiddlewares(_id, true);

    emit MiddlewareDeployed(_id, _versionId);
  }

  /**
   * @notice deploys a new package for given id with given type from packages mapping.
   * @param _type given package type
   * @param _poolId pool id, required for IGeodePackage.initialize
   * @param _package_data packages might require additional data on initialization
   * @dev no cloning because GeodePackages has Limited Upgradability (based on UUPS)
   */
  function _deployGeodePackage(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 _poolId,
    uint256 _type,
    bytes memory _package_data
  ) internal returns (address packageInstance) {
    uint256 versionId = self.packages[_type];
    require(versionId > 0, "SML:versionId cannot be 0");

    packageInstance = address(
      new ERC1967Proxy(DATASTORE.readAddress(versionId, rks.CONTROLLER), "")
    );
    // we don't call on deployment because initialize uses _getImplementation() which is not available
    IGeodePackage(packageInstance).initialize(
      _poolId,
      DATASTORE.readAddress(_poolId, rks.CONTROLLER),
      DATASTORE.readBytes(versionId, rks.NAME),
      _package_data
    );

    emit PackageDeployed(_poolId, _type, packageInstance);
  }

  /**
   * @custom:subsection                        ** WITHDRAWAL CONTRACT **
   *
   * @custom:visibility -> internal
   */

  /**
   * @notice Deploys a Withdrawal Contract that will be used as a withdrawal credential on validator creation
   * @dev every pool requires a Withdrawal Contract, thus this function is only used by the initiator
   */
  function _deployWithdrawalContract(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 _poolId
  ) internal {
    address wp = _deployGeodePackage(
      self,
      DATASTORE,
      _poolId,
      ID_TYPE.PACKAGE_WITHDRAWAL_CONTRACT,
      bytes("")
    );

    DATASTORE.writeAddress(_poolId, rks.withdrawalContract, wp);
    DATASTORE.writeBytes(_poolId, rks.withdrawalCredential, DCL.addressToWC(wp));
  }

  /**
   * @custom:subsection                        ** BOUND LIQUIDITY POOL **
   */

  /**
   * @custom:visibility -> internal
   */
  /**
   * @notice deploys a bound liquidity pool for a staking pool.
   * @dev gives full allowance to the pool (should not be a problem as Portal only temporarily holds gETH)
   * @dev unlike withdrawal Contract, a controller can deploy a liquidity pool after initiation as well
   * @dev _package_data of a liquidity pool is only the staking pool's name, used on LPToken.
   */
  function _deployLiquidityPool(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId
  ) internal {
    address lp = _deployGeodePackage(
      self,
      DATASTORE,
      poolId,
      ID_TYPE.PACKAGE_LIQUIDITY_POOL,
      DATASTORE.readBytes(poolId, rks.NAME)
    );

    DATASTORE.writeAddress(poolId, rks.liquidityPool, lp);
    // approve gETH so we can use it in buybacks
    self.gETH.setApprovalForAll(lp, true);
  }

  /**
   * @custom:visibility -> external
   */
  /**
   * @notice allows pools to deploy a Liquidity Pool after initiation, if it does not have one.
   */
  function deployLiquidityPool(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId
  ) public {
    SML._authenticate(DATASTORE, poolId, true, false, [false, true]);
    require(DATASTORE.readAddress(poolId, rks.liquidityPool) == address(0), "SML:already deployed");

    _deployLiquidityPool(self, DATASTORE, poolId);
  }
}

contracts/modules/StakeModule/libs/Oracl.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity =0.8.20;

// external - libraries
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
// internal - globals
import {PERCENTAGE_DENOMINATOR, gETH_DENOMINATOR} from "../../../globals/macros.sol";
import {RESERVED_KEY_SPACE as rks} from "../../../globals/reserved_key_space.sol";
import {ID_TYPE} from "../../../globals/id_type.sol";
import {VALIDATOR_STATE} from "../../../globals/validator_state.sol";
// internal - structs
import {DataStoreModuleStorage} from "../../DataStoreModule/structs/storage.sol";
import {StakeModuleStorage} from "../structs/storage.sol";
// internal - libraries
import {DataStoreModuleLib as DSML} from "../../DataStoreModule/libs/DataStoreModuleLib.sol";
import {DepositContractLib as DCL} from "./DepositContractLib.sol";
import {StakeModuleLib as SML} from "./StakeModuleLib.sol";

/**
 * @title OEL: Oracle Extension Library
 *
 * @notice An extension to SML
 * @notice Oracle, named Telescope, handles some operations for The Staking Library,
 * * using the logic explained below.
 *
 * @dev review: DataStoreModule for the id based isolated storage logic.
 * @dev review: StakeModuleLib for base staking logic.
 *
 * @dev Telescope is currently responsible for 4 tasks:
 * * Updating the on-chain price of all pools with a MerkleRoot for minting operations
 * * Updating the on-chain balances info of all validators with a MerkleRoot for withdrawal operations
 * * Confirming validator proposals
 * * Regulating the Node Operators
 *
 * 1. updateVerificationIndex: Confirming validator proposals
 * * 2 step process is essential to prevent the frontrunning with a problematic withdrawalCredential: https://bit.ly/3Tkc6UC
 * * Simply, all proposed validator has an index bound to them,
 * * n representing the latest proposal: (0,n]
 * * Telescope verifies the validator data provided in proposeStake:
 * * especially sig1, sig31 and withdrawal credentials.
 * * Telescope confirms the latest index verified and states the faulty validator proposals (aliens)
 * * If a validator proposal is faulty then it's state is set to 69, refer to globals/validator_state.sol
 *
 * 2. regulateOperators: Regulating the Operators
 * * Operators can act faulty in many different ways. To prevent such actions,
 * * Telescope regulates them with well defined limitations.
 * * Currently only issue is the fee theft, meaning operators have not
 * * used the withdrawal contract for miner fees or MEV boost.
 * * There can be other restrictions in the future.
 *
 * 2. reportBeacon: Continous Data from Beacon chain: Price Merkle Root & Balances Merkle Root & # of active validators
 * * 1. Oracle Nodes calculate the price of its derivative, according to the validator data such as balance and fees.
 * * 2. If a pool doesn't have a validator, the price is kept the same.
 * * 3. A merkle tree is constructed with the order of allIdsByType array.
 * * 4. A watcher collects all the signatures from Multiple Oracle Nodes, and submits the merkle root.
 * * 5. Anyone can update the price of the derivative  by calling priceSync() functions with correct merkle proofs
 * * 6. Minting is allowed within PRICE_EXPIRY (24H) after the last price update.
 * * 7. Updates the regulation around Monopolies and provides BALANCE_MERKLE_ROOT to be used within withdrawal process.
 *
 * @dev Most external functions have OracleOnly modifier. Except: priceSync, priceSyncBatch, blameExit and blameProposal.
 *
 * @dev This is an external library, requires deployment.
 *
 * @author Ice Bear & Crash Bandicoot
 */

library OracleExtensionLib {
  using DSML for DataStoreModuleStorage;
  using SML for StakeModuleStorage;

  /**
   * @custom:section                           ** CONSTANTS **
   */
  /// @notice effective on MONOPOLY_THRESHOLD, limiting the active validators: Set to 1%
  uint256 internal constant MONOPOLY_RATIO = 1e8; // PERCENTAGE_DENOMINATOR / 100;

  /// @notice sensible value for the minimum beacon chain validators. No reasoning.
  uint256 internal constant MIN_VALIDATOR_COUNT = 50000;

  /// @notice limiting the access for Operators in case of bad/malicious/faulty behaviour
  uint256 internal constant PRISON_SENTENCE = 14 days;

  /**
   * @custom:section                           ** EVENTS **
   */
  event Alienated(bytes pubkey);
  event VerificationIndexUpdated(uint256 validatorVerificationIndex);
  event FeeTheft(uint256 indexed id, bytes proofs);
  event Prisoned(uint256 indexed operatorId, bytes proof, uint256 releaseTimestamp);
  event YieldDistributed(uint256 indexed poolId, uint256 amount);
  event OracleReported(
    bytes32 priceMerkleRoot,
    bytes32 balanceMerkleRoot,
    uint256 monopolyThreshold
  );

  /**
   * @custom:section                           ** MODIFIERS **
   */
  modifier onlyOracle(StakeModuleStorage storage self) {
    require(msg.sender == self.ORACLE_POSITION, "OEL:sender not ORACLE");
    _;
  }

  /**
   * @custom:section                                       ** VERIFICATION INDEX **
   */

  /**
   * @custom:visibility -> internal
   *
   * @notice "Alien" is a validator that is created with a faulty withdrawal
   * credential or signatures, this is a malicious act.
   * @notice Alienation results in imprisonment for the operator of the faulty validator proposal.
   * @dev While alienating a validator we should adjust the 'surplus' and 'secured'
   * balances of the pool accordingly
   * @dev We should adjust the 'proposedValidators' to fix allowances.
   */
  function _alienateValidator(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 verificationIndex,
    bytes calldata _pk
  ) internal {
    require(self.validators[_pk].index <= verificationIndex, "OEL:unexpected index");
    require(
      self.validators[_pk].state == VALIDATOR_STATE.PROPOSED,
      "OEL:not all pubkeys are pending"
    );

    uint256 operatorId = self.validators[_pk].operatorId;
    _imprison(DATASTORE, operatorId, _pk);

    uint256 poolId = self.validators[_pk].poolId;
    DATASTORE.subUint(poolId, rks.secured, DCL.DEPOSIT_AMOUNT);
    DATASTORE.addUint(poolId, rks.surplus, DCL.DEPOSIT_AMOUNT);

    DATASTORE.subUint(poolId, DSML.getKey(operatorId, rks.proposedValidators), 1);
    DATASTORE.addUint(poolId, DSML.getKey(operatorId, rks.alienValidators), 1);

    self.validators[_pk].state = VALIDATOR_STATE.ALIENATED;

    emit Alienated(_pk);
  }

  /**
   * @custom:visibility -> external
   *
   * @notice Updating VERIFICATION_INDEX, signaling that it is safe to activate
   * the validator proposals with lower index than new VERIFICATION_INDEX
   * @param validatorVerificationIndex (inclusive) index of the highest validator that is verified to be activated
   * @param alienatedPubkeys faulty proposals within the range of new and old verification indexes.
   */
  function updateVerificationIndex(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 validatorVerificationIndex,
    bytes[] calldata alienatedPubkeys
  ) external onlyOracle(self) {
    require(self.VALIDATORS_INDEX >= validatorVerificationIndex, "OEL:high VERIFICATION_INDEX");
    require(validatorVerificationIndex > self.VERIFICATION_INDEX, "OEL:low VERIFICATION_INDEX");

    uint256 alienatedPubkeysLen = alienatedPubkeys.length;
    for (uint256 i; i < alienatedPubkeysLen; ) {
      _alienateValidator(self, DATASTORE, validatorVerificationIndex, alienatedPubkeys[i]);

      unchecked {
        i += 1;
      }
    }

    self.VERIFICATION_INDEX = validatorVerificationIndex;
    emit VerificationIndexUpdated(validatorVerificationIndex);
  }

  /**
   * @dev                                       ** REGULATING OPERATORS **
   */

  /**
   * @custom:section                           ** PRISON **
   *
   * When node operators act in a malicious way, which can also be interpreted as
   * an honest mistake like using a faulty signature, Oracle imprisons the operator.
   * These conditions are:
   * * 1. Created a malicious validator(alien): faulty withdrawal credential, faulty signatures etc.
   * * 2. Have not respect the validatorPeriod (or blamed for some other valid case)
   * * 3. Stole block fees or MEV boost rewards from the pool
   *
   * @dev this section lacks a potential punishable act, for now early exits are not enforced:
   * While state is EXIT_REQUESTED: validator requested exit, but it hasn't been executed.
   */

  /**
   * @custom:visibility -> internal
   */

  /**
   * @notice Put an operator in prison
   * @dev rks.release key refers to the end of the last imprisonment, when the limitations of operator is lifted
   */
  function _imprison(
    DataStoreModuleStorage storage DATASTORE,
    uint256 _operatorId,
    bytes calldata _proof
  ) internal {
    SML._authenticate(DATASTORE, _operatorId, false, false, [true, false]);

    DATASTORE.writeUint(_operatorId, rks.release, block.timestamp + PRISON_SENTENCE);

    emit Prisoned(_operatorId, _proof, block.timestamp + PRISON_SENTENCE);
  }

  /**
   * @custom:visibility -> external
   */

  /**
   * @notice imprisoning an Operator if the validator proposal is approved but have not been executed.
   * @dev anyone can call this function while the state is PROPOSED
   * @dev this check can be problematic in the case the beaconchain deposit delay is > BEACON_DELAY_ENTRY,
   * * depending on the expected delay of telescope approvals. However, BEACON_DELAY_ENTRY can be adjusted by the Governance.
   * @dev _canStake checks == VALIDATOR_STATE.PROPOSED.
   */
  function blameProposal(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    bytes calldata pk
  ) external {
    uint256 verificationIndex = self.VERIFICATION_INDEX;
    require(self._canStake(pk, verificationIndex), "OEL:cannot blame proposal");
    require(
      block.timestamp > self.validators[pk].createdAt + self.BEACON_DELAY_ENTRY,
      "OEL:acceptable delay"
    );

    _alienateValidator(self, DATASTORE, verificationIndex, pk);
  }

  /**
   * @notice imprisoning an Operator if the validator have not been exited until expected exit
   * @dev normally, oracle should verify the signed exit request on beacon chain for a (deterministic) epoch
   * * before approval. This function enforces it further for the stakers.
   * @dev anyone can call this function while the state is ACTIVE or EXIT_REQUESTED
   * @dev if operator has given enough allowance, they SHOULD rotate the validators to avoid being prisoned
   */
  function blameExit(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    bytes calldata pk,
    uint256 beaconBalance,
    uint256 withdrawnBalance,
    bytes32[] calldata balanceProof
  ) external {
    uint64 state = self.validators[pk].state;
    require(
      state == VALIDATOR_STATE.ACTIVE || state == VALIDATOR_STATE.EXIT_REQUESTED,
      "OEL:unexpected validator state"
    );
    require(
      block.timestamp >
        self.validators[pk].createdAt + self.validators[pk].period + self.BEACON_DELAY_EXIT,
      "OEL:validator is active or acceptable delay"
    );

    // verify balances
    bytes32 leaf = keccak256(
      bytes.concat(keccak256(abi.encode(pk, beaconBalance, withdrawnBalance)))
    );
    require(
      MerkleProof.verify(balanceProof, self.BALANCE_MERKLE_ROOT, leaf),
      "OEL:proof not valid"
    );

    require(beaconBalance != 0, "OEL:already exited");

    _imprison(DATASTORE, self.validators[pk].operatorId, pk);
  }

  /**

   */

  /**
   * @custom:visibility -> external
   */

  /**
   * @notice regulating operators, currently only regulation is towards fee theft, can add more stuff in the future.
   * @param feeThefts Operator ids who have stolen MEV or block rewards detected
   * @param proofs  BlockNumber, tx or any other referance as a proof
   * @dev Stuff here result in imprisonment
   */
  function regulateOperators(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256[] calldata feeThefts,
    bytes[] calldata proofs
  ) external onlyOracle(self) {
    require(feeThefts.length == proofs.length, "OEL:invalid proofs");

    uint256 feeTheftsLen = feeThefts.length;
    for (uint256 i; i < feeTheftsLen; ) {
      _imprison(DATASTORE, feeThefts[i], proofs[i]);

      emit FeeTheft(feeThefts[i], proofs[i]);

      unchecked {
        i += 1;
      }
    }
  }

  /**
   * @custom:section                           ** CONTINUOUS UPDATES **
   *
   * @custom:visibility -> external
   */

  /**
   * @notice Telescope reports all of the g-derivate prices with a new PRICE_MERKLE_ROOT.
   * Also reports all of the validator balances with a BALANCE_MERKLE_ROOT.
   * Then, updates the ORACLE_UPDATE_TIMESTAMP and MONOPOLY_THRESHOLD
   *
   * @param allValidatorsCount Number of all validators within BeaconChain, all of them.
   * Prevents monopolies.
   */
  function reportBeacon(
    StakeModuleStorage storage self,
    bytes32 priceMerkleRoot,
    bytes32 balanceMerkleRoot,
    uint256 allValidatorsCount
  ) external onlyOracle(self) {
    require(allValidatorsCount > MIN_VALIDATOR_COUNT, "OEL:low validator count");

    self.PRICE_MERKLE_ROOT = priceMerkleRoot;
    self.BALANCE_MERKLE_ROOT = balanceMerkleRoot;
    self.ORACLE_UPDATE_TIMESTAMP = block.timestamp;

    uint256 newThreshold = (allValidatorsCount * MONOPOLY_RATIO) / PERCENTAGE_DENOMINATOR;
    self.MONOPOLY_THRESHOLD = newThreshold;

    emit OracleReported(priceMerkleRoot, balanceMerkleRoot, newThreshold);
  }

  /**
   * @custom:section                           **  PRICE UPDATE **
   *
   * @dev Permissionless.
   */

  /**
   * @custom:visibility -> view-internal
   *
   * @dev in order to prevent faulty updates to the derivative prices there are boundaries to price updates.
   * 1. Price should not be increased more than DAILY_PRICE_INCREASE_LIMIT
   *  with the factor of how many days since priceUpdateTimestamp has past.
   * 2. Price should not be decreased more than DAILY_PRICE_DECREASE_LIMIT
   *  with the factor of how many days since priceUpdateTimestamp has past.
   *
   * @dev Worth noting, if price drops more than x%, UP TO (slashing percentage/x) days deposits/withdrawals are halted.
   * Example:
   * * A pool can have only one validator, it can get slashed.
   * * Lets say max decrease is 5%, and 50% is slashed.
   * * Then deposits/withdrawals are halted for 10 days.
   * This is not a bug, but a safe circuit-breaker.
   * This logic have effects the withdrawal contract logic.
   */
  function _sanityCheck(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 _id,
    uint256 _newPrice
  ) internal view {
    require(DATASTORE.readUint(_id, rks.TYPE) == ID_TYPE.POOL, "OEL:not a pool?");

    uint256 lastUpdate = self.gETH.priceUpdateTimestamp(_id);
    uint256 dayPercentSinceUpdate = ((block.timestamp - lastUpdate) * PERCENTAGE_DENOMINATOR) /
      1 days;

    uint256 curPrice = self.gETH.pricePerShare(_id);

    uint256 maxPriceIncrease = ((curPrice *
      self.DAILY_PRICE_INCREASE_LIMIT *
      dayPercentSinceUpdate) / PERCENTAGE_DENOMINATOR) / PERCENTAGE_DENOMINATOR;

    uint256 maxPriceDecrease = ((curPrice *
      self.DAILY_PRICE_DECREASE_LIMIT *
      dayPercentSinceUpdate) / PERCENTAGE_DENOMINATOR) / PERCENTAGE_DENOMINATOR;

    require(
      (_newPrice + maxPriceDecrease >= curPrice) && (_newPrice <= curPrice + maxPriceIncrease),
      "OEL:price is insane, price update is halted"
    );
  }

  /**
   * @custom:visibility -> internal
   *
   * @notice syncing the price of g-derivatives after checking the merkle proofs and the sanity of the price.
   * @param _price price of the derivative denominated in gETH.denominator()
   * @param _priceProof merkle proofs
   */
  function _priceSync(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 _poolId,
    uint256 _price,
    bytes32[] calldata _priceProof
  ) internal {
    require(
      self.ORACLE_UPDATE_TIMESTAMP > self.gETH.priceUpdateTimestamp(_poolId),
      "OEL:no price change"
    );

    bytes32 leaf = keccak256(bytes.concat(keccak256(abi.encode(_poolId, _price))));
    require(
      MerkleProof.verify(_priceProof, self.PRICE_MERKLE_ROOT, leaf),
      "OEL:not all proofs are valid"
    );

    _sanityCheck(self, DATASTORE, _poolId, _price);

    address yieldReceiver = DATASTORE.readAddress(_poolId, rks.yieldReceiver);

    if (yieldReceiver == address(0)) {
      self.gETH.setPricePerShare(_price, _poolId);
    } else {
      uint256 currentPrice = self.gETH.pricePerShare(_poolId);
      if (_price > currentPrice) {
        uint256 supplyDiff = ((_price - currentPrice) * self.gETH.totalSupply(_poolId)) /
          gETH_DENOMINATOR;
        self.gETH.mint(address(this), _poolId, supplyDiff, "");
        self.gETH.safeTransferFrom(address(this), yieldReceiver, _poolId, supplyDiff, "");
        emit YieldDistributed(_poolId, supplyDiff);
      } else {
        self.gETH.setPricePerShare(_price, _poolId);
      }
    }
  }

  /**
   * @custom:visibility -> external
   *
   * @notice external function to set a derivative price on Portal
   * @param price price of the derivative denominated in gETH.denominator()
   * @param priceProof merkle proofs
   */
  function priceSync(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    uint256 price,
    bytes32[] calldata priceProof
  ) external {
    _priceSync(self, DATASTORE, poolId, price, priceProof);
  }

  /**
   * @custom:visibility -> external
   *
   * @notice external function to set a multiple derivatives price at once, saves gas.
   * @param prices price of the derivative denominated in gETH.denominator()
   * @param priceProofs merkle proofs
   */
  function priceSyncBatch(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256[] calldata poolIds,
    uint256[] calldata prices,
    bytes32[][] calldata priceProofs
  ) external {
    require(poolIds.length == prices.length, "OEL:array lengths not equal");
    require(poolIds.length == priceProofs.length, "OEL:array lengths not equal");

    uint256 poolIdsLen = poolIds.length;
    for (uint256 i; i < poolIdsLen; ) {
      _priceSync(self, DATASTORE, poolIds[i], prices[i], priceProofs[i]);

      unchecked {
        i += 1;
      }
    }
  }
}

contracts/modules/StakeModule/libs/Stake.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity =0.8.20;

// internal - globals
import {ID_TYPE} from "../../../globals/id_type.sol";
import {PERCENTAGE_DENOMINATOR, gETH_DENOMINATOR} from "../../../globals/macros.sol";
import {VALIDATOR_STATE} from "../../../globals/validator_state.sol";
import {RESERVED_KEY_SPACE as rks} from "../../../globals/reserved_key_space.sol";
// internal - interfaces
import {IgETH} from "../../../interfaces/IgETH.sol";
import {IGeodePackage} from "../../../interfaces/packages/IGeodePackage.sol";
import {ILiquidityPool} from "../../../interfaces/packages/ILiquidityPool.sol";
import {IWhitelist} from "../../../interfaces/helpers/IWhitelist.sol";
// internal - structs
import {DataStoreModuleStorage} from "../../DataStoreModule/structs/storage.sol";
import {StakeModuleStorage} from "../structs/storage.sol";
import {ConstantValidatorData} from "../structs/helpers.sol";
import {Validator} from "../structs/utils.sol";
// internal - libraries
import {DataStoreModuleLib as DSML} from "../../DataStoreModule/libs/DataStoreModuleLib.sol";
import {DepositContractLib as DCL} from "./DepositContractLib.sol";

/**
 * @title SML: Stake Module Library (The Staking Library)
 *
 * @notice Creating a global standard for Staking, allowing anyone to OWN a trustless staking pool,
 * improving the user experience for stakers and removing the "need" for centralized or decentralized intermediaries.
 * * Exclusively contains functions related to:
 * * 1. Pool and Operator management, after initiation (review IEL).
 * * 2. Validator Delegation.
 * * 3. Depositing.
 * * 4. Staking Operations.
 *
 * @dev review: DataStoreModule for the id based isolated storage logic.
 * @dev review: InitiatorExtensionLib for initiator functions.
 * @dev review: OracleExtensionLib for oracle logic.
 *
 * @dev Every pool is isolated and every validator is unique. We segregate all the risk.
 *
 * @dev CONTROLLER and Maintainer:
 * CONTROLLER is the owner of an ID, it manages the pool/operator. Its security is exteremely important.
 * maintainer is the worker, can be used to automate some daily tasks:
 * * distributing validators for Staking Pools or creating validators for Operators.
 * * not so crucial in terms of security.
 *
 * @dev Users:
 * Type 4 : Permissioned Operators
 * * Needs to be onboarded by the Dual Governance (Senate + Governance).
 * * Maintains Beacon Chain Validators on behalf of the Staking Pools.
 * * Can participate in the Operator Marketplace after initiation.
 * * Can utilize maintainers for staking operations.
 *
 * Type 5 : Permissionless Configurable Staking Pools
 * * Permissionless to create.
 * * Can utilize powers of packages and middlewares such as Bound Liquidity Pools, gETHMiddlewares etc.
 * * Can be public or private, can use a whitelist if private.
 * * Can utilize maintainers for validator distribution on Operator Marketplace.
 * * Uses a Withdrawal Contract to be given as withdrawalCredential on validator creation,
 * * accruing rewards and keeping Staked Ether safe and isolated.
 *
 * @dev Packages:
 * An ID can only point to one version of a Package at a time.
 * Built by utilizing the Modules!
 * Can be upgraded by a dual governance, via pullUpgrade.
 * * A Package's dual governance consists of Portal(governance) and the pool owner(senate).
 *
 * Type 10011 : Withdrawal Contract
 * * Mandatory.
 * * CONTROLLER is the implementation contract position (always)
 * * Version Release Requires the approval of Senate
 * * Upgrading to a new version is optional for pool owners.
 * * * Staking Pools are in "Isolation Mode" until their Withdrawal Contract is upgraded.
 * * * Meaning, no more Depositing or Validator Proposal can happen.
 * * Custodian of the validator funds after creation, including any type of rewards and fees.
 *
 * Type 10021 : Liquidity Pool implementation
 * * Optional.
 * * CONTROLLER is the implementation contract position (always)
 * * Version Release Requires the approval of Senate
 * * Upgrading to a new version is optional for pool owners.
 * * * Liquidity Pools are in "Isolation Mode" until upgraded.
 *
 * @dev Middlewares:
 * Can support many different versions that can be utilized by the Pool Owners.
 * No particular way to build one.
 * Cannot be upgraded.
 * Currently only gETHMiddlewares.
 *
 * Type 20011 : gETHMiddleware
 * * Optional.
 * * CONTROLLER is the implementation contract position (always)
 * * Requires the approval of Senate
 * * Currently should be utilized on initiation.
 *
 * @dev Contracts relying on this library must initialize StakeModuleLib.StakeModuleStorage
 *
 * @dev Functions are protected with authentication function
 *
 * @author Ice Bear & Crash Bandicoot
 */

library StakeModuleLib {
  using DSML for DataStoreModuleStorage;

  /**
   * @custom:section                           ** CONSTANTS **
   */

  /// @notice limiting the GOVERNANCE_FEE to 5%
  uint256 internal constant MAX_POOL_INFRASTRUCTURE_FEE = 5e8; // (PERCENTAGE_DENOMINATOR * 5) / 100;

  /// @notice limit the beacon delays on entry and exit since it can be adjusted by the governance.
  uint256 internal constant MAX_BEACON_DELAY = 90 days; // = MIN_VALIDATOR_PERIOD

  /// @notice limiting the pool and operator maintenance fee, 10%
  uint256 internal constant MAX_MAINTENANCE_FEE = 1e9; // (PERCENTAGE_DENOMINATOR * 10) / 100;

  /// @notice effective on allowance per operator, prevents overflow. Exclusive, save gas with +1.
  uint256 internal constant MAX_ALLOWANCE = 1e6;

  /// @notice price of gETH is only valid for 24H, minting is not allowed afterwards.
  uint256 internal constant PRICE_EXPIRY = 24 hours;

  /// @notice ignoring any buybacks if the Liquidity Pool has a low debt
  uint256 internal constant IGNORABLE_DEBT = 1 ether;

  /// @notice limiting the operator.validatorPeriod, between 3 months to 2 years
  uint256 internal constant MIN_VALIDATOR_PERIOD = 90 days; // 3 * 30 days
  uint256 internal constant MAX_VALIDATOR_PERIOD = 730 days; // 2 * 365 days

  /// @notice some parameter changes are effective after a delay
  uint256 internal constant SWITCH_LATENCY = 3 days;

  /**
   * @custom:section                           ** EVENTS **
   */
  event InfrastructureFeeSet(uint256 _type, uint256 fee);
  event BeaconDelaySet(uint256 entryDelay, uint256 exitDelay);
  event VisibilitySet(uint256 id, bool isPrivate);
  event YieldReceiverSet(uint256 indexed poolId, address yieldReceiver);
  event MaintainerChanged(uint256 indexed id, address newMaintainer);
  event FeeSwitched(uint256 indexed id, uint256 fee, uint256 effectiveAfter);
  event ValidatorPeriodSwitched(uint256 indexed operatorId, uint256 period, uint256 effectiveAfter);
  event Delegation(uint256 poolId, uint256 indexed operatorId, uint256 allowance);
  event FallbackOperator(uint256 poolId, uint256 indexed operatorId, uint256 threshold);
  event Deposit(uint256 indexed poolId, uint256 boughtgETH, uint256 mintedgETH);
  event StakeProposal(uint256 poolId, uint256 operatorId, bytes[] pubkeys);
  event Stake(bytes[] pubkeys);
  event ExitRequest(bytes pubkey);
  event Exit(bytes pubkey);

  /**
   * @custom:section                           ** GOVERNING **
   *
   * @custom:visibility -> external
   * @dev IMPORTANT! These functions should be governed by a governance! Which is not done here!
   */

  /**
   * @notice Set the maxiumum allowed beacon delay for blaming validators on creation and exit.
   * @dev high beacon delays will affect the ux negatively, low delays can cause issues for operators.
   */
  function setBeaconDelays(StakeModuleStorage storage self, uint256 entry, uint256 exit) external {
    require(entry < MAX_BEACON_DELAY, "SML:> MAX");
    require(exit < MAX_BEACON_DELAY, "SML:> MAX");

    self.BEACON_DELAY_ENTRY = entry;
    self.BEACON_DELAY_EXIT = exit;

    emit BeaconDelaySet(entry, exit);
  }

  /**
   * @notice Set a fee (denominated in PERCENTAGE_DENOMINATOR) for any given TYPE.
   * @dev Changing the Staking Pool fee, only applies to the newly created validators.
   * @dev advise that there can be other fees within the package, thus we will never allow
   * * governance to take more than x%, while x<80(at least). For now, 50% limit on all fees
   * * makes sense to us. However, it can be adjusted to any value between 0-80 in the future.
   */
  function setInfrastructureFee(
    StakeModuleStorage storage self,
    uint256 _type,
    uint256 fee
  ) external {
    if (_type == ID_TYPE.POOL) {
      require(fee <= MAX_POOL_INFRASTRUCTURE_FEE, "PORTAL:> MAX");
    } else {
      require(fee < PERCENTAGE_DENOMINATOR / 2, "SML:> 50%");
    }

    self.infrastructureFees[_type] = fee;
    emit InfrastructureFeeSet(_type, fee);
  }

  /**
   * @custom:section                           ** AUTHENTICATION **
   *
   * @custom:visibility -> view-internal
   */

  /**
   * @notice restricts the access to given function based on TYPE and msg.sender
   * @param _expectCONTROLLER restricts the access to only CONTROLLER.
   * @param _expectMaintainer restricts the access to only maintainer.
   * @param _restrictionMap Restricts which TYPEs can pass the authentication.
   * * [0: Operator = TYPE(4), 1: Pool = TYPE(5)]
   * @dev can only be used after an ID is initiated
   * @dev CONTROLLERS and maintainers of the Prisoned Operators cannot access.
   */
  function _authenticate(
    DataStoreModuleStorage storage DATASTORE,
    uint256 _id,
    bool _expectCONTROLLER,
    bool _expectMaintainer,
    bool[2] memory _restrictionMap
  ) internal view {
    require(DATASTORE.readUint(_id, rks.initiated) != 0, "SML:not initiated");

    uint256 typeOfId = DATASTORE.readUint(_id, rks.TYPE);

    if (typeOfId == ID_TYPE.OPERATOR) {
      require(_restrictionMap[0], "SML:TYPE not allowed");
      if (_expectCONTROLLER || _expectMaintainer) {
        require(!isPrisoned(DATASTORE, _id), "SML:prisoned, get in touch with governance");
      }
    } else if (typeOfId == ID_TYPE.POOL) {
      require(_restrictionMap[1], "SML:TYPE not allowed");
    } else {
      revert("SML:invalid TYPE");
    }

    if (_expectMaintainer) {
      require(
        msg.sender == DATASTORE.readAddress(_id, rks.maintainer),
        "SML:sender not maintainer"
      );
      return;
    }

    if (_expectCONTROLLER) {
      require(
        msg.sender == DATASTORE.readAddress(_id, rks.CONTROLLER),
        "SML:sender not CONTROLLER"
      );
      return;
    }
  }

  /**
   * @custom:subsection                           ** POOL VISIBILITY **
   */

  /**
   * @custom:visibility -> external
   */

  /**
   * @notice changes the visibility of the pool
   * @param makePrivate true if pool should be private, false for public pools
   * @dev whitelist is cleared when pool is set to public, to prevent legacy bugs if ever made private again.
   * Note private pools can whitelist addresses with the help of a third party contract.
   */
  function setPoolVisibility(
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    bool makePrivate
  ) public {
    _authenticate(DATASTORE, poolId, true, false, [false, true]);
    require(makePrivate != isPrivatePool(DATASTORE, poolId), "SML:already set");

    DATASTORE.writeUint(poolId, rks.privatePool, makePrivate ? 1 : 0);

    if (!makePrivate) {
      DATASTORE.writeAddress(poolId, rks.whitelist, address(0));
    }

    emit VisibilitySet(poolId, makePrivate);
  }

  /**
   * @notice private pools can whitelist addresses with the help of a third party contract.
   * @dev Whitelisting contracts should implement IWhitelist interface.
   */
  function setWhitelist(
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    address whitelist
  ) external {
    _authenticate(DATASTORE, poolId, true, false, [false, true]);
    require(isPrivatePool(DATASTORE, poolId), "SML:must be private pool");

    DATASTORE.writeAddress(poolId, rks.whitelist, whitelist);
  }

  /**
   * @custom:visibility -> view-public
   */

  /**
   * @notice returns true if the pool is private
   */
  function isPrivatePool(
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId
  ) public view returns (bool) {
    return (DATASTORE.readUint(poolId, rks.privatePool) == 1);
  }

  /**
   * @notice checks if the Whitelist allows staker to use given private pool
   * @dev Owner of the pool doesn't need whitelisting
   * @dev Otherwise requires a whitelisting address to be set
   */
  function isWhitelisted(
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    address staker
  ) public view returns (bool) {
    if (DATASTORE.readAddress(poolId, rks.CONTROLLER) == staker) {
      return true;
    }

    address whitelist = DATASTORE.readAddress(poolId, rks.whitelist);
    if (whitelist == address(0)) {
      return false;
    }

    if (whitelist.code.length > 0) {
      try IWhitelist(whitelist).isAllowed(staker) returns (bool _isAllowed) {
        return _isAllowed;
      } catch {
        return false;
      }
    } else {
      return false;
    }
  }

  /**
   * @custom:section                           ** ID MANAGEMENT **
   *
   */

  /**
   * @custom:subsection                           ** YIELD SEPARATION **
   */

  /**
   * @custom:visibility -> external
   */

  /**
   * @notice Set the yield receiver address to activate or deactivete yield separation logic.
   * * If set other than address(0) separation will be activated, if set back to address(0)
   * * separation will be deactivated again.
   * @param poolId the gETH id of the Pool
   * @param yieldReceiver address of the yield receiver
   * @dev Only CONTROLLER of pool can set yield receier.
   */
  function setYieldReceiver(
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    address yieldReceiver
  ) external {
    _authenticate(DATASTORE, poolId, true, false, [false, true]);

    DATASTORE.writeAddress(poolId, rks.yieldReceiver, yieldReceiver);
    emit YieldReceiverSet(poolId, yieldReceiver);
  }

  /**
   * @custom:subsection                           ** MAINTAINER **
   */

  /**
   * @custom:visibility -> internal
   */

  /**
   * @notice Set the maintainer address on initiation or later
   * @param _newMaintainer address of the new maintainer
   */
  function _setMaintainer(
    DataStoreModuleStorage storage DATASTORE,
    uint256 _id,
    address _newMaintainer
  ) internal {
    require(_newMaintainer != address(0), "SML:maintainer cannot be zero");

    DATASTORE.writeAddress(_id, rks.maintainer, _newMaintainer);
    emit MaintainerChanged(_id, _newMaintainer);
  }

  /**
   * @custom:visibility -> external
   */

  /**
   * @notice CONTROLLER of the ID can change the maintainer to any address other than ZERO_ADDRESS
   * @dev there can only be 1 maintainer per ID.
   * @dev it is wise to change the maintainer before the CONTROLLER, in case of any migration
   * @dev we don't use _authenticate here because malicious maintainers can imprison operators
   * * and prevent them entering here, smh.
   */
  function changeMaintainer(
    DataStoreModuleStorage storage DATASTORE,
    uint256 id,
    address newMaintainer
  ) external {
    require(DATASTORE.readUint(id, rks.initiated) != 0, "SML:ID is not initiated");
    require(msg.sender == DATASTORE.readAddress(id, rks.CONTROLLER), "SML:sender not CONTROLLER");
    uint256 typeOfId = DATASTORE.readUint(id, rks.TYPE);
    require(typeOfId == ID_TYPE.OPERATOR || typeOfId == ID_TYPE.POOL, "SML:invalid TYPE");

    _setMaintainer(DATASTORE, id, newMaintainer);
  }

  /**
   * @custom:subsection                           ** MAINTENANCE FEE **
   */

  /**
   * @custom:visibility -> view-public
   */

  /**
   * @notice Gets fee as a percentage, PERCENTAGE_DENOMINATOR = 100%
   *
   * @dev respecs to the switching delay.
   *
   * @return fee = percentage * PERCENTAGE_DENOMINATOR / 100
   */
  function getMaintenanceFee(
    DataStoreModuleStorage storage DATASTORE,
    uint256 id
  ) public view returns (uint256 fee) {
    if (DATASTORE.readUint(id, rks.feeSwitch) > block.timestamp) {
      return DATASTORE.readUint(id, rks.priorFee);
    }
    return DATASTORE.readUint(id, rks.fee);
  }

  /**
   * @custom:visibility -> internal
   */

  /**
   * @notice internal function to set fee with NO DELAY
   */
  function _setMaintenanceFee(
    DataStoreModuleStorage storage DATASTORE,
    uint256 _id,
    uint256 _newFee
  ) internal {
    require(_newFee <= MAX_MAINTENANCE_FEE, "SML:> MAX_MAINTENANCE_FEE");
    DATASTORE.writeUint(_id, rks.fee, _newFee);
  }

  /**
   * @custom:visibility -> external
   */

  /**
   * @notice Changes the fee that is applied to the newly created validators, with A DELAY OF SWITCH_LATENCY.
   * @dev Cannot be called again while its currently switching.
   * @dev advise that 100% == PERCENTAGE_DENOMINATOR
   */
  function switchMaintenanceFee(
    DataStoreModuleStorage storage DATASTORE,
    uint256 id,
    uint256 newFee
  ) external {
    _authenticate(DATASTORE, id, true, false, [true, true]);

    require(block.timestamp > DATASTORE.readUint(id, rks.feeSwitch), "SML:currently switching");

    DATASTORE.writeUint(id, rks.priorFee, DATASTORE.readUint(id, rks.fee));
    DATASTORE.writeUint(id, rks.feeSwitch, block.timestamp + SWITCH_LATENCY);

    _setMaintenanceFee(DATASTORE, id, newFee);

    emit FeeSwitched(id, newFee, block.timestamp + SWITCH_LATENCY);
  }

  /**
   * @custom:subsection                           ** INTERNAL WALLET **
   *
   * @dev Internal wallet of an ID accrues fees over time.
   * It is also used by Node Operators to fund 1 ETH per validator proposal, which is reimbursed if/when activated.
   */

  /**
   * @custom:visibility -> internal
   */

  /**
   * @notice Simply increases the balance of an IDs Maintainer wallet
   * @param _value Ether (in Wei) amount to increase the wallet balance.
   */
  function _increaseWalletBalance(
    DataStoreModuleStorage storage DATASTORE,
    uint256 _id,
    uint256 _value
  ) internal {
    DATASTORE.addUint(_id, rks.wallet, _value);
  }

  /**
   * @notice To decrease the balance of an Operator's wallet internally
   * @param _value Ether (in Wei) amount to decrease the wallet balance and send back to Maintainer.
   */
  function _decreaseWalletBalance(
    DataStoreModuleStorage storage DATASTORE,
    uint256 _id,
    uint256 _value
  ) internal {
    require(DATASTORE.readUint(_id, rks.wallet) >= _value, "SML:insufficient wallet balance");
    DATASTORE.subUint(_id, rks.wallet, _value);
  }

  /**
   * @custom:visibility -> external
   */

  /**
   * @notice external function to increase the internal wallet balance
   * @dev anyone can increase the balance directly, useful for withdrawalContracts and fees etc.
   */
  function increaseWalletBalance(
    DataStoreModuleStorage storage DATASTORE,
    uint256 id
  ) external returns (bool success) {
    _authenticate(DATASTORE, id, false, false, [true, true]);
    _increaseWalletBalance(DATASTORE, id, msg.value);
    success = true;
  }

  /**
   * @notice external function to decrease the internal wallet balance
   * @dev only CONTROLLER can decrease the balance externally,
   * @return success if the amount was sent and deducted
   */
  function decreaseWalletBalance(
    DataStoreModuleStorage storage DATASTORE,
    uint256 id,
    uint256 value
  ) external returns (bool success) {
    _authenticate(DATASTORE, id, true, false, [true, true]);

    require(address(this).balance >= value, "SML:insufficient contract balance");

    _decreaseWalletBalance(DATASTORE, id, value);
    address controller = DATASTORE.readAddress(id, rks.CONTROLLER);

    (success, ) = payable(controller).call{value: value}("");
    require(success, "SML:Failed to send ETH");
  }

  /**
   * @custom:subsection                           ** OPERATORS PERIOD **
   */

  /**
   * @custom:visibility -> view-public
   */

  function getValidatorPeriod(
    DataStoreModuleStorage storage DATASTORE,
    uint256 id
  ) public view returns (uint256 period) {
    if (DATASTORE.readUint(id, rks.periodSwitch) > block.timestamp) {
      return DATASTORE.readUint(id, rks.priorPeriod);
    }
    return DATASTORE.readUint(id, rks.validatorPeriod);
  }

  /**
   * @custom:visibility -> internal
   */

  /**
   * @notice internal function to set validator period with NO DELAY
   */
  function _setValidatorPeriod(
    DataStoreModuleStorage storage DATASTORE,
    uint256 _operatorId,
    uint256 _newPeriod
  ) internal {
    require(_newPeriod >= MIN_VALIDATOR_PERIOD, "SML:< MIN_VALIDATOR_PERIOD");
    require(_newPeriod <= MAX_VALIDATOR_PERIOD, "SML:> MAX_VALIDATOR_PERIOD");

    DATASTORE.writeUint(_operatorId, rks.validatorPeriod, _newPeriod);
  }

  /**
   * @custom:visibility -> external
   */

  /**
   * @notice updates validatorPeriod for given operator, with A DELAY OF SWITCH_LATENCY.
   * @dev limited by MIN_VALIDATOR_PERIOD and MAX_VALIDATOR_PERIOD
   */
  function switchValidatorPeriod(
    DataStoreModuleStorage storage DATASTORE,
    uint256 operatorId,
    uint256 newPeriod
  ) external {
    _authenticate(DATASTORE, operatorId, true, false, [true, false]);

    require(
      block.timestamp > DATASTORE.readUint(operatorId, rks.periodSwitch),
      "SML:currently switching"
    );

    DATASTORE.writeUint(
      operatorId,
      rks.priorPeriod,
      DATASTORE.readUint(operatorId, rks.validatorPeriod)
    );
    DATASTORE.writeUint(operatorId, rks.periodSwitch, block.timestamp + SWITCH_LATENCY);

    _setValidatorPeriod(DATASTORE, operatorId, newPeriod);

    emit ValidatorPeriodSwitched(operatorId, newPeriod, block.timestamp + SWITCH_LATENCY);
  }

  /**
   * @custom:section                           ** PRISON **
   *
   * @custom:visibility -> view-public
   * @dev check OEL.blameProposal and OEL.blameExit for imprisonment details
   */

  /**
   * @notice Checks if the given operator is Prisoned
   * @dev rks.release key refers to the end of the last imprisonment, when the limitations of operator is lifted
   */
  function isPrisoned(
    DataStoreModuleStorage storage DATASTORE,
    uint256 operatorId
  ) public view returns (bool) {
    return (block.timestamp < DATASTORE.readUint(operatorId, rks.release));
  }

  /**
   * @custom:section                           ** VALIDATOR DELEGATION **
   */

  /**
   * @custom:visibility -> view-public
   */

  /**
   * @notice maximum number of remaining operator allowance that the given Operator is allowed to create for given Pool
   * @dev an operator cannot create new validators if:
   * * 1. operator is a monopoly
   * * 2. allowance is filled
   * * * But if operator is set as a fallback, it can if set fallbackThreshold is reached on all allowances.
   * @dev If operator withdraws a validator, then able to create a new one.
   * @dev prestake checks the approved validator count to make sure the number of validators are not bigger than allowance
   * @dev allowance doesn't change when new validators created or old ones are unstaked.
   */
  function operatorAllowance(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    uint256 operatorId
  ) public view returns (uint256 remValidators) {
    // monopoly check
    {
      // readUint for an array gives us length
      uint256 numOperatorValidators = DATASTORE.readUint(operatorId, rks.validators);
      uint256 monopoly_threshold = self.MONOPOLY_THRESHOLD;
      if (numOperatorValidators >= monopoly_threshold) {
        return 0;
      } else {
        remValidators = monopoly_threshold - numOperatorValidators;
      }
    }

    // fallback check
    {
      if (operatorId == DATASTORE.readUint(poolId, rks.fallbackOperator)) {
        // readUint for an array gives us length
        uint256 numPoolValidators = DATASTORE.readUint(poolId, rks.validators);
        uint256 totalAllowance = DATASTORE.readUint(poolId, rks.totalAllowance);

        if (
          totalAllowance == 0 ||
          (((numPoolValidators * PERCENTAGE_DENOMINATOR) / totalAllowance) >=
            DATASTORE.readUint(poolId, rks.fallbackThreshold))
        ) {
          return remValidators;
        }
      }
    }

    // approval check
    {
      uint256 allowance = DATASTORE.readUint(poolId, DSML.getKey(operatorId, rks.allowance));
      uint256 pooledValidators = DATASTORE.readUint(
        poolId,
        DSML.getKey(operatorId, rks.proposedValidators)
      ) + DATASTORE.readUint(poolId, DSML.getKey(operatorId, rks.activeValidators));
      if (pooledValidators >= allowance) {
        return 0;
      } else {
        uint256 remAllowance = allowance - pooledValidators;
        if (remValidators > remAllowance) {
          remValidators = remAllowance;
        }
      }
    }
  }

  /**
   * @custom:visibility -> internal
   */

  /**
   * @notice To give allowence to node operator for a pool. It re-sets the allowance with the given value.
   * @dev The value that is returned is not the new allowance, but the old one since it is required
   * * at the point where it is being returned.
   * @return oldAllowance to be used later, nothing is done with it within this function.
   */
  function _approveOperator(
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    uint256 operatorId,
    uint256 allowance
  ) internal returns (uint256 oldAllowance) {
    bytes32 allowanceKey = DSML.getKey(operatorId, rks.allowance);

    oldAllowance = DATASTORE.readUint(poolId, allowanceKey);
    DATASTORE.writeUint(poolId, allowanceKey, allowance);

    emit Delegation(poolId, operatorId, allowance);
  }

  /**
   * @custom:visibility -> external
   */

  /**
   * @notice To allow a Node Operator run validators for your Pool with a given number of validators.
   * * This number can be set again at any given point in the future.
   * @param poolId the gETH id of the Pool
   * @param operatorIds array of Operator IDs to allow them create validators
   * @param allowances the MAX number of validators that can be created by the Operator, for given Pool
   * @dev When decreased the approved validator count below current active+proposed validators,
   * operator cannot create new validators.
   */
  function delegate(
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    uint256[] calldata operatorIds,
    uint256[] calldata allowances
  ) external {
    _authenticate(DATASTORE, poolId, false, true, [false, true]);
    uint256 operatorIdsLen = operatorIds.length;
    require(operatorIdsLen == allowances.length, "SML:allowances should match");

    for (uint256 i; i < operatorIdsLen; ) {
      require(
        DATASTORE.readUint(operatorIds[i], rks.TYPE) == ID_TYPE.OPERATOR,
        "SML:id not operator"
      );
      require(allowances[i] <= MAX_ALLOWANCE, "SML:> MAX_ALLOWANCE, set fallback");

      unchecked {
        i += 1;
      }
    }

    uint256 newCumulativeSubset;
    uint256 oldCumulativeSubset;
    for (uint256 i; i < operatorIdsLen; ) {
      newCumulativeSubset += allowances[i];
      oldCumulativeSubset += _approveOperator(DATASTORE, poolId, operatorIds[i], allowances[i]);

      unchecked {
        i += 1;
      }
    }

    if (newCumulativeSubset > oldCumulativeSubset) {
      DATASTORE.addUint(poolId, rks.totalAllowance, newCumulativeSubset - oldCumulativeSubset);
    } else if (newCumulativeSubset < oldCumulativeSubset) {
      DATASTORE.subUint(poolId, rks.totalAllowance, oldCumulativeSubset - newCumulativeSubset);
    }
  }

  /**
   * @notice To allow a Node Operator run validators for your Pool without a limit
   * * after pool reaches a given treshold as percentage.
   * * fallback operator and percentage can be set again at any given point in the future.
   * * cannot set an operator as a fallback operator while it is currently in prison.
   * @param poolId the gETH id of the Pool
   * @param operatorId Operator ID to allow create validators
   * @param fallbackThreshold the percentage (with PERCENTAGE_DENOMINATOR) that fallback operator
   * * is activated for given Pool. Should not be greater than 100.
   */
  function setFallbackOperator(
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    uint256 operatorId,
    uint256 fallbackThreshold
  ) external {
    _authenticate(DATASTORE, poolId, false, true, [false, true]);

    if (operatorId == 0) {
      DATASTORE.writeUint(poolId, rks.fallbackOperator, 0);
      DATASTORE.writeUint(poolId, rks.fallbackThreshold, 0);
      emit FallbackOperator(poolId, 0, 0);
    } else {
      require(
        DATASTORE.readUint(operatorId, rks.TYPE) == ID_TYPE.OPERATOR,
        "SML:fallback not operator"
      );

      require(
        fallbackThreshold <= PERCENTAGE_DENOMINATOR,
        "SML:threshold cannot be greater than 100"
      );

      DATASTORE.writeUint(poolId, rks.fallbackThreshold, fallbackThreshold);
      DATASTORE.writeUint(poolId, rks.fallbackOperator, operatorId);

      emit FallbackOperator(poolId, operatorId, fallbackThreshold);
    }
  }

  /**
   * @custom:section                           ** POOLING  **
   */

  /**
   * @custom:subsection                           ** DEPOSIT HELPERS **
   */

  /**
   * @custom:visibility -> view-internal
   */

  function _isGeodePackageIsolated(address _packageAddress) internal view returns (bool) {
    return IGeodePackage(_packageAddress).isolationMode();
  }

  /**
   * @notice returns wrapped bound liquidity pool. If deployed, if not in isolationMode.
   * @dev returns address(0) if no pool or it is under isolation
   */
  function _getLiquidityPool(
    DataStoreModuleStorage storage DATASTORE,
    uint256 _poolId
  ) internal view returns (ILiquidityPool) {
    address liqPool = DATASTORE.readAddress(_poolId, rks.liquidityPool);
    if (liqPool == address(0)) {
      return ILiquidityPool(address(0));
    } else if (_isGeodePackageIsolated(liqPool)) {
      return ILiquidityPool(address(0));
    } else {
      return ILiquidityPool(liqPool);
    }
  }

  /**
   * @custom:visibility -> view-public
   */

  /**
   * @notice returns true if the price is valid:
   * - last price syncinc happened less than 24h
   * - there has been no oracle reports since the last update
   *
   * @dev known bug / feature: if there have been no oracle updates,
   * * this function will return true.
   *
   * lastupdate + PRICE_EXPIRY >= block.timestamp ? true
   *    : lastupdate >= self.ORACLE_UPDATE_TIMESTAMP ? true
   *    : false
   */
  function isPriceValid(
    StakeModuleStorage storage self,
    uint256 poolId
  ) public view returns (bool isValid) {
    uint256 lastupdate = self.gETH.priceUpdateTimestamp(poolId);
    unchecked {
      isValid =
        lastupdate + PRICE_EXPIRY >= block.timestamp &&
        lastupdate >= self.ORACLE_UPDATE_TIMESTAMP;
    }
  }

  /**
   * @notice checks if staking is allowed in given staking pool
   * @notice staking is not allowed if:
   * 1. Price is not valid
   * 2. WithdrawalContract is in Isolation Mode, can have many reasons
   */
  function isMintingAllowed(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId
  ) public view returns (bool) {
    return
      (isPriceValid(self, poolId)) &&
      !(_isGeodePackageIsolated(DATASTORE.readAddress(poolId, rks.withdrawalContract)));
  }

  /**
   * @custom:subsection                           ** DEPOSIT **
   */

  /**
   * @custom:visibility -> internal
   */

  /**
   * @notice mints gETH for a given ETH amount, keeps the tokens in Portal.
   * @dev fails if minting is not allowed: invalid price, or isolationMode.
   */
  function _mintgETH(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 _poolId,
    uint256 _ethAmount
  ) internal returns (uint256 mintedgETH) {
    require(isMintingAllowed(self, DATASTORE, _poolId), "SML:minting is not allowed");

    uint256 price = self.gETH.pricePerShare(_poolId);
    require(price > 0, "SML:price is zero?");

    mintedgETH = (((_ethAmount * gETH_DENOMINATOR) / price));
    self.gETH.mint(address(this), _poolId, mintedgETH, "");
    DATASTORE.addUint(_poolId, rks.surplus, _ethAmount);
  }

  /**
   * @notice conducts a buyback using the given liquidity pool
   * @param _poolId id of the gETH that will be bought
   * @param _maxEthToSell max ETH amount to sell in the liq pool
   * @param _deadline TX is expected to revert by Swap.sol if not meet
   * @dev this function assumes that pool is deployed by deployLiquidityPool
   * as index 0 is ETH and index 1 is gETH!
   */
  function _buyback(
    DataStoreModuleStorage storage DATASTORE,
    uint256 _poolId,
    uint256 _maxEthToSell,
    uint256 _deadline
  ) internal returns (uint256 remETH, uint256 boughtgETH) {
    ILiquidityPool LP = _getLiquidityPool(DATASTORE, _poolId);
    // skip if no liquidity pool is found
    if (address(LP) != address(0)) {
      uint256 debt = LP.getDebt();
      // skip if debt is too low
      if (debt > IGNORABLE_DEBT) {
        if (_maxEthToSell > debt) {
          // if debt is lower, then only sell debt
          remETH = _maxEthToSell - debt;
        } else {
          // if eth is lower, then sell all eth, remETH already 0
          debt = _maxEthToSell;
        }
        // SWAP in LP
        boughtgETH = LP.swap{value: debt}(0, 1, debt, 0, _deadline);
      } else {
        remETH = _maxEthToSell;
      }
    } else {
      remETH = _maxEthToSell;
    }
  }

  /**
   * @custom:visibility -> external
   */

  /**
   * @notice Allowing users to deposit into a staking pool.
   * @notice If a pool is not public, only the controller and if there is a whitelist contract, the whitelisted addresses can deposit.
   * @param poolId id of the staking pool, liquidity pool and gETH to be used.
   * @param mingETH liquidity pool parameter
   * @param deadline liquidity pool parameter
   * @dev an example for minting + buybacks
   * Buys from DWP if price is low -debt-, mints new tokens if surplus is sent -more than debt-
   * * debt  msgValue
   * * 100   10  => buyback
   * * 100   100 => buyback
   * * 10    100 => buyback + mint
   * * 1     x   => mint
   * * 0.5   x   => mint
   * * 0     x   => mint
   */
  function deposit(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    uint256 mingETH,
    uint256 deadline,
    address receiver
  ) external returns (uint256 boughtgETH, uint256 mintedgETH) {
    _authenticate(DATASTORE, poolId, false, false, [false, true]);
    require(msg.value > 0, "SML:msg.value cannot be zero");
    require(deadline > block.timestamp, "SML:deadline not met");
    require(receiver != address(0), "SML:receiver is zero address");

    if (isPrivatePool(DATASTORE, poolId)) {
      require(isWhitelisted(DATASTORE, poolId, msg.sender), "SML:sender not whitelisted");
    }

    uint256 remEth = msg.value;
    (remEth, boughtgETH) = _buyback(DATASTORE, poolId, remEth, deadline);

    if (remEth > 0) {
      mintedgETH = _mintgETH(self, DATASTORE, poolId, remEth);
    }

    require(boughtgETH + mintedgETH >= mingETH, "SML:less than minimum");

    // send back to user
    self.gETH.safeTransferFrom(address(this), receiver, poolId, boughtgETH + mintedgETH, "");

    emit Deposit(poolId, boughtgETH, mintedgETH);
  }

  /**
   * @custom:section                           ** VALIDATOR CREATION **
   *
   * @dev Creation of a Validator takes 2 steps: propose and beacon stake.
   * Before entering stake() function, _canStake verifies the eligibility of
   * given pubKey that is proposed by an operator with proposeStake function.
   * Eligibility is defined by an optimistic alienation, check OracleUtils._alienateValidator() for info.
   */

  /**
   * @custom:visibility -> view
   */

  /**
   * @notice internal function to check if a validator can use the pool funds
   *
   *  @param _pubkey BLS12-381 public key of the validator
   *  @return true if:
   *   - pubkey should be proposed
   *   - pubkey should not be alienated (https://bit.ly/3Tkc6UC)
   *   - the validator's index is already covered by VERIFICATION_INDEX. Updated by Telescope.
   */
  function _canStake(
    StakeModuleStorage storage self,
    bytes calldata _pubkey,
    uint256 _verificationIndex
  ) internal view returns (bool) {
    return
      (self.validators[_pubkey].state == VALIDATOR_STATE.PROPOSED) &&
      (self.validators[_pubkey].index <= _verificationIndex);
  }

  /**
   * @notice external function to check if a validator can use the pool funds
   */
  function canStake(
    StakeModuleStorage storage self,
    bytes calldata pubkey
  ) external view returns (bool) {
    return _canStake(self, pubkey, self.VERIFICATION_INDEX);
  }

  /**
   * @dev -> external
   */

  /**
   * @notice Validator Credentials Proposal function, first step of crating validators.
   * * Once a pubKey is proposed and not alienated after verificationIndex updated,
   * * it is optimistically allowed to take funds from staking pools.
   *
   * @param poolId the id of the staking pool
   * @param operatorId the id of the Operator whose maintainer calling this function
   * @param pubkeys  Array of BLS12-381 public keys of the validators that will be proposed
   * @param signatures1 Array of BLS12-381 signatures that will be used to send 1 ETH from the Operator's
   * maintainer balance
   * @param signatures31 Array of BLS12-381 signatures that will be used to send 31 ETH from pool on stake() function call
   *
   * @dev DCL.DEPOSIT_AMOUNT_PRESTAKE = 1 ether, DCL.DEPOSIT_AMOUNT = 32 ether which is the minimum amount to create a validator.
   * 31 Ether will be staked after verification of oracles. 32 in total.
   * 1 ether will be sent back to Node Operator when the finalized deposit is successful.
   * @dev ProposeStake requires enough allowance from Staking Pools to Operators.
   * @dev ProposeStake requires enough funds within Wallet.
   * @dev Max number of validators to propose is per call is MAX_DEPOSITS_PER_CALL (currently 50)
   */
  function proposeStake(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    uint256 operatorId,
    bytes[] calldata pubkeys,
    bytes[] calldata signatures1,
    bytes[] calldata signatures31
  ) external {
    // checks
    _authenticate(DATASTORE, operatorId, false, true, [true, false]);
    _authenticate(DATASTORE, poolId, false, false, [false, true]);
    require(
      !(_isGeodePackageIsolated(DATASTORE.readAddress(poolId, rks.withdrawalContract))),
      "SML:withdrawalContract is isolated"
    );

    uint256 pkLen = pubkeys.length;

    require((pkLen > 0) && (pkLen <= DCL.MAX_DEPOSITS_PER_CALL), "SML:1 - 50 validators");

    require(
      pkLen == signatures1.length && pkLen == signatures31.length,
      "SML:invalid input length"
    );

    require(
      operatorAllowance(self, DATASTORE, poolId, operatorId) >= pkLen,
      "SML:insufficient allowance"
    );

    require(
      DATASTORE.readUint(poolId, rks.surplus) >= DCL.DEPOSIT_AMOUNT * pkLen,
      "SML:not enough surplus"
    );

    _decreaseWalletBalance(DATASTORE, operatorId, (pkLen * DCL.DEPOSIT_AMOUNT_PRESTAKE));

    for (uint256 i; i < pkLen; ) {
      require(pubkeys[i].length == DCL.PUBKEY_LENGTH, "SML:PUBKEY_LENGTH ERROR");
      require(signatures1[i].length == DCL.SIGNATURE_LENGTH, "SML:SIGNATURE_LENGTH ERROR");
      require(signatures31[i].length == DCL.SIGNATURE_LENGTH, "SML:SIGNATURE_LENGTH ERROR");

      unchecked {
        i += 1;
      }
    }

    ConstantValidatorData memory valData = ConstantValidatorData({
      index: uint64(self.VALIDATORS_INDEX + 1),
      period: uint64(getValidatorPeriod(DATASTORE, operatorId)),
      poolFee: getMaintenanceFee(DATASTORE, poolId),
      operatorFee: getMaintenanceFee(DATASTORE, operatorId),
      infrastructureFee: self.infrastructureFees[ID_TYPE.POOL],
      withdrawalCredential: DATASTORE.readBytes(poolId, rks.withdrawalCredential)
    });

    for (uint256 i; i < pkLen; ) {
      require(
        self.validators[pubkeys[i]].state == VALIDATOR_STATE.NONE,
        "SML: used or alienated pk"
      );

      self.validators[pubkeys[i]] = Validator(
        VALIDATOR_STATE.PROPOSED,
        valData.index + uint64(i),
        uint64(block.timestamp),
        valData.period,
        poolId,
        operatorId,
        valData.poolFee,
        valData.operatorFee,
        valData.infrastructureFee,
        signatures31[i]
      );

      DCL.depositValidator(
        pubkeys[i],
        valData.withdrawalCredential,
        signatures1[i],
        DCL.DEPOSIT_AMOUNT_PRESTAKE
      );

      unchecked {
        i += 1;
      }
    }

    DATASTORE.subUint(poolId, rks.surplus, (pkLen * DCL.DEPOSIT_AMOUNT));
    DATASTORE.addUint(poolId, rks.secured, (pkLen * DCL.DEPOSIT_AMOUNT));

    DATASTORE.addUint(poolId, DSML.getKey(operatorId, rks.proposedValidators), pkLen);
    DATASTORE.appendBytesArrayBatch(poolId, rks.validators, pubkeys);
    DATASTORE.appendBytesArrayBatch(operatorId, rks.validators, pubkeys);

    self.VALIDATORS_INDEX += pkLen;

    emit StakeProposal(poolId, operatorId, pubkeys);
  }

  /**
   *  @notice Sends 31 Eth from staking pool to validators that are previously created with ProposeStake.
   *  1 Eth per successful validator boostraping is returned back to Wallet.
   *
   *  @param operatorId the id of the Operator whose maintainer calling this function
   *  @param pubkeys  Array of BLS12-381 public keys of the validators that are already proposed with ProposeStake.
   *
   *  @dev To save gas cost, pubkeys should be arranged by poolIds.
   *  ex: [pk1, pk2, pk3, pk4, pk5, pk6, pk7]
   *  pk1, pk2, pk3 from pool1
   *  pk4, pk5 from pool2
   *  pk6 from pool3
   *  separate them in similar groups as much as possible.
   *  @dev Max number of validators to boostrap is MAX_DEPOSITS_PER_CALL (currently 50)
   *  @dev A pubkey that is alienated will not get through. Do not frontrun during ProposeStake.
   */
  function stake(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 operatorId,
    bytes[] calldata pubkeys
  ) external {
    _authenticate(DATASTORE, operatorId, false, true, [true, false]);

    require(
      (pubkeys.length > 0) && (pubkeys.length <= DCL.MAX_DEPOSITS_PER_CALL),
      "SML:1 - 50 validators"
    );

    {
      uint256 pubkeysLen = pubkeys.length;
      uint256 _verificationIndex = self.VERIFICATION_INDEX;
      for (uint256 j; j < pubkeysLen; ) {
        require(
          _canStake(self, pubkeys[j], _verificationIndex),
          "SML:not all pubkeys are stakeable"
        );

        require(
          self.validators[pubkeys[j]].operatorId == operatorId,
          "SML:not all pubkeys belong to operator"
        );

        unchecked {
          j += 1;
        }
      }
    }

    {
      bytes32 activeValKey = DSML.getKey(operatorId, rks.activeValidators);
      bytes32 proposedValKey = DSML.getKey(operatorId, rks.proposedValidators);
      uint256 poolId = self.validators[pubkeys[0]].poolId;
      bytes memory withdrawalCredential = DATASTORE.readBytes(poolId, rks.withdrawalCredential);

      uint256 lastIdChange = 0;
      for (uint256 i; i < pubkeys.length; ) {
        uint256 newPoolId = self.validators[pubkeys[i]].poolId;
        if (poolId != newPoolId) {
          uint256 sinceLastIdChange;

          unchecked {
            sinceLastIdChange = i - lastIdChange;
          }

          DATASTORE.subUint(poolId, rks.secured, (DCL.DEPOSIT_AMOUNT * (sinceLastIdChange)));
          DATASTORE.subUint(poolId, proposedValKey, (sinceLastIdChange));
          DATASTORE.addUint(poolId, activeValKey, (sinceLastIdChange));

          lastIdChange = i;
          poolId = newPoolId;
          withdrawalCredential = DATASTORE.readBytes(poolId, rks.withdrawalCredential);
        }

        DCL.depositValidator(
          pubkeys[i],
          withdrawalCredential,
          self.validators[pubkeys[i]].signature31,
          (DCL.DEPOSIT_AMOUNT - DCL.DEPOSIT_AMOUNT_PRESTAKE)
        );

        self.validators[pubkeys[i]].state = VALIDATOR_STATE.ACTIVE;

        unchecked {
          i += 1;
        }
      }
      {
        uint256 sinceLastIdChange;
        unchecked {
          sinceLastIdChange = pubkeys.length - lastIdChange;
        }

        DATASTORE.subUint(poolId, rks.secured, DCL.DEPOSIT_AMOUNT * (sinceLastIdChange));
        DATASTORE.subUint(poolId, proposedValKey, (sinceLastIdChange));
        DATASTORE.addUint(poolId, activeValKey, (sinceLastIdChange));
      }

      _increaseWalletBalance(DATASTORE, operatorId, DCL.DEPOSIT_AMOUNT_PRESTAKE * pubkeys.length);

      emit Stake(pubkeys);
    }
  }

  /**
   * @custom:section                           ** VALIDATOR EXITS **
   *
   * @custom:visibility -> external
   */

  /**
   * @notice Notifies the node operator with ExitRequest event
   * @dev Prevents the request if validator is still within the MIN_VALIDATOR_PERIOD
   * @dev Only the active validators can be called for an exit
   * @dev Can only be called by the withdrawalContract of the pool given validator belongs to.
   * @dev fails in case the tx is submitted without a secure medium, withdrawal contract of the correct pool
   * @return true if operation is successful
   */
  function requestExit(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    bytes calldata pk
  ) external returns (bool) {
    if (self.validators[pk].createdAt + MIN_VALIDATOR_PERIOD > block.timestamp) {
      return false;
    }

    require(
      msg.sender == DATASTORE.readAddress(poolId, rks.withdrawalContract),
      "SML:sender is not withdrawal contract"
    );
    require(self.validators[pk].poolId == poolId, "SML:incorrect poolId");
    require(self.validators[pk].state == VALIDATOR_STATE.ACTIVE, "SML:not an active validator");

    self.validators[pk].state = VALIDATOR_STATE.EXIT_REQUESTED;
    emit ExitRequest(pk);
    return true;
  }

  /**
   * @notice Finalizes the exit process for a validator.
   * @dev Strongly advised to be called right after the exiting process is over.
   * @dev Operators can exit at any time they want.
   * @dev Can only be called by the withdrawalContract of the pool given validator belongs to.
   */
  function finalizeExit(
    StakeModuleStorage storage self,
    DataStoreModuleStorage storage DATASTORE,
    uint256 poolId,
    bytes calldata pk
  ) external {
    require(
      msg.sender == DATASTORE.readAddress(poolId, rks.withdrawalContract),
      "SML:sender is not withdrawal contract"
    );
    require(self.validators[pk].poolId == poolId, "SML:incorrect poolId");

    uint256 state = self.validators[pk].state;
    require(
      state == VALIDATOR_STATE.ACTIVE || state == VALIDATOR_STATE.EXIT_REQUESTED,
      "SML:not an active validator"
    );

    self.validators[pk].state = VALIDATOR_STATE.EXITED;

    emit Exit(pk);
  }
}

contracts/modules/StakeModule/StakeModul.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity =0.8.20;

// external - contracts
import {ERC1155HolderUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
// internal - globals
import {PERCENTAGE_DENOMINATOR} from "../../globals/macros.sol";
// internal - interfaces
import {IgETH} from "../../interfaces/IgETH.sol";
import {IStakeModule} from "../../interfaces/modules/IStakeModule.sol";
// internal - structs
import {DataStoreModuleStorage} from "../DataStoreModule/structs/storage.sol";
import {StakeModuleStorage} from "./structs/storage.sol";
import {Validator} from "./structs/utils.sol";
// internal - libraries
import {StakeModuleLib as SML} from "./libs/StakeModuleLib.sol";
import {InitiatorExtensionLib as IEL} from "./libs/InitiatorExtensionLib.sol";
import {OracleExtensionLib as OEL} from "./libs/OracleExtensionLib.sol";
// internal - contracts
import {DataStoreModule} from "../DataStoreModule/DataStoreModule.sol";

/**
 * @title SM: Stake Module
 *
 * @notice Liquid staking for everyone.
 * * pooling and staking for staking derivatives
 * * validator delegation and operator onboarding
 * * oracle operations such as pricing
 *
 * @dev review: this module delegates its functionality to SML (StakeModuleLib).
 * * SML has authenticate function for access control.
 * @dev review: OEL (OracleExtensionLib) is an extension for oracle operations.
 * @dev review: DCL (DepositContractLib) is an helper for validator creation.
 *
 * @dev There is 1 additional functionality implemented apart from the library:
 * * check price validity and accept proofs for updating the price (refer to deposit function).
 * * However, this module inherits and implements nonReentrant & whenNotPaused modifiers.
 * * SM has pausability and expects inheriting contract to provide the access control mechanism.
 *
 * @dev 4 functions need to be overriden when inherited: pause, unpause, setInfrastructureFee, setBeaconDelays.
 *
 * @dev __StakeModule_init (or _unchained) call is NECESSARY when inherited.
 *
 * @dev This module inherits DataStoreModule.
 *
 * @author Ice Bear & Crash Bandicoot
 */
abstract contract StakeModule is
  IStakeModule,
  ERC1155HolderUpgradeable,
  ReentrancyGuardUpgradeable,
  PausableUpgradeable,
  DataStoreModule
{
  using SML for StakeModuleStorage;
  using IEL for StakeModuleStorage;
  using OEL for StakeModuleStorage;

  /**
   * @custom:section                           ** VARIABLES **
   *
   * @dev Do not add any other variables here. Modules do not have a gap.
   * Library's main struct has a gap, providing up to 16 storage slots for this module.
   */
  // keccak256(abi.encode(uint256(keccak256("geode.storage.StakeModuleStorage")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 private constant StakeModuleStorageLocation =
    0x642b1534be65022221e9e6919fcbcd097fefb6d9d9b7897cee77332e470da700;

  function _getStakeModuleStorage() internal pure returns (StakeModuleStorage storage $) {
    assembly {
      $.slot := StakeModuleStorageLocation
    }
  }

  /**
   * @custom:section                           ** EVENTS **
   */
  event IdInitiated(uint256 id, uint256 indexed TYPE);
  event MiddlewareDeployed(uint256 poolId, uint256 version);
  event PackageDeployed(uint256 poolId, uint256 packageType, address instance);
  event InfrastructureFeeSet(uint256 _type, uint256 fee);
  event BeaconDelaySet(uint256 entryDelay, uint256 exitDelay);
  event InitiationDepositSet(uint256 initiationDeposit);
  event VisibilitySet(uint256 id, bool isPrivate);
  event YieldReceiverSet(uint256 indexed poolId, address yieldReceiver);
  event MaintainerChanged(uint256 indexed id, address newMaintainer);
  event FeeSwitched(uint256 indexed id, uint256 fee, uint256 effectiveAfter);
  event ValidatorPeriodSwitched(uint256 indexed operatorId, uint256 period, uint256 effectiveAfter);
  event Delegation(uint256 poolId, uint256 indexed operatorId, uint256 allowance);
  event FallbackOperator(uint256 poolId, uint256 indexed operatorId, uint256 threshold);
  event Prisoned(uint256 indexed operatorId, bytes proof, uint256 releaseTimestamp);
  event Deposit(uint256 indexed poolId, uint256 boughtgETH, uint256 mintedgETH);
  event StakeProposal(uint256 poolId, uint256 operatorId, bytes[] pubkeys);
  event Stake(bytes[] pubkeys);

  event Alienated(bytes pubkey);
  event VerificationIndexUpdated(uint256 validatorVerificationIndex);
  event FeeTheft(uint256 indexed id, bytes proofs);
  event YieldDistributed(uint256 indexed poolId, uint256 amount);
  event OracleReported(
    bytes32 priceMerkleRoot,
    bytes32 balanceMerkleRoot,
    uint256 monopolyThreshold
  );

  /**
   * @custom:section                           ** ABSTRACT FUNCTIONS **
   */
  function pause() external virtual override;

  function unpause() external virtual override;

  function setInfrastructureFee(uint256 _type, uint256 fee) external virtual override;

  function setBeaconDelays(uint256 _type, uint256 fee) external virtual override;

  function setInitiationDeposit(uint256 newInitiationDeposit) external virtual override;

  /**
   * @custom:section                           ** INITIALIZING **
   */
  function __StakeModule_init(address _gETH, address _oracle_position) internal onlyInitializing {
    __ReentrancyGuard_init();
    __Pausable_init();
    __ERC1155Holder_init();
    __DataStoreModule_init();
    __StakeModule_init_unchained(_gETH, _oracle_position);
  }

  function __StakeModule_init_unchained(
    address _gETH,
    address _oracle_position
  ) internal onlyInitializing {
    require(_gETH != address(0), "SM:gETH cannot be zero address");
    require(_oracle_position != address(0), "SM:oracle cannot be zero address");

    StakeModuleStorage storage $ = _getStakeModuleStorage();

    $.gETH = IgETH(_gETH);
    $.ORACLE_POSITION = _oracle_position;

    $.BEACON_DELAY_ENTRY = 14 days;
    $.BEACON_DELAY_EXIT = 14 days;

    $.INITIATION_DEPOSIT = 32 ether; // initially 32 eth

    $.DAILY_PRICE_INCREASE_LIMIT = (7 * PERCENTAGE_DENOMINATOR) / 100;
    $.DAILY_PRICE_DECREASE_LIMIT = (7 * PERCENTAGE_DENOMINATOR) / 100;
  }

  /**
   * @custom:section                           ** GETTER FUNCTIONS **
   *
   * @custom:visibility -> view-external
   */

  function StakeParams()
    external
    view
    virtual
    override
    returns (
      address gETH,
      address oraclePosition,
      uint256 validatorsIndex,
      uint256 verificationIndex,
      uint256 monopolyThreshold,
      uint256 beaconDelayEntry,
      uint256 beaconDelayExit,
      uint256 initiationDeposit,
      uint256 oracleUpdateTimestamp,
      uint256 dailyPriceIncreaseLimit,
      uint256 dailyPriceDecreaseLimit
    )
  {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    gETH = address($.gETH);
    oraclePosition = $.ORACLE_POSITION;
    validatorsIndex = $.VALIDATORS_INDEX;
    verificationIndex = $.VERIFICATION_INDEX;
    monopolyThreshold = $.MONOPOLY_THRESHOLD;
    beaconDelayEntry = $.BEACON_DELAY_ENTRY;
    beaconDelayExit = $.BEACON_DELAY_EXIT;
    initiationDeposit = $.INITIATION_DEPOSIT;
    oracleUpdateTimestamp = $.ORACLE_UPDATE_TIMESTAMP;
    dailyPriceIncreaseLimit = $.DAILY_PRICE_INCREASE_LIMIT;
    dailyPriceDecreaseLimit = $.DAILY_PRICE_DECREASE_LIMIT;
  }

  function getValidator(
    bytes calldata pubkey
  ) external view virtual override returns (Validator memory) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.validators[pubkey];
  }

  function getBalancesMerkleRoot() external view virtual override returns (bytes32) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.BALANCE_MERKLE_ROOT;
  }

  function getPriceMerkleRoot() external view virtual override returns (bytes32) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.PRICE_MERKLE_ROOT;
  }

  function getPackageVersion(uint256 _type) external view virtual override returns (uint256) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.packages[_type];
  }

  function isMiddleware(
    uint256 _type,
    uint256 _version
  ) external view virtual override returns (bool) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.middlewares[_type][_version];
  }

  function getInfrastructureFee(uint256 _type) external view virtual override returns (uint256) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.infrastructureFees[_type];
  }

  /**
   * @custom:section                           ** OPERATOR INITIATOR **
   *
   * @custom:visibility -> external
   */
  function initiateOperator(
    uint256 id,
    uint256 fee,
    uint256 validatorPeriod,
    address maintainer
  ) external payable virtual override nonReentrant whenNotPaused {
    IEL.initiateOperator(_getDataStoreModuleStorage(), id, fee, validatorPeriod, maintainer);
  }

  /**
   * @custom:section                           ** STAKING POOL INITIATOR **
   *
   * @custom:visibility -> external
   */

  function initiatePool(
    uint256 fee,
    uint256 middlewareVersion,
    address maintainer,
    bytes calldata NAME,
    bytes calldata middleware_data,
    bool[3] calldata config
  ) external payable virtual override whenNotPaused returns (uint256 poolId) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    poolId = $.initiatePool(
      _getDataStoreModuleStorage(),
      fee,
      middlewareVersion,
      maintainer,
      NAME,
      middleware_data,
      config
    );
  }

  /**
   * @custom:subsection                           ** POOL VISIBILITY **
   */

  function setPoolVisibility(uint256 poolId, bool makePrivate) external virtual override {
    SML.setPoolVisibility(_getDataStoreModuleStorage(), poolId, makePrivate);
  }

  function setWhitelist(uint256 poolId, address whitelist) external virtual override {
    SML.setWhitelist(_getDataStoreModuleStorage(), poolId, whitelist);
  }

  /**
   * @custom:visibility -> view
   */
  function isPrivatePool(uint256 poolId) external view virtual override returns (bool) {
    return SML.isPrivatePool(_getDataStoreModuleStorage(), poolId);
  }

  function isWhitelisted(
    uint256 poolId,
    address staker
  ) external view virtual override returns (bool) {
    return SML.isWhitelisted(_getDataStoreModuleStorage(), poolId, staker);
  }

  /**
   * @custom:subsection                           ** BOUND LIQUIDITY POOL **
   */

  function deployLiquidityPool(uint256 poolId) external virtual override whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.deployLiquidityPool(_getDataStoreModuleStorage(), poolId);
  }

  /**
   * @custom:subsection                           ** YIELD SEPARATION **
   */

  function setYieldReceiver(
    uint256 poolId,
    address yieldReceiver
  ) external virtual override whenNotPaused {
    SML.setYieldReceiver(_getDataStoreModuleStorage(), poolId, yieldReceiver);
  }

  /**
   * @custom:section                           ** ID MANAGEMENT **
   *
   * @custom:visibility -> external
   */

  /**
   * @custom:subsection                           ** MAINTAINER **
   */

  function changeMaintainer(
    uint256 id,
    address newMaintainer
  ) external virtual override whenNotPaused {
    SML.changeMaintainer(_getDataStoreModuleStorage(), id, newMaintainer);
  }

  /**
   * @custom:subsection                           ** FEE **
   */

  function switchMaintenanceFee(
    uint256 id,
    uint256 newFee
  ) external virtual override whenNotPaused {
    SML.switchMaintenanceFee(_getDataStoreModuleStorage(), id, newFee);
  }

  /**
   * @custom:visibility -> view
   */
  function getMaintenanceFee(uint256 id) external view virtual override returns (uint256) {
    return SML.getMaintenanceFee(_getDataStoreModuleStorage(), id);
  }

  /**
   * @custom:section                           ** INTERNAL WALLET **
   */

  function increaseWalletBalance(
    uint256 id
  ) external payable virtual override nonReentrant whenNotPaused returns (bool) {
    return SML.increaseWalletBalance(_getDataStoreModuleStorage(), id);
  }

  function decreaseWalletBalance(
    uint256 id,
    uint256 value
  ) external virtual override nonReentrant returns (bool) {
    return SML.decreaseWalletBalance(_getDataStoreModuleStorage(), id, value);
  }

  /**
   * @custom:section                           ** OPERATORS PERIOD **
   *
   * @custom:visibility -> external
   */

  function switchValidatorPeriod(
    uint256 operatorId,
    uint256 newPeriod
  ) external virtual override whenNotPaused {
    SML.switchValidatorPeriod(_getDataStoreModuleStorage(), operatorId, newPeriod);
  }

  /**
   * @custom:visibility -> view
   */
  function getValidatorPeriod(uint256 id) external view virtual override returns (uint256) {
    return SML.getValidatorPeriod(_getDataStoreModuleStorage(), id);
  }

  /**
   * @custom:section                           ** PRISON **
   *
   * @custom:visibility -> external
   */

  function blameProposal(bytes calldata pk) external virtual override whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.blameProposal(_getDataStoreModuleStorage(), pk);
  }

  function blameExit(
    bytes calldata pk,
    uint256 beaconBalance,
    uint256 withdrawnBalance,
    bytes32[] calldata balanceProof
  ) external virtual override whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.blameExit(_getDataStoreModuleStorage(), pk, beaconBalance, withdrawnBalance, balanceProof);
  }

  /**
   * @custom:visibility -> view
   */
  function isPrisoned(uint256 operatorId) external view virtual override returns (bool) {
    return SML.isPrisoned(_getDataStoreModuleStorage(), operatorId);
  }

  /**
   * @custom:section                           ** DELEGATION **
   *
   * @custom:visibility -> external
   */

  function delegate(
    uint256 poolId,
    uint256[] calldata operatorIds,
    uint256[] calldata allowances
  ) external virtual override whenNotPaused {
    SML.delegate(_getDataStoreModuleStorage(), poolId, operatorIds, allowances);
  }

  function setFallbackOperator(
    uint256 poolId,
    uint256 operatorId,
    uint256 fallbackThreshold
  ) external virtual override whenNotPaused {
    SML.setFallbackOperator(_getDataStoreModuleStorage(), poolId, operatorId, fallbackThreshold);
  }

  /**
   * @custom:visibility -> view
   */
  function operatorAllowance(
    uint256 poolId,
    uint256 operatorId
  ) external view virtual override returns (uint256) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.operatorAllowance(_getDataStoreModuleStorage(), poolId, operatorId);
  }

  /**
   * @custom:section                           ** DEPOSIT GETTERS **
   *
   * @custom:visibility -> view-external
   */

  function isPriceValid(uint256 poolId) external view virtual override returns (bool) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.isPriceValid(poolId);
  }

  function isMintingAllowed(uint256 poolId) external view virtual override returns (bool) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.isMintingAllowed(_getDataStoreModuleStorage(), poolId);
  }

  /**
   * @custom:section                           ** POOLING OPERATIONS **
   *
   * @custom:visibility -> external
   */
  function deposit(
    uint256 poolId,
    uint256 price,
    bytes32[] calldata priceProof,
    uint256 mingETH,
    uint256 deadline,
    address receiver
  )
    external
    payable
    virtual
    override
    nonReentrant
    whenNotPaused
    returns (uint256 boughtgETH, uint256 mintedgETH)
  {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    DataStoreModuleStorage storage DSMStorage = _getDataStoreModuleStorage();
    if (!$.isPriceValid(poolId)) {
      $.priceSync(DSMStorage, poolId, price, priceProof);
    }

    (boughtgETH, mintedgETH) = $.deposit(
      _getDataStoreModuleStorage(),
      poolId,
      mingETH,
      deadline,
      receiver
    );
  }

  /**
   * @custom:section                           ** VALIDATOR CREATION **
   *
   * @custom:visibility -> external
   */
  function proposeStake(
    uint256 poolId,
    uint256 operatorId,
    bytes[] calldata pubkeys,
    bytes[] calldata signatures1,
    bytes[] calldata signatures31
  ) external virtual override whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.proposeStake(
      _getDataStoreModuleStorage(),
      poolId,
      operatorId,
      pubkeys,
      signatures1,
      signatures31
    );
  }

  function stake(
    uint256 operatorId,
    bytes[] calldata pubkeys
  ) external virtual override whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.stake(_getDataStoreModuleStorage(), operatorId, pubkeys);
  }

  /**
   * @custom:visibility -> view
   */
  function canStake(bytes calldata pubkey) external view virtual override returns (bool) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.canStake(pubkey);
  }

  /**
   * @custom:section                           ** VALIDATOR EXITS **
   *
   * @custom:visibility -> external
   */

  function requestExit(
    uint256 poolId,
    bytes calldata pk
  ) external virtual override nonReentrant whenNotPaused returns (bool) {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    return $.requestExit(_getDataStoreModuleStorage(), poolId, pk);
  }

  function finalizeExit(
    uint256 poolId,
    bytes calldata pk
  ) external virtual override nonReentrant whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.finalizeExit(_getDataStoreModuleStorage(), poolId, pk);
  }

  /**
   * @custom:section                           ** ORACLE OPERATIONS **
   *
   * @custom:visibility -> external
   */

  function updateVerificationIndex(
    uint256 validatorVerificationIndex,
    bytes[] calldata alienatedPubkeys
  ) external virtual override whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.updateVerificationIndex(
      _getDataStoreModuleStorage(),
      validatorVerificationIndex,
      alienatedPubkeys
    );
  }

  function regulateOperators(
    uint256[] calldata feeThefts,
    bytes[] calldata proofs
  ) external virtual override whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.regulateOperators(_getDataStoreModuleStorage(), feeThefts, proofs);
  }

  function reportBeacon(
    bytes32 priceMerkleRoot,
    bytes32 balanceMerkleRoot,
    uint256 allValidatorsCount
  ) external virtual override whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.reportBeacon(priceMerkleRoot, balanceMerkleRoot, allValidatorsCount);
  }

  function priceSync(
    uint256 poolId,
    uint256 price,
    bytes32[] calldata priceProof
  ) external virtual override whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.priceSync(_getDataStoreModuleStorage(), poolId, price, priceProof);
  }

  function priceSyncBatch(
    uint256[] calldata poolIds,
    uint256[] calldata prices,
    bytes32[][] calldata priceProofs
  ) external virtual override whenNotPaused {
    StakeModuleStorage storage $ = _getStakeModuleStorage();
    $.priceSyncBatch(_getDataStoreModuleStorage(), poolIds, prices, priceProofs);
  }
}

